INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱجْعَل لِّى وَزِيرًۭا مِّنْ أَهْلِى',20,29,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰرُونَ أَخِى',20,30,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱشْدُدْ بِهِۦٓ أَزْرِى',20,31,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَشْرِكْهُ فِىٓ أَمْرِى',20,32,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَىْ نُسَبِّحَكَ كَثِيرًۭا',20,33,313,271,125,false);
