INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ قَدْ أُوتِيتَ سُؤْلَكَ يَٰمُوسَىٰ',20,36,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً أُخْرَىٰٓ',20,37,313,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ أَوْحَيْنَآ إِلَىٰٓ أُمِّكَ مَا يُوحَىٰٓ',20,38,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَنِ ٱقْذِفِيهِ فِى ٱلتَّابُوتِ فَٱقْذِفِيهِ فِى ٱلْيَمِّ فَلْيُلْقِهِ ٱلْيَمُّ بِٱلسَّاحِلِ يَأْخُذْهُ عَدُوٌّۭ لِّى وَعَدُوٌّۭ لَّهُۥ ۚ وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةًۭ مِّنِّى وَلِتُصْنَعَ عَلَىٰ عَيْنِىٓ',20,39,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ تَمْشِىٓ أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلَىٰ مَن يَكْفُلُهُۥ ۖ فَرَجَعْنَٰكَ إِلَىٰٓ أُمِّكَ كَىْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ۚ وَقَتَلْتَ نَفْسًۭا فَنَجَّيْنَٰكَ مِنَ ٱلْغَمِّ وَفَتَنَّٰكَ فُتُونًۭا ۚ فَلَبِثْتَ سِنِينَ فِىٓ أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلَىٰ قَدَرٍۢ يَٰمُوسَىٰ',20,40,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱصْطَنَعْتُكَ لِنَفْسِى',20,41,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱذْهَبْ أَنتَ وَأَخُوكَ بِـَٔايَٰتِى وَلَا تَنِيَا فِى ذِكْرِى',20,42,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱذْهَبَآ إِلَىٰ فِرْعَوْنَ إِنَّهُۥ طَغَىٰ',20,43,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقُولَا لَهُۥ قَوْلًۭا لَّيِّنًۭا لَّعَلَّهُۥ يَتَذَكَّرُ أَوْ يَخْشَىٰ',20,44,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَا رَبَّنَآ إِنَّنَا نَخَافُ أَن يَفْرُطَ عَلَيْنَآ أَوْ أَن يَطْغَىٰ',20,45,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لَا تَخَافَآ ۖ إِنَّنِى مَعَكُمَآ أَسْمَعُ وَأَرَىٰ',20,46,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأْتِيَاهُ فَقُولَآ إِنَّا رَسُولَا رَبِّكَ فَأَرْسِلْ مَعَنَا بَنِىٓ إِسْرَٰٓءِيلَ وَلَا تُعَذِّبْهُمْ ۖ قَدْ جِئْنَٰكَ بِـَٔايَةٍۢ مِّن رَّبِّكَ ۖ وَٱلسَّلَٰمُ عَلَىٰ مَنِ ٱتَّبَعَ ٱلْهُدَىٰٓ',20,47,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّا قَدْ أُوحِىَ إِلَيْنَآ أَنَّ ٱلْعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ',20,48,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَمَن رَّبُّكُمَا يَٰمُوسَىٰ',20,49,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبُّنَا ٱلَّذِىٓ أَعْطَىٰ كُلَّ شَىْءٍ خَلْقَهُۥ ثُمَّ هَدَىٰ',20,50,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَمَا بَالُ ٱلْقُرُونِ ٱلْأُولَىٰ',20,51,314,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ عِلْمُهَا عِندَ رَبِّى فِى كِتَٰبٍۢ ۖ لَّا يَضِلُّ رَبِّى وَلَا يَنسَى',20,52,315,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ مَهْدًۭا وَسَلَكَ لَكُمْ فِيهَا سُبُلًۭا وَأَنزَلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَأَخْرَجْنَا بِهِۦٓ أَزْوَٰجًۭا مِّن نَّبَاتٍۢ شَتَّىٰ',20,53,315,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كُلُوا۟ وَٱرْعَوْا۟ أَنْعَٰمَكُمْ ۗ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ لِّأُو۟لِى ٱلنُّهَىٰ',20,54,315,271,125,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ مِنْهَا خَلَقْنَٰكُمْ وَفِيهَا نُعِيدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً أُخْرَىٰ',20,55,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَرَيْنَٰهُ ءَايَٰتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ',20,56,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ أَجِئْتَنَا لِتُخْرِجَنَا مِنْ أَرْضِنَا بِسِحْرِكَ يَٰمُوسَىٰ',20,57,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَنَأْتِيَنَّكَ بِسِحْرٍۢ مِّثْلِهِۦ فَٱجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًۭا لَّا نُخْلِفُهُۥ نَحْنُ وَلَآ أَنتَ مَكَانًۭا سُوًۭى',20,58,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ مَوْعِدُكُمْ يَوْمُ ٱلزِّينَةِ وَأَن يُحْشَرَ ٱلنَّاسُ ضُحًۭى',20,59,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَتَوَلَّىٰ فِرْعَوْنُ فَجَمَعَ كَيْدَهُۥ ثُمَّ أَتَىٰ',20,60,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لَهُم مُّوسَىٰ وَيْلَكُمْ لَا تَفْتَرُوا۟ عَلَى ٱللَّهِ كَذِبًۭا فَيُسْحِتَكُم بِعَذَابٍۢ ۖ وَقَدْ خَابَ مَنِ ٱفْتَرَىٰ',20,61,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَتَنَٰزَعُوٓا۟ أَمْرَهُم بَيْنَهُمْ وَأَسَرُّوا۟ ٱلنَّجْوَىٰ',20,62,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ إِنْ هَٰذَٰنِ لَسَٰحِرَٰنِ يُرِيدَانِ أَن يُخْرِجَاكُم مِّنْ أَرْضِكُم بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيقَتِكُمُ ٱلْمُثْلَىٰ',20,63,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَجْمِعُوا۟ كَيْدَكُمْ ثُمَّ ٱئْتُوا۟ صَفًّۭا ۚ وَقَدْ أَفْلَحَ ٱلْيَوْمَ مَنِ ٱسْتَعْلَىٰ',20,64,315,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ يَٰمُوسَىٰٓ إِمَّآ أَن تُلْقِىَ وَإِمَّآ أَن نَّكُونَ أَوَّلَ مَنْ أَلْقَىٰ',20,65,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ بَلْ أَلْقُوا۟ ۖ فَإِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِن سِحْرِهِمْ أَنَّهَا تَسْعَىٰ',20,66,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَوْجَسَ فِى نَفْسِهِۦ خِيفَةًۭ مُّوسَىٰ',20,67,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْنَا لَا تَخَفْ إِنَّكَ أَنتَ ٱلْأَعْلَىٰ',20,68,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَلْقِ مَا فِى يَمِينِكَ تَلْقَفْ مَا صَنَعُوٓا۟ ۖ إِنَّمَا صَنَعُوا۟ كَيْدُ سَٰحِرٍۢ ۖ وَلَا يُفْلِحُ ٱلسَّاحِرُ حَيْثُ أَتَىٰ',20,69,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأُلْقِىَ ٱلسَّحَرَةُ سُجَّدًۭا قَالُوٓا۟ ءَامَنَّا بِرَبِّ هَٰرُونَ وَمُوسَىٰ',20,70,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ ءَامَنتُمْ لَهُۥ قَبْلَ أَنْ ءَاذَنَ لَكُمْ ۖ إِنَّهُۥ لَكَبِيرُكُمُ ٱلَّذِى عَلَّمَكُمُ ٱلسِّحْرَ ۖ فَلَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُم مِّنْ خِلَٰفٍۢ وَلَأُصَلِّبَنَّكُمْ فِى جُذُوعِ ٱلنَّخْلِ وَلَتَعْلَمُنَّ أَيُّنَآ أَشَدُّ عَذَابًۭا وَأَبْقَىٰ',20,71,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ لَن نُّؤْثِرَكَ عَلَىٰ مَا جَآءَنَا مِنَ ٱلْبَيِّنَٰتِ وَٱلَّذِى فَطَرَنَا ۖ فَٱقْضِ مَآ أَنتَ قَاضٍ ۖ إِنَّمَا تَقْضِى هَٰذِهِ ٱلْحَيَوٰةَ ٱلدُّنْيَآ',20,72,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّآ ءَامَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَٰيَٰنَا وَمَآ أَكْرَهْتَنَا عَلَيْهِ مِنَ ٱلسِّحْرِ ۗ وَٱللَّهُ خَيْرٌۭ وَأَبْقَىٰٓ',20,73,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّهُۥ مَن يَأْتِ رَبَّهُۥ مُجْرِمًۭا فَإِنَّ لَهُۥ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ',20,74,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يَأْتِهِۦ مُؤْمِنًۭا قَدْ عَمِلَ ٱلصَّٰلِحَٰتِ فَأُو۟لَٰٓئِكَ لَهُمُ ٱلدَّرَجَٰتُ ٱلْعُلَىٰ',20,75,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('جَنَّٰتُ عَدْنٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَا ۚ وَذَٰلِكَ جَزَآءُ مَن تَزَكَّىٰ',20,76,316,272,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَوْحَيْنَآ إِلَىٰ مُوسَىٰٓ أَنْ أَسْرِ بِعِبَادِى فَٱضْرِبْ لَهُمْ طَرِيقًۭا فِى ٱلْبَحْرِ يَبَسًۭا لَّا تَخَٰفُ دَرَكًۭا وَلَا تَخْشَىٰ',20,77,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِۦ فَغَشِيَهُم مِّنَ ٱلْيَمِّ مَا غَشِيَهُمْ',20,78,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَضَلَّ فِرْعَوْنُ قَوْمَهُۥ وَمَا هَدَىٰ',20,79,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰبَنِىٓ إِسْرَٰٓءِيلَ قَدْ أَنجَيْنَٰكُم مِّنْ عَدُوِّكُمْ وَوَٰعَدْنَٰكُمْ جَانِبَ ٱلطُّورِ ٱلْأَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ ٱلْمَنَّ وَٱلسَّلْوَىٰ',20,80,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كُلُوا۟ مِن طَيِّبَٰتِ مَا رَزَقْنَٰكُمْ وَلَا تَطْغَوْا۟ فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِى ۖ وَمَن يَحْلِلْ عَلَيْهِ غَضَبِى فَقَدْ هَوَىٰ',20,81,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنِّى لَغَفَّارٌۭ لِّمَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحًۭا ثُمَّ ٱهْتَدَىٰ',20,82,317,273,126,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَمَآ أَعْجَلَكَ عَن قَوْمِكَ يَٰمُوسَىٰ',20,83,317,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ هُمْ أُو۟لَآءِ عَلَىٰٓ أَثَرِى وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَىٰ',20,84,317,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَإِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنۢ بَعْدِكَ وَأَضَلَّهُمُ ٱلسَّامِرِىُّ',20,85,317,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَرَجَعَ مُوسَىٰٓ إِلَىٰ قَوْمِهِۦ غَضْبَٰنَ أَسِفًۭا ۚ قَالَ يَٰقَوْمِ أَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ۚ أَفَطَالَ عَلَيْكُمُ ٱلْعَهْدُ أَمْ أَرَدتُّمْ أَن يَحِلَّ عَلَيْكُمْ غَضَبٌۭ مِّن رَّبِّكُمْ فَأَخْلَفْتُم مَّوْعِدِى',20,86,317,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ مَآ أَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلَٰكِنَّا حُمِّلْنَآ أَوْزَارًۭا مِّن زِينَةِ ٱلْقَوْمِ فَقَذَفْنَٰهَا فَكَذَٰلِكَ أَلْقَى ٱلسَّامِرِىُّ',20,87,317,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَخْرَجَ لَهُمْ عِجْلًۭا جَسَدًۭا لَّهُۥ خُوَارٌۭ فَقَالُوا۟ هَٰذَآ إِلَٰهُكُمْ وَإِلَٰهُ مُوسَىٰ فَنَسِىَ',20,88,318,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَلَا يَرَوْنَ أَلَّا يَرْجِعُ إِلَيْهِمْ قَوْلًۭا وَلَا يَمْلِكُ لَهُمْ ضَرًّۭا وَلَا نَفْعًۭا',20,89,318,273,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ قَالَ لَهُمْ هَٰرُونُ مِن قَبْلُ يَٰقَوْمِ إِنَّمَا فُتِنتُم بِهِۦ ۖ وَإِنَّ رَبَّكُمُ ٱلرَّحْمَٰنُ فَٱتَّبِعُونِى وَأَطِيعُوٓا۟ أَمْرِى',20,90,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ لَن نَّبْرَحَ عَلَيْهِ عَٰكِفِينَ حَتَّىٰ يَرْجِعَ إِلَيْنَا مُوسَىٰ',20,91,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ يَٰهَٰرُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوٓا۟',20,92,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَّا تَتَّبِعَنِ ۖ أَفَعَصَيْتَ أَمْرِى',20,93,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ يَبْنَؤُمَّ لَا تَأْخُذْ بِلِحْيَتِى وَلَا بِرَأْسِىٓ ۖ إِنِّى خَشِيتُ أَن تَقُولَ فَرَّقْتَ بَيْنَ بَنِىٓ إِسْرَٰٓءِيلَ وَلَمْ تَرْقُبْ قَوْلِى',20,94,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَمَا خَطْبُكَ يَٰسَٰمِرِىُّ',20,95,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا۟ بِهِۦ فَقَبَضْتُ قَبْضَةًۭ مِّنْ أَثَرِ ٱلرَّسُولِ فَنَبَذْتُهَا وَكَذَٰلِكَ سَوَّلَتْ لِى نَفْسِى',20,96,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَٱذْهَبْ فَإِنَّ لَكَ فِى ٱلْحَيَوٰةِ أَن تَقُولَ لَا مِسَاسَ ۖ وَإِنَّ لَكَ مَوْعِدًۭا لَّن تُخْلَفَهُۥ ۖ وَٱنظُرْ إِلَىٰٓ إِلَٰهِكَ ٱلَّذِى ظَلْتَ عَلَيْهِ عَاكِفًۭا ۖ لَّنُحَرِّقَنَّهُۥ ثُمَّ لَنَنسِفَنَّهُۥ فِى ٱلْيَمِّ نَسْفًا',20,97,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّمَآ إِلَٰهُكُمُ ٱللَّهُ ٱلَّذِى لَآ إِلَٰهَ إِلَّا هُوَ ۚ وَسِعَ كُلَّ شَىْءٍ عِلْمًۭا',20,98,318,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَذَٰلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنۢبَآءِ مَا قَدْ سَبَقَ ۚ وَقَدْ ءَاتَيْنَٰكَ مِن لَّدُنَّا ذِكْرًۭا',20,99,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَّنْ أَعْرَضَ عَنْهُ فَإِنَّهُۥ يَحْمِلُ يَوْمَ ٱلْقِيَٰمَةِ وِزْرًا',20,100,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('خَٰلِدِينَ فِيهِ ۖ وَسَآءَ لَهُمْ يَوْمَ ٱلْقِيَٰمَةِ حِمْلًۭا',20,101,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ يُنفَخُ فِى ٱلصُّورِ ۚ وَنَحْشُرُ ٱلْمُجْرِمِينَ يَوْمَئِذٍۢ زُرْقًۭا',20,102,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَتَخَٰفَتُونَ بَيْنَهُمْ إِن لَّبِثْتُمْ إِلَّا عَشْرًۭا',20,103,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('نَّحْنُ أَعْلَمُ بِمَا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِن لَّبِثْتُمْ إِلَّا يَوْمًۭا',20,104,319,274,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَسْـَٔلُونَكَ عَنِ ٱلْجِبَالِ فَقُلْ يَنسِفُهَا رَبِّى نَسْفًۭا',20,105,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَيَذَرُهَا قَاعًۭا صَفْصَفًۭا',20,106,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّا تَرَىٰ فِيهَا عِوَجًۭا وَلَآ أَمْتًۭا',20,107,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَئِذٍۢ يَتَّبِعُونَ ٱلدَّاعِىَ لَا عِوَجَ لَهُۥ ۖ وَخَشَعَتِ ٱلْأَصْوَاتُ لِلرَّحْمَٰنِ فَلَا تَسْمَعُ إِلَّا هَمْسًۭا',20,108,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَئِذٍۢ لَّا تَنفَعُ ٱلشَّفَٰعَةُ إِلَّا مَنْ أَذِنَ لَهُ ٱلرَّحْمَٰنُ وَرَضِىَ لَهُۥ قَوْلًۭا',20,109,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيطُونَ بِهِۦ عِلْمًۭا',20,110,319,275,127,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَعَنَتِ ٱلْوُجُوهُ لِلْحَىِّ ٱلْقَيُّومِ ۖ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًۭا',20,111,319,275,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يَعْمَلْ مِنَ ٱلصَّٰلِحَٰتِ وَهُوَ مُؤْمِنٌۭ فَلَا يَخَافُ ظُلْمًۭا وَلَا هَضْمًۭا',20,112,319,275,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ أَنزَلْنَٰهُ قُرْءَانًا عَرَبِيًّۭا وَصَرَّفْنَا فِيهِ مِنَ ٱلْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْرًۭا',20,113,319,275,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَتَعَٰلَى ٱللَّهُ ٱلْمَلِكُ ٱلْحَقُّ ۗ وَلَا تَعْجَلْ بِٱلْقُرْءَانِ مِن قَبْلِ أَن يُقْضَىٰٓ إِلَيْكَ وَحْيُهُۥ ۖ وَقُل رَّبِّ زِدْنِى عِلْمًۭا',20,114,320,275,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ عَهِدْنَآ إِلَىٰٓ ءَادَمَ مِن قَبْلُ فَنَسِىَ وَلَمْ نَجِدْ لَهُۥ عَزْمًۭا',20,115,320,275,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذْ قُلْنَا لِلْمَلَٰٓئِكَةِ ٱسْجُدُوا۟ لِءَادَمَ فَسَجَدُوٓا۟ إِلَّآ إِبْلِيسَ أَبَىٰ',20,116,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقُلْنَا يَٰٓـَٔادَمُ إِنَّ هَٰذَا عَدُوٌّۭ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ ٱلْجَنَّةِ فَتَشْقَىٰٓ',20,117,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعْرَىٰ',20,118,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنَّكَ لَا تَظْمَؤُا۟ فِيهَا وَلَا تَضْحَىٰ',20,119,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَوَسْوَسَ إِلَيْهِ ٱلشَّيْطَٰنُ قَالَ يَٰٓـَٔادَمُ هَلْ أَدُلُّكَ عَلَىٰ شَجَرَةِ ٱلْخُلْدِ وَمُلْكٍۢ لَّا يَبْلَىٰ',20,120,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْءَٰتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِن وَرَقِ ٱلْجَنَّةِ ۚ وَعَصَىٰٓ ءَادَمُ رَبَّهُۥ فَغَوَىٰ',20,121,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ ٱجْتَبَٰهُ رَبُّهُۥ فَتَابَ عَلَيْهِ وَهَدَىٰ',20,122,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ ٱهْبِطَا مِنْهَا جَمِيعًۢا ۖ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّۭ ۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّى هُدًۭى فَمَنِ ٱتَّبَعَ هُدَاىَ فَلَا يَضِلُّ وَلَا يَشْقَىٰ',20,123,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَنْ أَعْرَضَ عَن ذِكْرِى فَإِنَّ لَهُۥ مَعِيشَةًۭ ضَنكًۭا وَنَحْشُرُهُۥ يَوْمَ ٱلْقِيَٰمَةِ أَعْمَىٰ',20,124,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ لِمَ حَشَرْتَنِىٓ أَعْمَىٰ وَقَدْ كُنتُ بَصِيرًۭا',20,125,320,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ كَذَٰلِكَ أَتَتْكَ ءَايَٰتُنَا فَنَسِيتَهَا ۖ وَكَذَٰلِكَ ٱلْيَوْمَ تُنسَىٰ',20,126,321,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ نَجْزِى مَنْ أَسْرَفَ وَلَمْ يُؤْمِنۢ بِـَٔايَٰتِ رَبِّهِۦ ۚ وَلَعَذَابُ ٱلْءَاخِرَةِ أَشَدُّ وَأَبْقَىٰٓ',20,127,321,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا قَبْلَهُم مِّنَ ٱلْقُرُونِ يَمْشُونَ فِى مَسَٰكِنِهِمْ ۗ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ لِّأُو۟لِى ٱلنُّهَىٰ',20,128,321,276,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَا كَلِمَةٌۭ سَبَقَتْ مِن رَّبِّكَ لَكَانَ لِزَامًۭا وَأَجَلٌۭ مُّسَمًّۭى',20,129,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ ٱلشَّمْسِ وَقَبْلَ غُرُوبِهَا ۖ وَمِنْ ءَانَآئِ ٱلَّيْلِ فَسَبِّحْ وَأَطْرَافَ ٱلنَّهَارِ لَعَلَّكَ تَرْضَىٰ',20,130,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا تَمُدَّنَّ عَيْنَيْكَ إِلَىٰ مَا مَتَّعْنَا بِهِۦٓ أَزْوَٰجًۭا مِّنْهُمْ زَهْرَةَ ٱلْحَيَوٰةِ ٱلدُّنْيَا لِنَفْتِنَهُمْ فِيهِ ۚ وَرِزْقُ رَبِّكَ خَيْرٌۭ وَأَبْقَىٰ',20,131,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأْمُرْ أَهْلَكَ بِٱلصَّلَوٰةِ وَٱصْطَبِرْ عَلَيْهَا ۖ لَا نَسْـَٔلُكَ رِزْقًۭا ۖ نَّحْنُ نَرْزُقُكَ ۗ وَٱلْعَٰقِبَةُ لِلتَّقْوَىٰ',20,132,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ لَوْلَا يَأْتِينَا بِـَٔايَةٍۢ مِّن رَّبِّهِۦٓ ۚ أَوَلَمْ تَأْتِهِم بَيِّنَةُ مَا فِى ٱلصُّحُفِ ٱلْأُولَىٰ',20,133,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ أَنَّآ أَهْلَكْنَٰهُم بِعَذَابٍۢ مِّن قَبْلِهِۦ لَقَالُوا۟ رَبَّنَا لَوْلَآ أَرْسَلْتَ إِلَيْنَا رَسُولًۭا فَنَتَّبِعَ ءَايَٰتِكَ مِن قَبْلِ أَن نَّذِلَّ وَنَخْزَىٰ',20,134,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ كُلٌّۭ مُّتَرَبِّصٌۭ فَتَرَبَّصُوا۟ ۖ فَسَتَعْلَمُونَ مَنْ أَصْحَٰبُ ٱلصِّرَٰطِ ٱلسَّوِىِّ وَمَنِ ٱهْتَدَىٰ',20,135,321,277,128,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ ٱقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِى غَفْلَةٍۢ مُّعْرِضُونَ',21,1,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا يَأْتِيهِم مِّن ذِكْرٍۢ مِّن رَّبِّهِم مُّحْدَثٍ إِلَّا ٱسْتَمَعُوهُ وَهُمْ يَلْعَبُونَ',21,2,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَاهِيَةًۭ قُلُوبُهُمْ ۗ وَأَسَرُّوا۟ ٱلنَّجْوَى ٱلَّذِينَ ظَلَمُوا۟ هَلْ هَٰذَآ إِلَّا بَشَرٌۭ مِّثْلُكُمْ ۖ أَفَتَأْتُونَ ٱلسِّحْرَ وَأَنتُمْ تُبْصِرُونَ',21,3,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّى يَعْلَمُ ٱلْقَوْلَ فِى ٱلسَّمَآءِ وَٱلْأَرْضِ ۖ وَهُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ',21,4,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ قَالُوٓا۟ أَضْغَٰثُ أَحْلَٰمٍۭ بَلِ ٱفْتَرَىٰهُ بَلْ هُوَ شَاعِرٌۭ فَلْيَأْتِنَا بِـَٔايَةٍۢ كَمَآ أُرْسِلَ ٱلْأَوَّلُونَ',21,5,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَآ ءَامَنَتْ قَبْلَهُم مِّن قَرْيَةٍ أَهْلَكْنَٰهَآ ۖ أَفَهُمْ يُؤْمِنُونَ',21,6,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَا قَبْلَكَ إِلَّا رِجَالًۭا نُّوحِىٓ إِلَيْهِمْ ۖ فَسْـَٔلُوٓا۟ أَهْلَ ٱلذِّكْرِ إِن كُنتُمْ لَا تَعْلَمُونَ',21,7,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا جَعَلْنَٰهُمْ جَسَدًۭا لَّا يَأْكُلُونَ ٱلطَّعَامَ وَمَا كَانُوا۟ خَٰلِدِينَ',21,8,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ صَدَقْنَٰهُمُ ٱلْوَعْدَ فَأَنجَيْنَٰهُمْ وَمَن نَّشَآءُ وَأَهْلَكْنَا ٱلْمُسْرِفِينَ',21,9,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَقَدْ أَنزَلْنَآ إِلَيْكُمْ كِتَٰبًۭا فِيهِ ذِكْرُكُمْ ۖ أَفَلَا تَعْقِلُونَ',21,10,322,278,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَمْ قَصَمْنَا مِن قَرْيَةٍۢ كَانَتْ ظَالِمَةًۭ وَأَنشَأْنَا بَعْدَهَا قَوْمًا ءَاخَرِينَ',21,11,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّآ أَحَسُّوا۟ بَأْسَنَآ إِذَا هُم مِّنْهَا يَرْكُضُونَ',21,12,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا تَرْكُضُوا۟ وَٱرْجِعُوٓا۟ إِلَىٰ مَآ أُتْرِفْتُمْ فِيهِ وَمَسَٰكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُونَ',21,13,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ يَٰوَيْلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ',21,14,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَمَا زَالَت تِّلْكَ دَعْوَىٰهُمْ حَتَّىٰ جَعَلْنَٰهُمْ حَصِيدًا خَٰمِدِينَ',21,15,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا خَلَقْنَا ٱلسَّمَآءَ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا لَٰعِبِينَ',21,16,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَوْ أَرَدْنَآ أَن نَّتَّخِذَ لَهْوًۭا لَّٱتَّخَذْنَٰهُ مِن لَّدُنَّآ إِن كُنَّا فَٰعِلِينَ',21,17,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ نَقْذِفُ بِٱلْحَقِّ عَلَى ٱلْبَٰطِلِ فَيَدْمَغُهُۥ فَإِذَا هُوَ زَاهِقٌۭ ۚ وَلَكُمُ ٱلْوَيْلُ مِمَّا تَصِفُونَ',21,18,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَهُۥ مَن فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۚ وَمَنْ عِندَهُۥ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِۦ وَلَا يَسْتَحْسِرُونَ',21,19,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُسَبِّحُونَ ٱلَّيْلَ وَٱلنَّهَارَ لَا يَفْتُرُونَ',21,20,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمِ ٱتَّخَذُوٓا۟ ءَالِهَةًۭ مِّنَ ٱلْأَرْضِ هُمْ يُنشِرُونَ',21,21,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَوْ كَانَ فِيهِمَآ ءَالِهَةٌ إِلَّا ٱللَّهُ لَفَسَدَتَا ۚ فَسُبْحَٰنَ ٱللَّهِ رَبِّ ٱلْعَرْشِ عَمَّا يَصِفُونَ',21,22,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُونَ',21,23,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمِ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ ءَالِهَةًۭ ۖ قُلْ هَاتُوا۟ بُرْهَٰنَكُمْ ۖ هَٰذَا ذِكْرُ مَن مَّعِىَ وَذِكْرُ مَن قَبْلِى ۗ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ ٱلْحَقَّ ۖ فَهُم مُّعْرِضُونَ',21,24,323,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَا مِن قَبْلِكَ مِن رَّسُولٍ إِلَّا نُوحِىٓ إِلَيْهِ أَنَّهُۥ لَآ إِلَٰهَ إِلَّآ أَنَا۠ فَٱعْبُدُونِ',21,25,324,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ ٱتَّخَذَ ٱلرَّحْمَٰنُ وَلَدًۭا ۗ سُبْحَٰنَهُۥ ۚ بَلْ عِبَادٌۭ مُّكْرَمُونَ',21,26,324,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا يَسْبِقُونَهُۥ بِٱلْقَوْلِ وَهُم بِأَمْرِهِۦ يَعْمَلُونَ',21,27,324,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُونَ إِلَّا لِمَنِ ٱرْتَضَىٰ وَهُم مِّنْ خَشْيَتِهِۦ مُشْفِقُونَ',21,28,324,279,129,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَمَن يَقُلْ مِنْهُمْ إِنِّىٓ إِلَٰهٌۭ مِّن دُونِهِۦ فَذَٰلِكَ نَجْزِيهِ جَهَنَّمَ ۚ كَذَٰلِكَ نَجْزِى ٱلظَّٰلِمِينَ',21,29,324,279,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوَلَمْ يَرَ ٱلَّذِينَ كَفَرُوٓا۟ أَنَّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ كَانَتَا رَتْقًۭا فَفَتَقْنَٰهُمَا ۖ وَجَعَلْنَا مِنَ ٱلْمَآءِ كُلَّ شَىْءٍ حَىٍّ ۖ أَفَلَا يُؤْمِنُونَ',21,30,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلْنَا فِى ٱلْأَرْضِ رَوَٰسِىَ أَن تَمِيدَ بِهِمْ وَجَعَلْنَا فِيهَا فِجَاجًۭا سُبُلًۭا لَّعَلَّهُمْ يَهْتَدُونَ',21,31,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلْنَا ٱلسَّمَآءَ سَقْفًۭا مَّحْفُوظًۭا ۖ وَهُمْ عَنْ ءَايَٰتِهَا مُعْرِضُونَ',21,32,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى خَلَقَ ٱلَّيْلَ وَٱلنَّهَارَ وَٱلشَّمْسَ وَٱلْقَمَرَ ۖ كُلٌّۭ فِى فَلَكٍۢ يَسْبَحُونَ',21,33,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا جَعَلْنَا لِبَشَرٍۢ مِّن قَبْلِكَ ٱلْخُلْدَ ۖ أَفَإِي۟ن مِّتَّ فَهُمُ ٱلْخَٰلِدُونَ',21,34,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كُلُّ نَفْسٍۢ ذَآئِقَةُ ٱلْمَوْتِ ۗ وَنَبْلُوكُم بِٱلشَّرِّ وَٱلْخَيْرِ فِتْنَةًۭ ۖ وَإِلَيْنَا تُرْجَعُونَ',21,35,324,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا رَءَاكَ ٱلَّذِينَ كَفَرُوٓا۟ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِى يَذْكُرُ ءَالِهَتَكُمْ وَهُم بِذِكْرِ ٱلرَّحْمَٰنِ هُمْ كَٰفِرُونَ',21,36,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('خُلِقَ ٱلْإِنسَٰنُ مِنْ عَجَلٍۢ ۚ سَأُو۟رِيكُمْ ءَايَٰتِى فَلَا تَسْتَعْجِلُونِ',21,37,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَٰدِقِينَ',21,38,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَوْ يَعْلَمُ ٱلَّذِينَ كَفَرُوا۟ حِينَ لَا يَكُفُّونَ عَن وُجُوهِهِمُ ٱلنَّارَ وَلَا عَن ظُهُورِهِمْ وَلَا هُمْ يُنصَرُونَ',21,39,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ تَأْتِيهِم بَغْتَةًۭ فَتَبْهَتُهُمْ فَلَا يَسْتَطِيعُونَ رَدَّهَا وَلَا هُمْ يُنظَرُونَ',21,40,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدِ ٱسْتُهْزِئَ بِرُسُلٍۢ مِّن قَبْلِكَ فَحَاقَ بِٱلَّذِينَ سَخِرُوا۟ مِنْهُم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ',21,41,325,280,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَن يَكْلَؤُكُم بِٱلَّيْلِ وَٱلنَّهَارِ مِنَ ٱلرَّحْمَٰنِ ۗ بَلْ هُمْ عَن ذِكْرِ رَبِّهِم مُّعْرِضُونَ',21,42,325,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ لَهُمْ ءَالِهَةٌۭ تَمْنَعُهُم مِّن دُونِنَا ۚ لَا يَسْتَطِيعُونَ نَصْرَ أَنفُسِهِمْ وَلَا هُم مِّنَّا يُصْحَبُونَ',21,43,325,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ مَتَّعْنَا هَٰٓؤُلَآءِ وَءَابَآءَهُمْ حَتَّىٰ طَالَ عَلَيْهِمُ ٱلْعُمُرُ ۗ أَفَلَا يَرَوْنَ أَنَّا نَأْتِى ٱلْأَرْضَ نَنقُصُهَا مِنْ أَطْرَافِهَآ ۚ أَفَهُمُ ٱلْغَٰلِبُونَ',21,44,325,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنَّمَآ أُنذِرُكُم بِٱلْوَحْىِ ۚ وَلَا يَسْمَعُ ٱلصُّمُّ ٱلدُّعَآءَ إِذَا مَا يُنذَرُونَ',21,45,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَئِن مَّسَّتْهُمْ نَفْحَةٌۭ مِّنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَٰوَيْلَنَآ إِنَّا كُنَّا ظَٰلِمِينَ',21,46,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَضَعُ ٱلْمَوَٰزِينَ ٱلْقِسْطَ لِيَوْمِ ٱلْقِيَٰمَةِ فَلَا تُظْلَمُ نَفْسٌۭ شَيْـًۭٔا ۖ وَإِن كَانَ مِثْقَالَ حَبَّةٍۢ مِّنْ خَرْدَلٍ أَتَيْنَا بِهَا ۗ وَكَفَىٰ بِنَا حَٰسِبِينَ',21,47,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ءَاتَيْنَا مُوسَىٰ وَهَٰرُونَ ٱلْفُرْقَانَ وَضِيَآءًۭ وَذِكْرًۭا لِّلْمُتَّقِينَ',21,48,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ وَهُم مِّنَ ٱلسَّاعَةِ مُشْفِقُونَ',21,49,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهَٰذَا ذِكْرٌۭ مُّبَارَكٌ أَنزَلْنَٰهُ ۚ أَفَأَنتُمْ لَهُۥ مُنكِرُونَ',21,50,326,281,130,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَلَقَدْ ءَاتَيْنَآ إِبْرَٰهِيمَ رُشْدَهُۥ مِن قَبْلُ وَكُنَّا بِهِۦ عَٰلِمِينَ',21,51,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِۦ مَا هَٰذِهِ ٱلتَّمَاثِيلُ ٱلَّتِىٓ أَنتُمْ لَهَا عَٰكِفُونَ',21,52,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ وَجَدْنَآ ءَابَآءَنَا لَهَا عَٰبِدِينَ',21,53,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لَقَدْ كُنتُمْ أَنتُمْ وَءَابَآؤُكُمْ فِى ضَلَٰلٍۢ مُّبِينٍۢ',21,54,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ أَجِئْتَنَا بِٱلْحَقِّ أَمْ أَنتَ مِنَ ٱللَّٰعِبِينَ',21,55,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ بَل رَّبُّكُمْ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ٱلَّذِى فَطَرَهُنَّ وَأَنَا۠ عَلَىٰ ذَٰلِكُم مِّنَ ٱلشَّٰهِدِينَ',21,56,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتَٱللَّهِ لَأَكِيدَنَّ أَصْنَٰمَكُم بَعْدَ أَن تُوَلُّوا۟ مُدْبِرِينَ',21,57,326,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَجَعَلَهُمْ جُذَٰذًا إِلَّا كَبِيرًۭا لَّهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ',21,58,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ مَن فَعَلَ هَٰذَا بِـَٔالِهَتِنَآ إِنَّهُۥ لَمِنَ ٱلظَّٰلِمِينَ',21,59,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ سَمِعْنَا فَتًۭى يَذْكُرُهُمْ يُقَالُ لَهُۥٓ إِبْرَٰهِيمُ',21,60,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ فَأْتُوا۟ بِهِۦ عَلَىٰٓ أَعْيُنِ ٱلنَّاسِ لَعَلَّهُمْ يَشْهَدُونَ',21,61,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ ءَأَنتَ فَعَلْتَ هَٰذَا بِـَٔالِهَتِنَا يَٰٓإِبْرَٰهِيمُ',21,62,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ بَلْ فَعَلَهُۥ كَبِيرُهُمْ هَٰذَا فَسْـَٔلُوهُمْ إِن كَانُوا۟ يَنطِقُونَ',21,63,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَرَجَعُوٓا۟ إِلَىٰٓ أَنفُسِهِمْ فَقَالُوٓا۟ إِنَّكُمْ أَنتُمُ ٱلظَّٰلِمُونَ',21,64,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ نُكِسُوا۟ عَلَىٰ رُءُوسِهِمْ لَقَدْ عَلِمْتَ مَا هَٰٓؤُلَآءِ يَنطِقُونَ',21,65,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ أَفَتَعْبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُكُمْ شَيْـًۭٔا وَلَا يَضُرُّكُمْ',21,66,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أُفٍّۢ لَّكُمْ وَلِمَا تَعْبُدُونَ مِن دُونِ ٱللَّهِ ۖ أَفَلَا تَعْقِلُونَ',21,67,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ حَرِّقُوهُ وَٱنصُرُوٓا۟ ءَالِهَتَكُمْ إِن كُنتُمْ فَٰعِلِينَ',21,68,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْنَا يَٰنَارُ كُونِى بَرْدًۭا وَسَلَٰمًا عَلَىٰٓ إِبْرَٰهِيمَ',21,69,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَرَادُوا۟ بِهِۦ كَيْدًۭا فَجَعَلْنَٰهُمُ ٱلْأَخْسَرِينَ',21,70,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَجَّيْنَٰهُ وَلُوطًا إِلَى ٱلْأَرْضِ ٱلَّتِى بَٰرَكْنَا فِيهَا لِلْعَٰلَمِينَ',21,71,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَوَهَبْنَا لَهُۥٓ إِسْحَٰقَ وَيَعْقُوبَ نَافِلَةًۭ ۖ وَكُلًّۭا جَعَلْنَا صَٰلِحِينَ',21,72,327,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلْنَٰهُمْ أَئِمَّةًۭ يَهْدُونَ بِأَمْرِنَا وَأَوْحَيْنَآ إِلَيْهِمْ فِعْلَ ٱلْخَيْرَٰتِ وَإِقَامَ ٱلصَّلَوٰةِ وَإِيتَآءَ ٱلزَّكَوٰةِ ۖ وَكَانُوا۟ لَنَا عَٰبِدِينَ',21,73,328,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلُوطًا ءَاتَيْنَٰهُ حُكْمًۭا وَعِلْمًۭا وَنَجَّيْنَٰهُ مِنَ ٱلْقَرْيَةِ ٱلَّتِى كَانَت تَّعْمَلُ ٱلْخَبَٰٓئِثَ ۗ إِنَّهُمْ كَانُوا۟ قَوْمَ سَوْءٍۢ فَٰسِقِينَ',21,74,328,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَدْخَلْنَٰهُ فِى رَحْمَتِنَآ ۖ إِنَّهُۥ مِنَ ٱلصَّٰلِحِينَ',21,75,328,282,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنُوحًا إِذْ نَادَىٰ مِن قَبْلُ فَٱسْتَجَبْنَا لَهُۥ فَنَجَّيْنَٰهُ وَأَهْلَهُۥ مِنَ ٱلْكَرْبِ ٱلْعَظِيمِ',21,76,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَصَرْنَٰهُ مِنَ ٱلْقَوْمِ ٱلَّذِينَ كَذَّبُوا۟ بِـَٔايَٰتِنَآ ۚ إِنَّهُمْ كَانُوا۟ قَوْمَ سَوْءٍۢ فَأَغْرَقْنَٰهُمْ أَجْمَعِينَ',21,77,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَدَاوُۥدَ وَسُلَيْمَٰنَ إِذْ يَحْكُمَانِ فِى ٱلْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ ٱلْقَوْمِ وَكُنَّا لِحُكْمِهِمْ شَٰهِدِينَ',21,78,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَفَهَّمْنَٰهَا سُلَيْمَٰنَ ۚ وَكُلًّا ءَاتَيْنَا حُكْمًۭا وَعِلْمًۭا ۚ وَسَخَّرْنَا مَعَ دَاوُۥدَ ٱلْجِبَالَ يُسَبِّحْنَ وَٱلطَّيْرَ ۚ وَكُنَّا فَٰعِلِينَ',21,79,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَعَلَّمْنَٰهُ صَنْعَةَ لَبُوسٍۢ لَّكُمْ لِتُحْصِنَكُم مِّنۢ بَأْسِكُمْ ۖ فَهَلْ أَنتُمْ شَٰكِرُونَ',21,80,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلِسُلَيْمَٰنَ ٱلرِّيحَ عَاصِفَةًۭ تَجْرِى بِأَمْرِهِۦٓ إِلَى ٱلْأَرْضِ ٱلَّتِى بَٰرَكْنَا فِيهَا ۚ وَكُنَّا بِكُلِّ شَىْءٍ عَٰلِمِينَ',21,81,328,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنَ ٱلشَّيَٰطِينِ مَن يَغُوصُونَ لَهُۥ وَيَعْمَلُونَ عَمَلًۭا دُونَ ذَٰلِكَ ۖ وَكُنَّا لَهُمْ حَٰفِظِينَ',21,82,329,283,131,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَأَيُّوبَ إِذْ نَادَىٰ رَبَّهُۥٓ أَنِّى مَسَّنِىَ ٱلضُّرُّ وَأَنتَ أَرْحَمُ ٱلرَّٰحِمِينَ',21,83,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱسْتَجَبْنَا لَهُۥ فَكَشَفْنَا مَا بِهِۦ مِن ضُرٍّۢ ۖ وَءَاتَيْنَٰهُ أَهْلَهُۥ وَمِثْلَهُم مَّعَهُمْ رَحْمَةًۭ مِّنْ عِندِنَا وَذِكْرَىٰ لِلْعَٰبِدِينَ',21,84,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِسْمَٰعِيلَ وَإِدْرِيسَ وَذَا ٱلْكِفْلِ ۖ كُلٌّۭ مِّنَ ٱلصَّٰبِرِينَ',21,85,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَدْخَلْنَٰهُمْ فِى رَحْمَتِنَآ ۖ إِنَّهُم مِّنَ ٱلصَّٰلِحِينَ',21,86,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَذَا ٱلنُّونِ إِذ ذَّهَبَ مُغَٰضِبًۭا فَظَنَّ أَن لَّن نَّقْدِرَ عَلَيْهِ فَنَادَىٰ فِى ٱلظُّلُمَٰتِ أَن لَّآ إِلَٰهَ إِلَّآ أَنتَ سُبْحَٰنَكَ إِنِّى كُنتُ مِنَ ٱلظَّٰلِمِينَ',21,87,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱسْتَجَبْنَا لَهُۥ وَنَجَّيْنَٰهُ مِنَ ٱلْغَمِّ ۚ وَكَذَٰلِكَ نُۨجِى ٱلْمُؤْمِنِينَ',21,88,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَزَكَرِيَّآ إِذْ نَادَىٰ رَبَّهُۥ رَبِّ لَا تَذَرْنِى فَرْدًۭا وَأَنتَ خَيْرُ ٱلْوَٰرِثِينَ',21,89,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱسْتَجَبْنَا لَهُۥ وَوَهَبْنَا لَهُۥ يَحْيَىٰ وَأَصْلَحْنَا لَهُۥ زَوْجَهُۥٓ ۚ إِنَّهُمْ كَانُوا۟ يُسَٰرِعُونَ فِى ٱلْخَيْرَٰتِ وَيَدْعُونَنَا رَغَبًۭا وَرَهَبًۭا ۖ وَكَانُوا۟ لَنَا خَٰشِعِينَ',21,90,329,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّتِىٓ أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهَا مِن رُّوحِنَا وَجَعَلْنَٰهَا وَٱبْنَهَآ ءَايَةًۭ لِّلْعَٰلَمِينَ',21,91,330,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ هَٰذِهِۦٓ أُمَّتُكُمْ أُمَّةًۭ وَٰحِدَةًۭ وَأَنَا۠ رَبُّكُمْ فَٱعْبُدُونِ',21,92,330,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتَقَطَّعُوٓا۟ أَمْرَهُم بَيْنَهُمْ ۖ كُلٌّ إِلَيْنَا رَٰجِعُونَ',21,93,330,283,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَمَن يَعْمَلْ مِنَ ٱلصَّٰلِحَٰتِ وَهُوَ مُؤْمِنٌۭ فَلَا كُفْرَانَ لِسَعْيِهِۦ وَإِنَّا لَهُۥ كَٰتِبُونَ',21,94,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَحَرَٰمٌ عَلَىٰ قَرْيَةٍ أَهْلَكْنَٰهَآ أَنَّهُمْ لَا يَرْجِعُونَ',21,95,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُم مِّن كُلِّ حَدَبٍۢ يَنسِلُونَ',21,96,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱقْتَرَبَ ٱلْوَعْدُ ٱلْحَقُّ فَإِذَا هِىَ شَٰخِصَةٌ أَبْصَٰرُ ٱلَّذِينَ كَفَرُوا۟ يَٰوَيْلَنَا قَدْ كُنَّا فِى غَفْلَةٍۢ مِّنْ هَٰذَا بَلْ كُنَّا ظَٰلِمِينَ',21,97,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّكُمْ وَمَا تَعْبُدُونَ مِن دُونِ ٱللَّهِ حَصَبُ جَهَنَّمَ أَنتُمْ لَهَا وَٰرِدُونَ',21,98,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَوْ كَانَ هَٰٓؤُلَآءِ ءَالِهَةًۭ مَّا وَرَدُوهَا ۖ وَكُلٌّۭ فِيهَا خَٰلِدُونَ',21,99,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَهُمْ فِيهَا زَفِيرٌۭ وَهُمْ فِيهَا لَا يَسْمَعُونَ',21,100,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ سَبَقَتْ لَهُم مِّنَّا ٱلْحُسْنَىٰٓ أُو۟لَٰٓئِكَ عَنْهَا مُبْعَدُونَ',21,101,330,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا يَسْمَعُونَ حَسِيسَهَا ۖ وَهُمْ فِى مَا ٱشْتَهَتْ أَنفُسُهُمْ خَٰلِدُونَ',21,102,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا يَحْزُنُهُمُ ٱلْفَزَعُ ٱلْأَكْبَرُ وَتَتَلَقَّىٰهُمُ ٱلْمَلَٰٓئِكَةُ هَٰذَا يَوْمُكُمُ ٱلَّذِى كُنتُمْ تُوعَدُونَ',21,103,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ نَطْوِى ٱلسَّمَآءَ كَطَىِّ ٱلسِّجِلِّ لِلْكُتُبِ ۚ كَمَا بَدَأْنَآ أَوَّلَ خَلْقٍۢ نُّعِيدُهُۥ ۚ وَعْدًا عَلَيْنَآ ۚ إِنَّا كُنَّا فَٰعِلِينَ',21,104,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ كَتَبْنَا فِى ٱلزَّبُورِ مِنۢ بَعْدِ ٱلذِّكْرِ أَنَّ ٱلْأَرْضَ يَرِثُهَا عِبَادِىَ ٱلصَّٰلِحُونَ',21,105,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ فِى هَٰذَا لَبَلَٰغًۭا لِّقَوْمٍ عَٰبِدِينَ',21,106,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَٰكَ إِلَّا رَحْمَةًۭ لِّلْعَٰلَمِينَ',21,107,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنَّمَا يُوحَىٰٓ إِلَىَّ أَنَّمَآ إِلَٰهُكُمْ إِلَٰهٌۭ وَٰحِدٌۭ ۖ فَهَلْ أَنتُم مُّسْلِمُونَ',21,108,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِن تَوَلَّوْا۟ فَقُلْ ءَاذَنتُكُمْ عَلَىٰ سَوَآءٍۢ ۖ وَإِنْ أَدْرِىٓ أَقَرِيبٌ أَم بَعِيدٌۭ مَّا تُوعَدُونَ',21,109,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّهُۥ يَعْلَمُ ٱلْجَهْرَ مِنَ ٱلْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ',21,110,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنْ أَدْرِى لَعَلَّهُۥ فِتْنَةٌۭ لَّكُمْ وَمَتَٰعٌ إِلَىٰ حِينٍۢ',21,111,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَٰلَ رَبِّ ٱحْكُم بِٱلْحَقِّ ۗ وَرَبُّنَا ٱلرَّحْمَٰنُ ٱلْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَ',21,112,331,284,132,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ يَٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمْ ۚ إِنَّ زَلْزَلَةَ ٱلسَّاعَةِ شَىْءٌ عَظِيمٌۭ',22,1,332,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّآ أَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى ٱلنَّاسَ سُكَٰرَىٰ وَمَا هُم بِسُكَٰرَىٰ وَلَٰكِنَّ عَذَابَ ٱللَّهِ شَدِيدٌۭ',22,2,332,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِى ٱللَّهِ بِغَيْرِ عِلْمٍۢ وَيَتَّبِعُ كُلَّ شَيْطَٰنٍۢ مَّرِيدٍۢ',22,3,332,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كُتِبَ عَلَيْهِ أَنَّهُۥ مَن تَوَلَّاهُ فَأَنَّهُۥ يُضِلُّهُۥ وَيَهْدِيهِ إِلَىٰ عَذَابِ ٱلسَّعِيرِ',22,4,332,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلنَّاسُ إِن كُنتُمْ فِى رَيْبٍۢ مِّنَ ٱلْبَعْثِ فَإِنَّا خَلَقْنَٰكُم مِّن تُرَابٍۢ ثُمَّ مِن نُّطْفَةٍۢ ثُمَّ مِنْ عَلَقَةٍۢ ثُمَّ مِن مُّضْغَةٍۢ مُّخَلَّقَةٍۢ وَغَيْرِ مُخَلَّقَةٍۢ لِّنُبَيِّنَ لَكُمْ ۚ وَنُقِرُّ فِى ٱلْأَرْحَامِ مَا نَشَآءُ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى ثُمَّ نُخْرِجُكُمْ طِفْلًۭا ثُمَّ لِتَبْلُغُوٓا۟ أَشُدَّكُمْ ۖ وَمِنكُم مَّن يُتَوَفَّىٰ وَمِنكُم مَّن يُرَدُّ إِلَىٰٓ أَرْذَلِ ٱلْعُمُرِ لِكَيْلَا يَعْلَمَ مِنۢ بَعْدِ عِلْمٍۢ شَيْـًۭٔا ۚ وَتَرَى ٱلْأَرْضَ هَامِدَةًۭ فَإِذَآ أَنزَلْنَا عَلَيْهَا ٱلْمَآءَ ٱهْتَزَّتْ وَرَبَتْ وَأَنۢبَتَتْ مِن كُلِّ زَوْجٍۭ بَهِيجٍۢ',22,5,332,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلْحَقُّ وَأَنَّهُۥ يُحْىِ ٱلْمَوْتَىٰ وَأَنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ',22,6,333,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنَّ ٱلسَّاعَةَ ءَاتِيَةٌۭ لَّا رَيْبَ فِيهَا وَأَنَّ ٱللَّهَ يَبْعَثُ مَن فِى ٱلْقُبُورِ',22,7,333,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِى ٱللَّهِ بِغَيْرِ عِلْمٍۢ وَلَا هُدًۭى وَلَا كِتَٰبٍۢ مُّنِيرٍۢ',22,8,333,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثَانِىَ عِطْفِهِۦ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ ۖ لَهُۥ فِى ٱلدُّنْيَا خِزْىٌۭ ۖ وَنُذِيقُهُۥ يَوْمَ ٱلْقِيَٰمَةِ عَذَابَ ٱلْحَرِيقِ',22,9,333,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَأَنَّ ٱللَّهَ لَيْسَ بِظَلَّٰمٍۢ لِّلْعَبِيدِ',22,10,333,285,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنَ ٱلنَّاسِ مَن يَعْبُدُ ٱللَّهَ عَلَىٰ حَرْفٍۢ ۖ فَإِنْ أَصَابَهُۥ خَيْرٌ ٱطْمَأَنَّ بِهِۦ ۖ وَإِنْ أَصَابَتْهُ فِتْنَةٌ ٱنقَلَبَ عَلَىٰ وَجْهِهِۦ خَسِرَ ٱلدُّنْيَا وَٱلْءَاخِرَةَ ۚ ذَٰلِكَ هُوَ ٱلْخُسْرَانُ ٱلْمُبِينُ',22,11,333,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَدْعُوا۟ مِن دُونِ ٱللَّهِ مَا لَا يَضُرُّهُۥ وَمَا لَا يَنفَعُهُۥ ۚ ذَٰلِكَ هُوَ ٱلضَّلَٰلُ ٱلْبَعِيدُ',22,12,333,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَدْعُوا۟ لَمَن ضَرُّهُۥٓ أَقْرَبُ مِن نَّفْعِهِۦ ۚ لَبِئْسَ ٱلْمَوْلَىٰ وَلَبِئْسَ ٱلْعَشِيرُ',22,13,333,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱللَّهَ يُدْخِلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ جَنَّٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ ۚ إِنَّ ٱللَّهَ يَفْعَلُ مَا يُرِيدُ',22,14,333,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَن كَانَ يَظُنُّ أَن لَّن يَنصُرَهُ ٱللَّهُ فِى ٱلدُّنْيَا وَٱلْءَاخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى ٱلسَّمَآءِ ثُمَّ لْيَقْطَعْ فَلْيَنظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُۥ مَا يَغِيظُ',22,15,333,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ أَنزَلْنَٰهُ ءَايَٰتٍۭ بَيِّنَٰتٍۢ وَأَنَّ ٱللَّهَ يَهْدِى مَن يُرِيدُ',22,16,334,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَٱلَّذِينَ هَادُوا۟ وَٱلصَّٰبِـِٔينَ وَٱلنَّصَٰرَىٰ وَٱلْمَجُوسَ وَٱلَّذِينَ أَشْرَكُوٓا۟ إِنَّ ٱللَّهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ ٱلْقِيَٰمَةِ ۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍۢ شَهِيدٌ',22,17,334,286,133,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ يَسْجُدُ لَهُۥ مَن فِى ٱلسَّمَٰوَٰتِ وَمَن فِى ٱلْأَرْضِ وَٱلشَّمْسُ وَٱلْقَمَرُ وَٱلنُّجُومُ وَٱلْجِبَالُ وَٱلشَّجَرُ وَٱلدَّوَآبُّ وَكَثِيرٌۭ مِّنَ ٱلنَّاسِ ۖ وَكَثِيرٌ حَقَّ عَلَيْهِ ٱلْعَذَابُ ۗ وَمَن يُهِنِ ٱللَّهُ فَمَا لَهُۥ مِن مُّكْرِمٍ ۚ إِنَّ ٱللَّهَ يَفْعَلُ مَا يَشَآءُ ۩',22,18,334,286,133,true);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ هَٰذَانِ خَصْمَانِ ٱخْتَصَمُوا۟ فِى رَبِّهِمْ ۖ فَٱلَّذِينَ كَفَرُوا۟ قُطِّعَتْ لَهُمْ ثِيَابٌۭ مِّن نَّارٍۢ يُصَبُّ مِن فَوْقِ رُءُوسِهِمُ ٱلْحَمِيمُ',22,19,334,286,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُصْهَرُ بِهِۦ مَا فِى بُطُونِهِمْ وَٱلْجُلُودُ',22,20,334,286,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَهُم مَّقَٰمِعُ مِنْ حَدِيدٍۢ',22,21,334,286,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كُلَّمَآ أَرَادُوٓا۟ أَن يَخْرُجُوا۟ مِنْهَا مِنْ غَمٍّ أُعِيدُوا۟ فِيهَا وَذُوقُوا۟ عَذَابَ ٱلْحَرِيقِ',22,22,334,286,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱللَّهَ يُدْخِلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ جَنَّٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍۢ وَلُؤْلُؤًۭا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌۭ',22,23,334,287,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُدُوٓا۟ إِلَى ٱلطَّيِّبِ مِنَ ٱلْقَوْلِ وَهُدُوٓا۟ إِلَىٰ صِرَٰطِ ٱلْحَمِيدِ',22,24,335,287,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ كَفَرُوا۟ وَيَصُدُّونَ عَن سَبِيلِ ٱللَّهِ وَٱلْمَسْجِدِ ٱلْحَرَامِ ٱلَّذِى جَعَلْنَٰهُ لِلنَّاسِ سَوَآءً ٱلْعَٰكِفُ فِيهِ وَٱلْبَادِ ۚ وَمَن يُرِدْ فِيهِ بِإِلْحَادٍۭ بِظُلْمٍۢ نُّذِقْهُ مِنْ عَذَابٍ أَلِيمٍۢ',22,25,335,287,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذْ بَوَّأْنَا لِإِبْرَٰهِيمَ مَكَانَ ٱلْبَيْتِ أَن لَّا تُشْرِكْ بِى شَيْـًۭٔا وَطَهِّرْ بَيْتِىَ لِلطَّآئِفِينَ وَٱلْقَآئِمِينَ وَٱلرُّكَّعِ ٱلسُّجُودِ',22,26,335,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَذِّن فِى ٱلنَّاسِ بِٱلْحَجِّ يَأْتُوكَ رِجَالًۭا وَعَلَىٰ كُلِّ ضَامِرٍۢ يَأْتِينَ مِن كُلِّ فَجٍّ عَمِيقٍۢ',22,27,335,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِّيَشْهَدُوا۟ مَنَٰفِعَ لَهُمْ وَيَذْكُرُوا۟ ٱسْمَ ٱللَّهِ فِىٓ أَيَّامٍۢ مَّعْلُومَٰتٍ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلْأَنْعَٰمِ ۖ فَكُلُوا۟ مِنْهَا وَأَطْعِمُوا۟ ٱلْبَآئِسَ ٱلْفَقِيرَ',22,28,335,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ لْيَقْضُوا۟ تَفَثَهُمْ وَلْيُوفُوا۟ نُذُورَهُمْ وَلْيَطَّوَّفُوا۟ بِٱلْبَيْتِ ٱلْعَتِيقِ',22,29,335,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ وَمَن يُعَظِّمْ حُرُمَٰتِ ٱللَّهِ فَهُوَ خَيْرٌۭ لَّهُۥ عِندَ رَبِّهِۦ ۗ وَأُحِلَّتْ لَكُمُ ٱلْأَنْعَٰمُ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ ۖ فَٱجْتَنِبُوا۟ ٱلرِّجْسَ مِنَ ٱلْأَوْثَٰنِ وَٱجْتَنِبُوا۟ قَوْلَ ٱلزُّورِ',22,30,335,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حُنَفَآءَ لِلَّهِ غَيْرَ مُشْرِكِينَ بِهِۦ ۚ وَمَن يُشْرِكْ بِٱللَّهِ فَكَأَنَّمَا خَرَّ مِنَ ٱلسَّمَآءِ فَتَخْطَفُهُ ٱلطَّيْرُ أَوْ تَهْوِى بِهِ ٱلرِّيحُ فِى مَكَانٍۢ سَحِيقٍۢ',22,31,336,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ وَمَن يُعَظِّمْ شَعَٰٓئِرَ ٱللَّهِ فَإِنَّهَا مِن تَقْوَى ٱلْقُلُوبِ',22,32,336,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَكُمْ فِيهَا مَنَٰفِعُ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى ثُمَّ مَحِلُّهَآ إِلَى ٱلْبَيْتِ ٱلْعَتِيقِ',22,33,336,288,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلِكُلِّ أُمَّةٍۢ جَعَلْنَا مَنسَكًۭا لِّيَذْكُرُوا۟ ٱسْمَ ٱللَّهِ عَلَىٰ مَا رَزَقَهُم مِّنۢ بَهِيمَةِ ٱلْأَنْعَٰمِ ۗ فَإِلَٰهُكُمْ إِلَٰهٌۭ وَٰحِدٌۭ فَلَهُۥٓ أَسْلِمُوا۟ ۗ وَبَشِّرِ ٱلْمُخْبِتِينَ',22,34,336,289,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ إِذَا ذُكِرَ ٱللَّهُ وَجِلَتْ قُلُوبُهُمْ وَٱلصَّٰبِرِينَ عَلَىٰ مَآ أَصَابَهُمْ وَٱلْمُقِيمِى ٱلصَّلَوٰةِ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ',22,35,336,289,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلْبُدْنَ جَعَلْنَٰهَا لَكُم مِّن شَعَٰٓئِرِ ٱللَّهِ لَكُمْ فِيهَا خَيْرٌۭ ۖ فَٱذْكُرُوا۟ ٱسْمَ ٱللَّهِ عَلَيْهَا صَوَآفَّ ۖ فَإِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا۟ مِنْهَا وَأَطْعِمُوا۟ ٱلْقَانِعَ وَٱلْمُعْتَرَّ ۚ كَذَٰلِكَ سَخَّرْنَٰهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ',22,36,336,289,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَن يَنَالَ ٱللَّهَ لُحُومُهَا وَلَا دِمَآؤُهَا وَلَٰكِن يَنَالُهُ ٱلتَّقْوَىٰ مِنكُمْ ۚ كَذَٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا۟ ٱللَّهَ عَلَىٰ مَا هَدَىٰكُمْ ۗ وَبَشِّرِ ٱلْمُحْسِنِينَ',22,37,336,289,134,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ إِنَّ ٱللَّهَ يُدَٰفِعُ عَنِ ٱلَّذِينَ ءَامَنُوٓا۟ ۗ إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٍۢ كَفُورٍ',22,38,336,289,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أُذِنَ لِلَّذِينَ يُقَٰتَلُونَ بِأَنَّهُمْ ظُلِمُوا۟ ۚ وَإِنَّ ٱللَّهَ عَلَىٰ نَصْرِهِمْ لَقَدِيرٌ',22,39,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ أُخْرِجُوا۟ مِن دِيَٰرِهِم بِغَيْرِ حَقٍّ إِلَّآ أَن يَقُولُوا۟ رَبُّنَا ٱللَّهُ ۗ وَلَوْلَا دَفْعُ ٱللَّهِ ٱلنَّاسَ بَعْضَهُم بِبَعْضٍۢ لَّهُدِّمَتْ صَوَٰمِعُ وَبِيَعٌۭ وَصَلَوَٰتٌۭ وَمَسَٰجِدُ يُذْكَرُ فِيهَا ٱسْمُ ٱللَّهِ كَثِيرًۭا ۗ وَلَيَنصُرَنَّ ٱللَّهُ مَن يَنصُرُهُۥٓ ۗ إِنَّ ٱللَّهَ لَقَوِىٌّ عَزِيزٌ',22,40,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ إِن مَّكَّنَّٰهُمْ فِى ٱلْأَرْضِ أَقَامُوا۟ ٱلصَّلَوٰةَ وَءَاتَوُا۟ ٱلزَّكَوٰةَ وَأَمَرُوا۟ بِٱلْمَعْرُوفِ وَنَهَوْا۟ عَنِ ٱلْمُنكَرِ ۗ وَلِلَّهِ عَٰقِبَةُ ٱلْأُمُورِ',22,41,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِن يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ وَعَادٌۭ وَثَمُودُ',22,42,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَوْمُ إِبْرَٰهِيمَ وَقَوْمُ لُوطٍۢ',22,43,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَصْحَٰبُ مَدْيَنَ ۖ وَكُذِّبَ مُوسَىٰ فَأَمْلَيْتُ لِلْكَٰفِرِينَ ثُمَّ أَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ نَكِيرِ',22,44,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَكَأَيِّن مِّن قَرْيَةٍ أَهْلَكْنَٰهَا وَهِىَ ظَالِمَةٌۭ فَهِىَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئْرٍۢ مُّعَطَّلَةٍۢ وَقَصْرٍۢ مَّشِيدٍ',22,45,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌۭ يَعْقِلُونَ بِهَآ أَوْ ءَاذَانٌۭ يَسْمَعُونَ بِهَا ۖ فَإِنَّهَا لَا تَعْمَى ٱلْأَبْصَٰرُ وَلَٰكِن تَعْمَى ٱلْقُلُوبُ ٱلَّتِى فِى ٱلصُّدُورِ',22,46,337,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَسْتَعْجِلُونَكَ بِٱلْعَذَابِ وَلَن يُخْلِفَ ٱللَّهُ وَعْدَهُۥ ۚ وَإِنَّ يَوْمًا عِندَ رَبِّكَ كَأَلْفِ سَنَةٍۢ مِّمَّا تَعُدُّونَ',22,47,338,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَأَيِّن مِّن قَرْيَةٍ أَمْلَيْتُ لَهَا وَهِىَ ظَالِمَةٌۭ ثُمَّ أَخَذْتُهَا وَإِلَىَّ ٱلْمَصِيرُ',22,48,338,290,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ يَٰٓأَيُّهَا ٱلنَّاسُ إِنَّمَآ أَنَا۠ لَكُمْ نَذِيرٌۭ مُّبِينٌۭ',22,49,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُم مَّغْفِرَةٌۭ وَرِزْقٌۭ كَرِيمٌۭ',22,50,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ سَعَوْا۟ فِىٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلْجَحِيمِ',22,51,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَا مِن قَبْلِكَ مِن رَّسُولٍۢ وَلَا نَبِىٍّ إِلَّآ إِذَا تَمَنَّىٰٓ أَلْقَى ٱلشَّيْطَٰنُ فِىٓ أُمْنِيَّتِهِۦ فَيَنسَخُ ٱللَّهُ مَا يُلْقِى ٱلشَّيْطَٰنُ ثُمَّ يُحْكِمُ ٱللَّهُ ءَايَٰتِهِۦ ۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌۭ',22,52,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِّيَجْعَلَ مَا يُلْقِى ٱلشَّيْطَٰنُ فِتْنَةًۭ لِّلَّذِينَ فِى قُلُوبِهِم مَّرَضٌۭ وَٱلْقَاسِيَةِ قُلُوبُهُمْ ۗ وَإِنَّ ٱلظَّٰلِمِينَ لَفِى شِقَاقٍۭ بَعِيدٍۢ',22,53,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلِيَعْلَمَ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ أَنَّهُ ٱلْحَقُّ مِن رَّبِّكَ فَيُؤْمِنُوا۟ بِهِۦ فَتُخْبِتَ لَهُۥ قُلُوبُهُمْ ۗ وَإِنَّ ٱللَّهَ لَهَادِ ٱلَّذِينَ ءَامَنُوٓا۟ إِلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ',22,54,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا يَزَالُ ٱلَّذِينَ كَفَرُوا۟ فِى مِرْيَةٍۢ مِّنْهُ حَتَّىٰ تَأْتِيَهُمُ ٱلسَّاعَةُ بَغْتَةً أَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيمٍ',22,55,338,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلْمُلْكُ يَوْمَئِذٍۢ لِّلَّهِ يَحْكُمُ بَيْنَهُمْ ۚ فَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فِى جَنَّٰتِ ٱلنَّعِيمِ',22,56,339,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَٰتِنَا فَأُو۟لَٰٓئِكَ لَهُمْ عَذَابٌۭ مُّهِينٌۭ',22,57,339,291,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هَاجَرُوا۟ فِى سَبِيلِ ٱللَّهِ ثُمَّ قُتِلُوٓا۟ أَوْ مَاتُوا۟ لَيَرْزُقَنَّهُمُ ٱللَّهُ رِزْقًا حَسَنًۭا ۚ وَإِنَّ ٱللَّهَ لَهُوَ خَيْرُ ٱلرَّٰزِقِينَ',22,58,339,292,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَيُدْخِلَنَّهُم مُّدْخَلًۭا يَرْضَوْنَهُۥ ۗ وَإِنَّ ٱللَّهَ لَعَلِيمٌ حَلِيمٌۭ',22,59,339,292,135,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ ذَٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِهِۦ ثُمَّ بُغِىَ عَلَيْهِ لَيَنصُرَنَّهُ ٱللَّهُ ۗ إِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٌۭ',22,60,339,292,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ بِأَنَّ ٱللَّهَ يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَأَنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌۭ',22,61,339,292,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلْحَقُّ وَأَنَّ مَا يَدْعُونَ مِن دُونِهِۦ هُوَ ٱلْبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلْعَلِىُّ ٱلْكَبِيرُ',22,62,339,292,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَتُصْبِحُ ٱلْأَرْضُ مُخْضَرَّةً ۗ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٌۭ',22,63,339,292,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۗ وَإِنَّ ٱللَّهَ لَهُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ',22,64,339,292,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِى ٱلْأَرْضِ وَٱلْفُلْكَ تَجْرِى فِى ٱلْبَحْرِ بِأَمْرِهِۦ وَيُمْسِكُ ٱلسَّمَآءَ أَن تَقَعَ عَلَى ٱلْأَرْضِ إِلَّا بِإِذْنِهِۦٓ ۗ إِنَّ ٱللَّهَ بِٱلنَّاسِ لَرَءُوفٌۭ رَّحِيمٌۭ',22,65,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِىٓ أَحْيَاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۗ إِنَّ ٱلْإِنسَٰنَ لَكَفُورٌۭ',22,66,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِّكُلِّ أُمَّةٍۢ جَعَلْنَا مَنسَكًا هُمْ نَاسِكُوهُ ۖ فَلَا يُنَٰزِعُنَّكَ فِى ٱلْأَمْرِ ۚ وَٱدْعُ إِلَىٰ رَبِّكَ ۖ إِنَّكَ لَعَلَىٰ هُدًۭى مُّسْتَقِيمٍۢ',22,67,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِن جَٰدَلُوكَ فَقُلِ ٱللَّهُ أَعْلَمُ بِمَا تَعْمَلُونَ',22,68,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ ٱلْقِيَٰمَةِ فِيمَا كُنتُمْ فِيهِ تَخْتَلِفُونَ',22,69,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَعْلَمْ أَنَّ ٱللَّهَ يَعْلَمُ مَا فِى ٱلسَّمَآءِ وَٱلْأَرْضِ ۗ إِنَّ ذَٰلِكَ فِى كِتَٰبٍ ۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌۭ',22,70,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَعْبُدُونَ مِن دُونِ ٱللَّهِ مَا لَمْ يُنَزِّلْ بِهِۦ سُلْطَٰنًۭا وَمَا لَيْسَ لَهُم بِهِۦ عِلْمٌۭ ۗ وَمَا لِلظَّٰلِمِينَ مِن نَّصِيرٍۢ',22,71,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا بَيِّنَٰتٍۢ تَعْرِفُ فِى وُجُوهِ ٱلَّذِينَ كَفَرُوا۟ ٱلْمُنكَرَ ۖ يَكَادُونَ يَسْطُونَ بِٱلَّذِينَ يَتْلُونَ عَلَيْهِمْ ءَايَٰتِنَا ۗ قُلْ أَفَأُنَبِّئُكُم بِشَرٍّۢ مِّن ذَٰلِكُمُ ۗ ٱلنَّارُ وَعَدَهَا ٱللَّهُ ٱلَّذِينَ كَفَرُوا۟ ۖ وَبِئْسَ ٱلْمَصِيرُ',22,72,340,293,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلنَّاسُ ضُرِبَ مَثَلٌۭ فَٱسْتَمِعُوا۟ لَهُۥٓ ۚ إِنَّ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ لَن يَخْلُقُوا۟ ذُبَابًۭا وَلَوِ ٱجْتَمَعُوا۟ لَهُۥ ۖ وَإِن يَسْلُبْهُمُ ٱلذُّبَابُ شَيْـًۭٔا لَّا يَسْتَنقِذُوهُ مِنْهُ ۚ ضَعُفَ ٱلطَّالِبُ وَٱلْمَطْلُوبُ',22,73,341,294,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدْرِهِۦٓ ۗ إِنَّ ٱللَّهَ لَقَوِىٌّ عَزِيزٌ',22,74,341,294,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ يَصْطَفِى مِنَ ٱلْمَلَٰٓئِكَةِ رُسُلًۭا وَمِنَ ٱلنَّاسِ ۚ إِنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌۭ',22,75,341,294,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ ۗ وَإِلَى ٱللَّهِ تُرْجَعُ ٱلْأُمُورُ',22,76,341,294,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱرْكَعُوا۟ وَٱسْجُدُوا۟ وَٱعْبُدُوا۟ رَبَّكُمْ وَٱفْعَلُوا۟ ٱلْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ ۩',22,77,341,294,136,true);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَٰهِدُوا۟ فِى ٱللَّهِ حَقَّ جِهَادِهِۦ ۚ هُوَ ٱجْتَبَىٰكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِى ٱلدِّينِ مِنْ حَرَجٍۢ ۚ مِّلَّةَ أَبِيكُمْ إِبْرَٰهِيمَ ۚ هُوَ سَمَّىٰكُمُ ٱلْمُسْلِمِينَ مِن قَبْلُ وَفِى هَٰذَا لِيَكُونَ ٱلرَّسُولُ شَهِيدًا عَلَيْكُمْ وَتَكُونُوا۟ شُهَدَآءَ عَلَى ٱلنَّاسِ ۚ فَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَٱعْتَصِمُوا۟ بِٱللَّهِ هُوَ مَوْلَىٰكُمْ ۖ فَنِعْمَ ٱلْمَوْلَىٰ وَنِعْمَ ٱلنَّصِيرُ',22,78,341,294,136,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ قَدْ أَفْلَحَ ٱلْمُؤْمِنُونَ',23,1,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ هُمْ فِى صَلَاتِهِمْ خَٰشِعُونَ',23,2,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُمْ عَنِ ٱللَّغْوِ مُعْرِضُونَ',23,3,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُمْ لِلزَّكَوٰةِ فَٰعِلُونَ',23,4,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُمْ لِفُرُوجِهِمْ حَٰفِظُونَ',23,5,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّا عَلَىٰٓ أَزْوَٰجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَٰنُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ',23,6,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَمَنِ ٱبْتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُو۟لَٰٓئِكَ هُمُ ٱلْعَادُونَ',23,7,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُمْ لِأَمَٰنَٰتِهِمْ وَعَهْدِهِمْ رَٰعُونَ',23,8,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُمْ عَلَىٰ صَلَوَٰتِهِمْ يُحَافِظُونَ',23,9,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أُو۟لَٰٓئِكَ هُمُ ٱلْوَٰرِثُونَ',23,10,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يَرِثُونَ ٱلْفِرْدَوْسَ هُمْ فِيهَا خَٰلِدُونَ',23,11,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ خَلَقْنَا ٱلْإِنسَٰنَ مِن سُلَٰلَةٍۢ مِّن طِينٍۢ',23,12,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ جَعَلْنَٰهُ نُطْفَةًۭ فِى قَرَارٍۢ مَّكِينٍۢ',23,13,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ خَلَقْنَا ٱلنُّطْفَةَ عَلَقَةًۭ فَخَلَقْنَا ٱلْعَلَقَةَ مُضْغَةًۭ فَخَلَقْنَا ٱلْمُضْغَةَ عِظَٰمًۭا فَكَسَوْنَا ٱلْعِظَٰمَ لَحْمًۭا ثُمَّ أَنشَأْنَٰهُ خَلْقًا ءَاخَرَ ۚ فَتَبَارَكَ ٱللَّهُ أَحْسَنُ ٱلْخَٰلِقِينَ',23,14,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ إِنَّكُم بَعْدَ ذَٰلِكَ لَمَيِّتُونَ',23,15,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ إِنَّكُمْ يَوْمَ ٱلْقِيَٰمَةِ تُبْعَثُونَ',23,16,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَآئِقَ وَمَا كُنَّا عَنِ ٱلْخَلْقِ غَٰفِلِينَ',23,17,342,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنزَلْنَا مِنَ ٱلسَّمَآءِ مَآءًۢ بِقَدَرٍۢ فَأَسْكَنَّٰهُ فِى ٱلْأَرْضِ ۖ وَإِنَّا عَلَىٰ ذَهَابٍۭ بِهِۦ لَقَٰدِرُونَ',23,18,343,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَنشَأْنَا لَكُم بِهِۦ جَنَّٰتٍۢ مِّن نَّخِيلٍۢ وَأَعْنَٰبٍۢ لَّكُمْ فِيهَا فَوَٰكِهُ كَثِيرَةٌۭ وَمِنْهَا تَأْكُلُونَ',23,19,343,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَشَجَرَةًۭ تَخْرُجُ مِن طُورِ سَيْنَآءَ تَنۢبُتُ بِٱلدُّهْنِ وَصِبْغٍۢ لِّلْءَاكِلِينَ',23,20,343,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ لَكُمْ فِى ٱلْأَنْعَٰمِ لَعِبْرَةًۭ ۖ نُّسْقِيكُم مِّمَّا فِى بُطُونِهَا وَلَكُمْ فِيهَا مَنَٰفِعُ كَثِيرَةٌۭ وَمِنْهَا تَأْكُلُونَ',23,21,343,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَعَلَيْهَا وَعَلَى ٱلْفُلْكِ تُحْمَلُونَ',23,22,343,295,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦ فَقَالَ يَٰقَوْمِ ٱعْبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥٓ ۖ أَفَلَا تَتَّقُونَ',23,23,343,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَالَ ٱلْمَلَؤُا۟ ٱلَّذِينَ كَفَرُوا۟ مِن قَوْمِهِۦ مَا هَٰذَآ إِلَّا بَشَرٌۭ مِّثْلُكُمْ يُرِيدُ أَن يَتَفَضَّلَ عَلَيْكُمْ وَلَوْ شَآءَ ٱللَّهُ لَأَنزَلَ مَلَٰٓئِكَةًۭ مَّا سَمِعْنَا بِهَٰذَا فِىٓ ءَابَآئِنَا ٱلْأَوَّلِينَ',23,24,343,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنْ هُوَ إِلَّا رَجُلٌۢ بِهِۦ جِنَّةٌۭ فَتَرَبَّصُوا۟ بِهِۦ حَتَّىٰ حِينٍۢ',23,25,343,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ ٱنصُرْنِى بِمَا كَذَّبُونِ',23,26,343,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَوْحَيْنَآ إِلَيْهِ أَنِ ٱصْنَعِ ٱلْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا فَإِذَا جَآءَ أَمْرُنَا وَفَارَ ٱلتَّنُّورُ ۙ فَٱسْلُكْ فِيهَا مِن كُلٍّۢ زَوْجَيْنِ ٱثْنَيْنِ وَأَهْلَكَ إِلَّا مَن سَبَقَ عَلَيْهِ ٱلْقَوْلُ مِنْهُمْ ۖ وَلَا تُخَٰطِبْنِى فِى ٱلَّذِينَ ظَلَمُوٓا۟ ۖ إِنَّهُم مُّغْرَقُونَ',23,27,343,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِذَا ٱسْتَوَيْتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلْفُلْكِ فَقُلِ ٱلْحَمْدُ لِلَّهِ ٱلَّذِى نَجَّىٰنَا مِنَ ٱلْقَوْمِ ٱلظَّٰلِمِينَ',23,28,344,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقُل رَّبِّ أَنزِلْنِى مُنزَلًۭا مُّبَارَكًۭا وَأَنتَ خَيْرُ ٱلْمُنزِلِينَ',23,29,344,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ وَإِن كُنَّا لَمُبْتَلِينَ',23,30,344,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ أَنشَأْنَا مِنۢ بَعْدِهِمْ قَرْنًا ءَاخَرِينَ',23,31,344,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَرْسَلْنَا فِيهِمْ رَسُولًۭا مِّنْهُمْ أَنِ ٱعْبُدُوا۟ ٱللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥٓ ۖ أَفَلَا تَتَّقُونَ',23,32,344,296,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلْمَلَأُ مِن قَوْمِهِ ٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِلِقَآءِ ٱلْءَاخِرَةِ وَأَتْرَفْنَٰهُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا مَا هَٰذَآ إِلَّا بَشَرٌۭ مِّثْلُكُمْ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ',23,33,344,297,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَئِنْ أَطَعْتُم بَشَرًۭا مِّثْلَكُمْ إِنَّكُمْ إِذًۭا لَّخَٰسِرُونَ',23,34,344,297,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَيَعِدُكُمْ أَنَّكُمْ إِذَا مِتُّمْ وَكُنتُمْ تُرَابًۭا وَعِظَٰمًا أَنَّكُم مُّخْرَجُونَ',23,35,344,297,137,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَ',23,36,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنْ هِىَ إِلَّا حَيَاتُنَا ٱلدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ',23,37,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنْ هُوَ إِلَّا رَجُلٌ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًۭا وَمَا نَحْنُ لَهُۥ بِمُؤْمِنِينَ',23,38,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ ٱنصُرْنِى بِمَا كَذَّبُونِ',23,39,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ عَمَّا قَلِيلٍۢ لَّيُصْبِحُنَّ نَٰدِمِينَ',23,40,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَخَذَتْهُمُ ٱلصَّيْحَةُ بِٱلْحَقِّ فَجَعَلْنَٰهُمْ غُثَآءًۭ ۚ فَبُعْدًۭا لِّلْقَوْمِ ٱلظَّٰلِمِينَ',23,41,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ أَنشَأْنَا مِنۢ بَعْدِهِمْ قُرُونًا ءَاخَرِينَ',23,42,344,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَـْٔخِرُونَ',23,43,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ أَرْسَلْنَا رُسُلَنَا تَتْرَا ۖ كُلَّ مَا جَآءَ أُمَّةًۭ رَّسُولُهَا كَذَّبُوهُ ۚ فَأَتْبَعْنَا بَعْضَهُم بَعْضًۭا وَجَعَلْنَٰهُمْ أَحَادِيثَ ۚ فَبُعْدًۭا لِّقَوْمٍۢ لَّا يُؤْمِنُونَ',23,44,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ أَرْسَلْنَا مُوسَىٰ وَأَخَاهُ هَٰرُونَ بِـَٔايَٰتِنَا وَسُلْطَٰنٍۢ مُّبِينٍ',23,45,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦ فَٱسْتَكْبَرُوا۟ وَكَانُوا۟ قَوْمًا عَالِينَ',23,46,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَالُوٓا۟ أَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَٰبِدُونَ',23,47,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَكَذَّبُوهُمَا فَكَانُوا۟ مِنَ ٱلْمُهْلَكِينَ',23,48,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ لَعَلَّهُمْ يَهْتَدُونَ',23,49,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلْنَا ٱبْنَ مَرْيَمَ وَأُمَّهُۥٓ ءَايَةًۭ وَءَاوَيْنَٰهُمَآ إِلَىٰ رَبْوَةٍۢ ذَاتِ قَرَارٍۢ وَمَعِينٍۢ',23,50,345,297,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلرُّسُلُ كُلُوا۟ مِنَ ٱلطَّيِّبَٰتِ وَٱعْمَلُوا۟ صَٰلِحًا ۖ إِنِّى بِمَا تَعْمَلُونَ عَلِيمٌۭ',23,51,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ هَٰذِهِۦٓ أُمَّتُكُمْ أُمَّةًۭ وَٰحِدَةًۭ وَأَنَا۠ رَبُّكُمْ فَٱتَّقُونِ',23,52,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَتَقَطَّعُوٓا۟ أَمْرَهُم بَيْنَهُمْ زُبُرًۭا ۖ كُلُّ حِزْبٍۭ بِمَا لَدَيْهِمْ فَرِحُونَ',23,53,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَذَرْهُمْ فِى غَمْرَتِهِمْ حَتَّىٰ حِينٍ',23,54,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَيَحْسَبُونَ أَنَّمَا نُمِدُّهُم بِهِۦ مِن مَّالٍۢ وَبَنِينَ',23,55,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('نُسَارِعُ لَهُمْ فِى ٱلْخَيْرَٰتِ ۚ بَل لَّا يَشْعُرُونَ',23,56,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ هُم مِّنْ خَشْيَةِ رَبِّهِم مُّشْفِقُونَ',23,57,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُم بِـَٔايَٰتِ رَبِّهِمْ يُؤْمِنُونَ',23,58,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ هُم بِرَبِّهِمْ لَا يُشْرِكُونَ',23,59,345,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يُؤْتُونَ مَآ ءَاتَوا۟ وَّقُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلَىٰ رَبِّهِمْ رَٰجِعُونَ',23,60,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أُو۟لَٰٓئِكَ يُسَٰرِعُونَ فِى ٱلْخَيْرَٰتِ وَهُمْ لَهَا سَٰبِقُونَ',23,61,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا ۖ وَلَدَيْنَا كِتَٰبٌۭ يَنطِقُ بِٱلْحَقِّ ۚ وَهُمْ لَا يُظْلَمُونَ',23,62,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ قُلُوبُهُمْ فِى غَمْرَةٍۢ مِّنْ هَٰذَا وَلَهُمْ أَعْمَٰلٌۭ مِّن دُونِ ذَٰلِكَ هُمْ لَهَا عَٰمِلُونَ',23,63,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَآ أَخَذْنَا مُتْرَفِيهِم بِٱلْعَذَابِ إِذَا هُمْ يَجْـَٔرُونَ',23,64,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا تَجْـَٔرُوا۟ ٱلْيَوْمَ ۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ',23,65,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَدْ كَانَتْ ءَايَٰتِى تُتْلَىٰ عَلَيْكُمْ فَكُنتُمْ عَلَىٰٓ أَعْقَٰبِكُمْ تَنكِصُونَ',23,66,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مُسْتَكْبِرِينَ بِهِۦ سَٰمِرًۭا تَهْجُرُونَ',23,67,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَلَمْ يَدَّبَّرُوا۟ ٱلْقَوْلَ أَمْ جَآءَهُم مَّا لَمْ يَأْتِ ءَابَآءَهُمُ ٱلْأَوَّلِينَ',23,68,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ لَمْ يَعْرِفُوا۟ رَسُولَهُمْ فَهُمْ لَهُۥ مُنكِرُونَ',23,69,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ يَقُولُونَ بِهِۦ جِنَّةٌۢ ۚ بَلْ جَآءَهُم بِٱلْحَقِّ وَأَكْثَرُهُمْ لِلْحَقِّ كَٰرِهُونَ',23,70,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوِ ٱتَّبَعَ ٱلْحَقُّ أَهْوَآءَهُمْ لَفَسَدَتِ ٱلسَّمَٰوَٰتُ وَٱلْأَرْضُ وَمَن فِيهِنَّ ۚ بَلْ أَتَيْنَٰهُم بِذِكْرِهِمْ فَهُمْ عَن ذِكْرِهِم مُّعْرِضُونَ',23,71,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ تَسْـَٔلُهُمْ خَرْجًۭا فَخَرَاجُ رَبِّكَ خَيْرٌۭ ۖ وَهُوَ خَيْرُ ٱلرَّٰزِقِينَ',23,72,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّكَ لَتَدْعُوهُمْ إِلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ',23,73,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ عَنِ ٱلصِّرَٰطِ لَنَٰكِبُونَ',23,74,346,298,138,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَلَوْ رَحِمْنَٰهُمْ وَكَشَفْنَا مَا بِهِم مِّن ضُرٍّۢ لَّلَجُّوا۟ فِى طُغْيَٰنِهِمْ يَعْمَهُونَ',23,75,347,298,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَخَذْنَٰهُم بِٱلْعَذَابِ فَمَا ٱسْتَكَانُوا۟ لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ',23,76,347,298,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَا فَتَحْنَا عَلَيْهِم بَابًۭا ذَا عَذَابٍۢ شَدِيدٍ إِذَا هُمْ فِيهِ مُبْلِسُونَ',23,77,347,298,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِىٓ أَنشَأَ لَكُمُ ٱلسَّمْعَ وَٱلْأَبْصَٰرَ وَٱلْأَفْـِٔدَةَ ۚ قَلِيلًۭا مَّا تَشْكُرُونَ',23,78,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى ذَرَأَكُمْ فِى ٱلْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ',23,79,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى يُحْىِۦ وَيُمِيتُ وَلَهُ ٱخْتِلَٰفُ ٱلَّيْلِ وَٱلنَّهَارِ ۚ أَفَلَا تَعْقِلُونَ',23,80,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ قَالُوا۟ مِثْلَ مَا قَالَ ٱلْأَوَّلُونَ',23,81,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ أَءِذَا مِتْنَا وَكُنَّا تُرَابًۭا وَعِظَٰمًا أَءِنَّا لَمَبْعُوثُونَ',23,82,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَقَدْ وُعِدْنَا نَحْنُ وَءَابَآؤُنَا هَٰذَا مِن قَبْلُ إِنْ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلْأَوَّلِينَ',23,83,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُل لِّمَنِ ٱلْأَرْضُ وَمَن فِيهَآ إِن كُنتُمْ تَعْلَمُونَ',23,84,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَذَكَّرُونَ',23,85,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَن رَّبُّ ٱلسَّمَٰوَٰتِ ٱلسَّبْعِ وَرَبُّ ٱلْعَرْشِ ٱلْعَظِيمِ',23,86,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَتَّقُونَ',23,87,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَنۢ بِيَدِهِۦ مَلَكُوتُ كُلِّ شَىْءٍۢ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيْهِ إِن كُنتُمْ تَعْلَمُونَ',23,88,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('سَيَقُولُونَ لِلَّهِ ۚ قُلْ فَأَنَّىٰ تُسْحَرُونَ',23,89,347,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ أَتَيْنَٰهُم بِٱلْحَقِّ وَإِنَّهُمْ لَكَٰذِبُونَ',23,90,348,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا ٱتَّخَذَ ٱللَّهُ مِن وَلَدٍۢ وَمَا كَانَ مَعَهُۥ مِنْ إِلَٰهٍ ۚ إِذًۭا لَّذَهَبَ كُلُّ إِلَٰهٍۭ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلَىٰ بَعْضٍۢ ۚ سُبْحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ',23,91,348,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('عَٰلِمِ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ فَتَعَٰلَىٰ عَمَّا يُشْرِكُونَ',23,92,348,299,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُل رَّبِّ إِمَّا تُرِيَنِّى مَا يُوعَدُونَ',23,93,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رَبِّ فَلَا تَجْعَلْنِى فِى ٱلْقَوْمِ ٱلظَّٰلِمِينَ',23,94,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّا عَلَىٰٓ أَن نُّرِيَكَ مَا نَعِدُهُمْ لَقَٰدِرُونَ',23,95,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱدْفَعْ بِٱلَّتِى هِىَ أَحْسَنُ ٱلسَّيِّئَةَ ۚ نَحْنُ أَعْلَمُ بِمَا يَصِفُونَ',23,96,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقُل رَّبِّ أَعُوذُ بِكَ مِنْ هَمَزَٰتِ ٱلشَّيَٰطِينِ',23,97,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَعُوذُ بِكَ رَبِّ أَن يَحْضُرُونِ',23,98,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَا جَآءَ أَحَدَهُمُ ٱلْمَوْتُ قَالَ رَبِّ ٱرْجِعُونِ',23,99,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَعَلِّىٓ أَعْمَلُ صَٰلِحًۭا فِيمَا تَرَكْتُ ۚ كَلَّآ ۚ إِنَّهَا كَلِمَةٌ هُوَ قَآئِلُهَا ۖ وَمِن وَرَآئِهِم بَرْزَخٌ إِلَىٰ يَوْمِ يُبْعَثُونَ',23,100,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِذَا نُفِخَ فِى ٱلصُّورِ فَلَآ أَنسَابَ بَيْنَهُمْ يَوْمَئِذٍۢ وَلَا يَتَسَآءَلُونَ',23,101,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَمَن ثَقُلَتْ مَوَٰزِينُهُۥ فَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ',23,102,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَنْ خَفَّتْ مَوَٰزِينُهُۥ فَأُو۟لَٰٓئِكَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ فِى جَهَنَّمَ خَٰلِدُونَ',23,103,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَلْفَحُ وُجُوهَهُمُ ٱلنَّارُ وَهُمْ فِيهَا كَٰلِحُونَ',23,104,348,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَكُنْ ءَايَٰتِى تُتْلَىٰ عَلَيْكُمْ فَكُنتُم بِهَا تُكَذِّبُونَ',23,105,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًۭا ضَآلِّينَ',23,106,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رَبَّنَآ أَخْرِجْنَا مِنْهَا فَإِنْ عُدْنَا فَإِنَّا ظَٰلِمُونَ',23,107,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ ٱخْسَـُٔوا۟ فِيهَا وَلَا تُكَلِّمُونِ',23,108,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّهُۥ كَانَ فَرِيقٌۭ مِّنْ عِبَادِى يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱغْفِرْ لَنَا وَٱرْحَمْنَا وَأَنتَ خَيْرُ ٱلرَّٰحِمِينَ',23,109,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتَّىٰٓ أَنسَوْكُمْ ذِكْرِى وَكُنتُم مِّنْهُمْ تَضْحَكُونَ',23,110,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنِّى جَزَيْتُهُمُ ٱلْيَوْمَ بِمَا صَبَرُوٓا۟ أَنَّهُمْ هُمُ ٱلْفَآئِزُونَ',23,111,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَٰلَ كَمْ لَبِثْتُمْ فِى ٱلْأَرْضِ عَدَدَ سِنِينَ',23,112,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍۢ فَسْـَٔلِ ٱلْعَآدِّينَ',23,113,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَٰلَ إِن لَّبِثْتُمْ إِلَّا قَلِيلًۭا ۖ لَّوْ أَنَّكُمْ كُنتُمْ تَعْلَمُونَ',23,114,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَحَسِبْتُمْ أَنَّمَا خَلَقْنَٰكُمْ عَبَثًۭا وَأَنَّكُمْ إِلَيْنَا لَا تُرْجَعُونَ',23,115,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَتَعَٰلَى ٱللَّهُ ٱلْمَلِكُ ٱلْحَقُّ ۖ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلْعَرْشِ ٱلْكَرِيمِ',23,116,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يَدْعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَا بُرْهَٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦٓ ۚ إِنَّهُۥ لَا يُفْلِحُ ٱلْكَٰفِرُونَ',23,117,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقُل رَّبِّ ٱغْفِرْ وَٱرْحَمْ وَأَنتَ خَيْرُ ٱلرَّٰحِمِينَ',23,118,349,300,139,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ سُورَةٌ أَنزَلْنَٰهَا وَفَرَضْنَٰهَا وَأَنزَلْنَا فِيهَآ ءَايَٰتٍۭ بَيِّنَٰتٍۢ لَّعَلَّكُمْ تَذَكَّرُونَ',24,1,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلزَّانِيَةُ وَٱلزَّانِى فَٱجْلِدُوا۟ كُلَّ وَٰحِدٍۢ مِّنْهُمَا مِا۟ئَةَ جَلْدَةٍۢ ۖ وَلَا تَأْخُذْكُم بِهِمَا رَأْفَةٌۭ فِى دِينِ ٱللَّهِ إِن كُنتُمْ تُؤْمِنُونَ بِٱللَّهِ وَٱلْيَوْمِ ٱلْءَاخِرِ ۖ وَلْيَشْهَدْ عَذَابَهُمَا طَآئِفَةٌۭ مِّنَ ٱلْمُؤْمِنِينَ',24,2,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلزَّانِى لَا يَنكِحُ إِلَّا زَانِيَةً أَوْ مُشْرِكَةًۭ وَٱلزَّانِيَةُ لَا يَنكِحُهَآ إِلَّا زَانٍ أَوْ مُشْرِكٌۭ ۚ وَحُرِّمَ ذَٰلِكَ عَلَى ٱلْمُؤْمِنِينَ',24,3,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَرْمُونَ ٱلْمُحْصَنَٰتِ ثُمَّ لَمْ يَأْتُوا۟ بِأَرْبَعَةِ شُهَدَآءَ فَٱجْلِدُوهُمْ ثَمَٰنِينَ جَلْدَةًۭ وَلَا تَقْبَلُوا۟ لَهُمْ شَهَٰدَةً أَبَدًۭا ۚ وَأُو۟لَٰٓئِكَ هُمُ ٱلْفَٰسِقُونَ',24,4,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّا ٱلَّذِينَ تَابُوا۟ مِنۢ بَعْدِ ذَٰلِكَ وَأَصْلَحُوا۟ فَإِنَّ ٱللَّهَ غَفُورٌۭ رَّحِيمٌۭ',24,5,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَرْمُونَ أَزْوَٰجَهُمْ وَلَمْ يَكُن لَّهُمْ شُهَدَآءُ إِلَّآ أَنفُسُهُمْ فَشَهَٰدَةُ أَحَدِهِمْ أَرْبَعُ شَهَٰدَٰتٍۭ بِٱللَّهِ ۙ إِنَّهُۥ لَمِنَ ٱلصَّٰدِقِينَ',24,6,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلْخَٰمِسَةُ أَنَّ لَعْنَتَ ٱللَّهِ عَلَيْهِ إِن كَانَ مِنَ ٱلْكَٰذِبِينَ',24,7,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَدْرَؤُا۟ عَنْهَا ٱلْعَذَابَ أَن تَشْهَدَ أَرْبَعَ شَهَٰدَٰتٍۭ بِٱللَّهِ ۙ إِنَّهُۥ لَمِنَ ٱلْكَٰذِبِينَ',24,8,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلْخَٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَيْهَآ إِن كَانَ مِنَ ٱلصَّٰدِقِينَ',24,9,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَا فَضْلُ ٱللَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِيمٌ',24,10,350,301,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ جَآءُو بِٱلْإِفْكِ عُصْبَةٌۭ مِّنكُمْ ۚ لَا تَحْسَبُوهُ شَرًّۭا لَّكُم ۖ بَلْ هُوَ خَيْرٌۭ لَّكُمْ ۚ لِكُلِّ ٱمْرِئٍۢ مِّنْهُم مَّا ٱكْتَسَبَ مِنَ ٱلْإِثْمِ ۚ وَٱلَّذِى تَوَلَّىٰ كِبْرَهُۥ مِنْهُمْ لَهُۥ عَذَابٌ عَظِيمٌۭ',24,11,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّوْلَآ إِذْ سَمِعْتُمُوهُ ظَنَّ ٱلْمُؤْمِنُونَ وَٱلْمُؤْمِنَٰتُ بِأَنفُسِهِمْ خَيْرًۭا وَقَالُوا۟ هَٰذَآ إِفْكٌۭ مُّبِينٌۭ',24,12,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّوْلَا جَآءُو عَلَيْهِ بِأَرْبَعَةِ شُهَدَآءَ ۚ فَإِذْ لَمْ يَأْتُوا۟ بِٱلشُّهَدَآءِ فَأُو۟لَٰٓئِكَ عِندَ ٱللَّهِ هُمُ ٱلْكَٰذِبُونَ',24,13,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَا فَضْلُ ٱللَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ فِى ٱلدُّنْيَا وَٱلْءَاخِرَةِ لَمَسَّكُمْ فِى مَآ أَفَضْتُمْ فِيهِ عَذَابٌ عَظِيمٌ',24,14,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ تَلَقَّوْنَهُۥ بِأَلْسِنَتِكُمْ وَتَقُولُونَ بِأَفْوَاهِكُم مَّا لَيْسَ لَكُم بِهِۦ عِلْمٌۭ وَتَحْسَبُونَهُۥ هَيِّنًۭا وَهُوَ عِندَ ٱللَّهِ عَظِيمٌۭ',24,15,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَآ إِذْ سَمِعْتُمُوهُ قُلْتُم مَّا يَكُونُ لَنَآ أَن نَّتَكَلَّمَ بِهَٰذَا سُبْحَٰنَكَ هَٰذَا بُهْتَٰنٌ عَظِيمٌۭ',24,16,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَعِظُكُمُ ٱللَّهُ أَن تَعُودُوا۟ لِمِثْلِهِۦٓ أَبَدًا إِن كُنتُم مُّؤْمِنِينَ',24,17,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيُبَيِّنُ ٱللَّهُ لَكُمُ ٱلْءَايَٰتِ ۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ',24,18,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ يُحِبُّونَ أَن تَشِيعَ ٱلْفَٰحِشَةُ فِى ٱلَّذِينَ ءَامَنُوا۟ لَهُمْ عَذَابٌ أَلِيمٌۭ فِى ٱلدُّنْيَا وَٱلْءَاخِرَةِ ۚ وَٱللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ',24,19,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَا فَضْلُ ٱللَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفٌۭ رَّحِيمٌۭ',24,20,351,302,140,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَتَّبِعُوا۟ خُطُوَٰتِ ٱلشَّيْطَٰنِ ۚ وَمَن يَتَّبِعْ خُطُوَٰتِ ٱلشَّيْطَٰنِ فَإِنَّهُۥ يَأْمُرُ بِٱلْفَحْشَآءِ وَٱلْمُنكَرِ ۚ وَلَوْلَا فَضْلُ ٱللَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنْ أَحَدٍ أَبَدًۭا وَلَٰكِنَّ ٱللَّهَ يُزَكِّى مَن يَشَآءُ ۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌۭ',24,21,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا يَأْتَلِ أُو۟لُوا۟ ٱلْفَضْلِ مِنكُمْ وَٱلسَّعَةِ أَن يُؤْتُوٓا۟ أُو۟لِى ٱلْقُرْبَىٰ وَٱلْمَسَٰكِينَ وَٱلْمُهَٰجِرِينَ فِى سَبِيلِ ٱللَّهِ ۖ وَلْيَعْفُوا۟ وَلْيَصْفَحُوٓا۟ ۗ أَلَا تُحِبُّونَ أَن يَغْفِرَ ٱللَّهُ لَكُمْ ۗ وَٱللَّهُ غَفُورٌۭ رَّحِيمٌ',24,22,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ يَرْمُونَ ٱلْمُحْصَنَٰتِ ٱلْغَٰفِلَٰتِ ٱلْمُؤْمِنَٰتِ لُعِنُوا۟ فِى ٱلدُّنْيَا وَٱلْءَاخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌۭ',24,23,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُم بِمَا كَانُوا۟ يَعْمَلُونَ',24,24,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَئِذٍۢ يُوَفِّيهِمُ ٱللَّهُ دِينَهُمُ ٱلْحَقَّ وَيَعْلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلْحَقُّ ٱلْمُبِينُ',24,25,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلْخَبِيثَٰتُ لِلْخَبِيثِينَ وَٱلْخَبِيثُونَ لِلْخَبِيثَٰتِ ۖ وَٱلطَّيِّبَٰتُ لِلطَّيِّبِينَ وَٱلطَّيِّبُونَ لِلطَّيِّبَٰتِ ۚ أُو۟لَٰٓئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَ ۖ لَهُم مَّغْفِرَةٌۭ وَرِزْقٌۭ كَرِيمٌۭ',24,26,352,303,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَدْخُلُوا۟ بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّىٰ تَسْتَأْنِسُوا۟ وَتُسَلِّمُوا۟ عَلَىٰٓ أَهْلِهَا ۚ ذَٰلِكُمْ خَيْرٌۭ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُونَ',24,27,352,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِن لَّمْ تَجِدُوا۟ فِيهَآ أَحَدًۭا فَلَا تَدْخُلُوهَا حَتَّىٰ يُؤْذَنَ لَكُمْ ۖ وَإِن قِيلَ لَكُمُ ٱرْجِعُوا۟ فَٱرْجِعُوا۟ ۖ هُوَ أَزْكَىٰ لَكُمْ ۚ وَٱللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌۭ',24,28,353,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَدْخُلُوا۟ بُيُوتًا غَيْرَ مَسْكُونَةٍۢ فِيهَا مَتَٰعٌۭ لَّكُمْ ۚ وَٱللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ',24,29,353,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُل لِّلْمُؤْمِنِينَ يَغُضُّوا۟ مِنْ أَبْصَٰرِهِمْ وَيَحْفَظُوا۟ فُرُوجَهُمْ ۚ ذَٰلِكَ أَزْكَىٰ لَهُمْ ۗ إِنَّ ٱللَّهَ خَبِيرٌۢ بِمَا يَصْنَعُونَ',24,30,353,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقُل لِّلْمُؤْمِنَٰتِ يَغْضُضْنَ مِنْ أَبْصَٰرِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنْهَا ۖ وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّ ۖ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوْ ءَابَآئِهِنَّ أَوْ ءَابَآءِ بُعُولَتِهِنَّ أَوْ أَبْنَآئِهِنَّ أَوْ أَبْنَآءِ بُعُولَتِهِنَّ أَوْ إِخْوَٰنِهِنَّ أَوْ بَنِىٓ إِخْوَٰنِهِنَّ أَوْ بَنِىٓ أَخَوَٰتِهِنَّ أَوْ نِسَآئِهِنَّ أَوْ مَا مَلَكَتْ أَيْمَٰنُهُنَّ أَوِ ٱلتَّٰبِعِينَ غَيْرِ أُو۟لِى ٱلْإِرْبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفْلِ ٱلَّذِينَ لَمْ يَظْهَرُوا۟ عَلَىٰ عَوْرَٰتِ ٱلنِّسَآءِ ۖ وَلَا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِن زِينَتِهِنَّ ۚ وَتُوبُوٓا۟ إِلَى ٱللَّهِ جَمِيعًا أَيُّهَ ٱلْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ',24,31,353,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنكِحُوا۟ ٱلْأَيَٰمَىٰ مِنكُمْ وَٱلصَّٰلِحِينَ مِنْ عِبَادِكُمْ وَإِمَآئِكُمْ ۚ إِن يَكُونُوا۟ فُقَرَآءَ يُغْنِهِمُ ٱللَّهُ مِن فَضْلِهِۦ ۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٌۭ',24,32,354,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلْيَسْتَعْفِفِ ٱلَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغْنِيَهُمُ ٱللَّهُ مِن فَضْلِهِۦ ۗ وَٱلَّذِينَ يَبْتَغُونَ ٱلْكِتَٰبَ مِمَّا مَلَكَتْ أَيْمَٰنُكُمْ فَكَاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْرًۭا ۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِىٓ ءَاتَىٰكُمْ ۚ وَلَا تُكْرِهُوا۟ فَتَيَٰتِكُمْ عَلَى ٱلْبِغَآءِ إِنْ أَرَدْنَ تَحَصُّنًۭا لِّتَبْتَغُوا۟ عَرَضَ ٱلْحَيَوٰةِ ٱلدُّنْيَا ۚ وَمَن يُكْرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعْدِ إِكْرَٰهِهِنَّ غَفُورٌۭ رَّحِيمٌۭ',24,33,354,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَنزَلْنَآ إِلَيْكُمْ ءَايَٰتٍۢ مُّبَيِّنَٰتٍۢ وَمَثَلًۭا مِّنَ ٱلَّذِينَ خَلَوْا۟ مِن قَبْلِكُمْ وَمَوْعِظَةًۭ لِّلْمُتَّقِينَ',24,34,354,304,141,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ ٱللَّهُ نُورُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۚ مَثَلُ نُورِهِۦ كَمِشْكَوٰةٍۢ فِيهَا مِصْبَاحٌ ۖ ٱلْمِصْبَاحُ فِى زُجَاجَةٍ ۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوْكَبٌۭ دُرِّىٌّۭ يُوقَدُ مِن شَجَرَةٍۢ مُّبَٰرَكَةٍۢ زَيْتُونَةٍۢ لَّا شَرْقِيَّةٍۢ وَلَا غَرْبِيَّةٍۢ يَكَادُ زَيْتُهَا يُضِىٓءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۭ ۚ نُّورٌ عَلَىٰ نُورٍۢ ۗ يَهْدِى ٱللَّهُ لِنُورِهِۦ مَن يَشَآءُ ۚ وَيَضْرِبُ ٱللَّهُ ٱلْأَمْثَٰلَ لِلنَّاسِ ۗ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌۭ',24,35,354,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فِى بُيُوتٍ أَذِنَ ٱللَّهُ أَن تُرْفَعَ وَيُذْكَرَ فِيهَا ٱسْمُهُۥ يُسَبِّحُ لَهُۥ فِيهَا بِٱلْغُدُوِّ وَٱلْءَاصَالِ',24,36,354,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رِجَالٌۭ لَّا تُلْهِيهِمْ تِجَٰرَةٌۭ وَلَا بَيْعٌ عَن ذِكْرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِيتَآءِ ٱلزَّكَوٰةِ ۙ يَخَافُونَ يَوْمًۭا تَتَقَلَّبُ فِيهِ ٱلْقُلُوبُ وَٱلْأَبْصَٰرُ',24,37,355,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِيَجْزِيَهُمُ ٱللَّهُ أَحْسَنَ مَا عَمِلُوا۟ وَيَزِيدَهُم مِّن فَضْلِهِۦ ۗ وَٱللَّهُ يَرْزُقُ مَن يَشَآءُ بِغَيْرِ حِسَابٍۢ',24,38,355,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ كَفَرُوٓا۟ أَعْمَٰلُهُمْ كَسَرَابٍۭ بِقِيعَةٍۢ يَحْسَبُهُ ٱلظَّمْـَٔانُ مَآءً حَتَّىٰٓ إِذَا جَآءَهُۥ لَمْ يَجِدْهُ شَيْـًۭٔا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥ ۗ وَٱللَّهُ سَرِيعُ ٱلْحِسَابِ',24,39,355,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ كَظُلُمَٰتٍۢ فِى بَحْرٍۢ لُّجِّىٍّۢ يَغْشَىٰهُ مَوْجٌۭ مِّن فَوْقِهِۦ مَوْجٌۭ مِّن فَوْقِهِۦ سَحَابٌۭ ۚ ظُلُمَٰتٌۢ بَعْضُهَا فَوْقَ بَعْضٍ إِذَآ أَخْرَجَ يَدَهُۥ لَمْ يَكَدْ يَرَىٰهَا ۗ وَمَن لَّمْ يَجْعَلِ ٱللَّهُ لَهُۥ نُورًۭا فَمَا لَهُۥ مِن نُّورٍ',24,40,355,305,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ يُسَبِّحُ لَهُۥ مَن فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱلطَّيْرُ صَٰٓفَّٰتٍۢ ۖ كُلٌّۭ قَدْ عَلِمَ صَلَاتَهُۥ وَتَسْبِيحَهُۥ ۗ وَٱللَّهُ عَلِيمٌۢ بِمَا يَفْعَلُونَ',24,41,355,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلِلَّهِ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۖ وَإِلَى ٱللَّهِ ٱلْمَصِيرُ',24,42,355,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ يُزْجِى سَحَابًۭا ثُمَّ يُؤَلِّفُ بَيْنَهُۥ ثُمَّ يَجْعَلُهُۥ رُكَامًۭا فَتَرَى ٱلْوَدْقَ يَخْرُجُ مِنْ خِلَٰلِهِۦ وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مِن جِبَالٍۢ فِيهَا مِنۢ بَرَدٍۢ فَيُصِيبُ بِهِۦ مَن يَشَآءُ وَيَصْرِفُهُۥ عَن مَّن يَشَآءُ ۖ يَكَادُ سَنَا بَرْقِهِۦ يَذْهَبُ بِٱلْأَبْصَٰرِ',24,43,355,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُقَلِّبُ ٱللَّهُ ٱلَّيْلَ وَٱلنَّهَارَ ۚ إِنَّ فِى ذَٰلِكَ لَعِبْرَةًۭ لِّأُو۟لِى ٱلْأَبْصَٰرِ',24,44,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱللَّهُ خَلَقَ كُلَّ دَآبَّةٍۢ مِّن مَّآءٍۢ ۖ فَمِنْهُم مَّن يَمْشِى عَلَىٰ بَطْنِهِۦ وَمِنْهُم مَّن يَمْشِى عَلَىٰ رِجْلَيْنِ وَمِنْهُم مَّن يَمْشِى عَلَىٰٓ أَرْبَعٍۢ ۚ يَخْلُقُ ٱللَّهُ مَا يَشَآءُ ۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ',24,45,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّقَدْ أَنزَلْنَآ ءَايَٰتٍۢ مُّبَيِّنَٰتٍۢ ۚ وَٱللَّهُ يَهْدِى مَن يَشَآءُ إِلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ',24,46,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعْنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٌۭ مِّنْهُم مِّنۢ بَعْدِ ذَٰلِكَ ۚ وَمَآ أُو۟لَٰٓئِكَ بِٱلْمُؤْمِنِينَ',24,47,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا دُعُوٓا۟ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحْكُمَ بَيْنَهُمْ إِذَا فَرِيقٌۭ مِّنْهُم مُّعْرِضُونَ',24,48,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِن يَكُن لَّهُمُ ٱلْحَقُّ يَأْتُوٓا۟ إِلَيْهِ مُذْعِنِينَ',24,49,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفِى قُلُوبِهِم مَّرَضٌ أَمِ ٱرْتَابُوٓا۟ أَمْ يَخَافُونَ أَن يَحِيفَ ٱللَّهُ عَلَيْهِمْ وَرَسُولُهُۥ ۚ بَلْ أُو۟لَٰٓئِكَ هُمُ ٱلظَّٰلِمُونَ',24,50,356,306,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّمَا كَانَ قَوْلَ ٱلْمُؤْمِنِينَ إِذَا دُعُوٓا۟ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحْكُمَ بَيْنَهُمْ أَن يَقُولُوا۟ سَمِعْنَا وَأَطَعْنَا ۚ وَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ',24,51,356,307,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَيَخْشَ ٱللَّهَ وَيَتَّقْهِ فَأُو۟لَٰٓئِكَ هُمُ ٱلْفَآئِزُونَ',24,52,356,307,142,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَأَقْسَمُوا۟ بِٱللَّهِ جَهْدَ أَيْمَٰنِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ ۖ قُل لَّا تُقْسِمُوا۟ ۖ طَاعَةٌۭ مَّعْرُوفَةٌ ۚ إِنَّ ٱللَّهَ خَبِيرٌۢ بِمَا تَعْمَلُونَ',24,53,356,307,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ أَطِيعُوا۟ ٱللَّهَ وَأَطِيعُوا۟ ٱلرَّسُولَ ۖ فَإِن تَوَلَّوْا۟ فَإِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُم مَّا حُمِّلْتُمْ ۖ وَإِن تُطِيعُوهُ تَهْتَدُوا۟ ۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلْبَلَٰغُ ٱلْمُبِينُ',24,54,357,307,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ مِنكُمْ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَيَسْتَخْلِفَنَّهُمْ فِى ٱلْأَرْضِ كَمَا ٱسْتَخْلَفَ ٱلَّذِينَ مِن قَبْلِهِمْ وَلَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ ٱلَّذِى ٱرْتَضَىٰ لَهُمْ وَلَيُبَدِّلَنَّهُم مِّنۢ بَعْدِ خَوْفِهِمْ أَمْنًۭا ۚ يَعْبُدُونَنِى لَا يُشْرِكُونَ بِى شَيْـًۭٔا ۚ وَمَن كَفَرَ بَعْدَ ذَٰلِكَ فَأُو۟لَٰٓئِكَ هُمُ ٱلْفَٰسِقُونَ',24,55,357,307,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَطِيعُوا۟ ٱلرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ',24,56,357,307,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا تَحْسَبَنَّ ٱلَّذِينَ كَفَرُوا۟ مُعْجِزِينَ فِى ٱلْأَرْضِ ۚ وَمَأْوَىٰهُمُ ٱلنَّارُ ۖ وَلَبِئْسَ ٱلْمَصِيرُ',24,57,357,307,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لِيَسْتَـْٔذِنكُمُ ٱلَّذِينَ مَلَكَتْ أَيْمَٰنُكُمْ وَٱلَّذِينَ لَمْ يَبْلُغُوا۟ ٱلْحُلُمَ مِنكُمْ ثَلَٰثَ مَرَّٰتٍۢ ۚ مِّن قَبْلِ صَلَوٰةِ ٱلْفَجْرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ ٱلظَّهِيرَةِ وَمِنۢ بَعْدِ صَلَوٰةِ ٱلْعِشَآءِ ۚ ثَلَٰثُ عَوْرَٰتٍۢ لَّكُمْ ۚ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌۢ بَعْدَهُنَّ ۚ طَوَّٰفُونَ عَلَيْكُم بَعْضُكُمْ عَلَىٰ بَعْضٍۢ ۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلْءَايَٰتِ ۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌۭ',24,58,357,308,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا بَلَغَ ٱلْأَطْفَٰلُ مِنكُمُ ٱلْحُلُمَ فَلْيَسْتَـْٔذِنُوا۟ كَمَا ٱسْتَـْٔذَنَ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمْ ءَايَٰتِهِۦ ۗ وَٱللَّهُ عَلِيمٌ حَكِيمٌۭ',24,59,358,308,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلْقَوَٰعِدُ مِنَ ٱلنِّسَآءِ ٱلَّٰتِى لَا يَرْجُونَ نِكَاحًۭا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ أَن يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَٰتٍۭ بِزِينَةٍۢ ۖ وَأَن يَسْتَعْفِفْنَ خَيْرٌۭ لَّهُنَّ ۗ وَٱللَّهُ سَمِيعٌ عَلِيمٌۭ',24,60,358,308,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّيْسَ عَلَى ٱلْأَعْمَىٰ حَرَجٌۭ وَلَا عَلَى ٱلْأَعْرَجِ حَرَجٌۭ وَلَا عَلَى ٱلْمَرِيضِ حَرَجٌۭ وَلَا عَلَىٰٓ أَنفُسِكُمْ أَن تَأْكُلُوا۟ مِنۢ بُيُوتِكُمْ أَوْ بُيُوتِ ءَابَآئِكُمْ أَوْ بُيُوتِ أُمَّهَٰتِكُمْ أَوْ بُيُوتِ إِخْوَٰنِكُمْ أَوْ بُيُوتِ أَخَوَٰتِكُمْ أَوْ بُيُوتِ أَعْمَٰمِكُمْ أَوْ بُيُوتِ عَمَّٰتِكُمْ أَوْ بُيُوتِ أَخْوَٰلِكُمْ أَوْ بُيُوتِ خَٰلَٰتِكُمْ أَوْ مَا مَلَكْتُم مَّفَاتِحَهُۥٓ أَوْ صَدِيقِكُمْ ۚ لَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَأْكُلُوا۟ جَمِيعًا أَوْ أَشْتَاتًۭا ۚ فَإِذَا دَخَلْتُم بُيُوتًۭا فَسَلِّمُوا۟ عَلَىٰٓ أَنفُسِكُمْ تَحِيَّةًۭ مِّنْ عِندِ ٱللَّهِ مُبَٰرَكَةًۭ طَيِّبَةًۭ ۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلْءَايَٰتِ لَعَلَّكُمْ تَعْقِلُونَ',24,61,358,308,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّمَا ٱلْمُؤْمِنُونَ ٱلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُوا۟ مَعَهُۥ عَلَىٰٓ أَمْرٍۢ جَامِعٍۢ لَّمْ يَذْهَبُوا۟ حَتَّىٰ يَسْتَـْٔذِنُوهُ ۚ إِنَّ ٱلَّذِينَ يَسْتَـْٔذِنُونَكَ أُو۟لَٰٓئِكَ ٱلَّذِينَ يُؤْمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ ۚ فَإِذَا ٱسْتَـْٔذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَن لِّمَن شِئْتَ مِنْهُمْ وَٱسْتَغْفِرْ لَهُمُ ٱللَّهَ ۚ إِنَّ ٱللَّهَ غَفُورٌۭ رَّحِيمٌۭ',24,62,359,309,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّا تَجْعَلُوا۟ دُعَآءَ ٱلرَّسُولِ بَيْنَكُمْ كَدُعَآءِ بَعْضِكُم بَعْضًۭا ۚ قَدْ يَعْلَمُ ٱللَّهُ ٱلَّذِينَ يَتَسَلَّلُونَ مِنكُمْ لِوَاذًۭا ۚ فَلْيَحْذَرِ ٱلَّذِينَ يُخَالِفُونَ عَنْ أَمْرِهِۦٓ أَن تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذَابٌ أَلِيمٌ',24,63,359,309,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَآ إِنَّ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۖ قَدْ يَعْلَمُ مَآ أَنتُمْ عَلَيْهِ وَيَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُم بِمَا عَمِلُوا۟ ۗ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌۢ',24,64,359,309,143,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ تَبَارَكَ ٱلَّذِى نَزَّلَ ٱلْفُرْقَانَ عَلَىٰ عَبْدِهِۦ لِيَكُونَ لِلْعَٰلَمِينَ نَذِيرًا',25,1,359,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِى لَهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَلَمْ يَتَّخِذْ وَلَدًۭا وَلَمْ يَكُن لَّهُۥ شَرِيكٌۭ فِى ٱلْمُلْكِ وَخَلَقَ كُلَّ شَىْءٍۢ فَقَدَّرَهُۥ تَقْدِيرًۭا',25,2,359,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱتَّخَذُوا۟ مِن دُونِهِۦٓ ءَالِهَةًۭ لَّا يَخْلُقُونَ شَيْـًۭٔا وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِأَنفُسِهِمْ ضَرًّۭا وَلَا نَفْعًۭا وَلَا يَمْلِكُونَ مَوْتًۭا وَلَا حَيَوٰةًۭ وَلَا نُشُورًۭا',25,3,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِينَ كَفَرُوٓا۟ إِنْ هَٰذَآ إِلَّآ إِفْكٌ ٱفْتَرَىٰهُ وَأَعَانَهُۥ عَلَيْهِ قَوْمٌ ءَاخَرُونَ ۖ فَقَدْ جَآءُو ظُلْمًۭا وَزُورًۭا',25,4,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوٓا۟ أَسَٰطِيرُ ٱلْأَوَّلِينَ ٱكْتَتَبَهَا فَهِىَ تُمْلَىٰ عَلَيْهِ بُكْرَةًۭ وَأَصِيلًۭا',25,5,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ أَنزَلَهُ ٱلَّذِى يَعْلَمُ ٱلسِّرَّ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۚ إِنَّهُۥ كَانَ غَفُورًۭا رَّحِيمًۭا',25,6,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ مَالِ هَٰذَا ٱلرَّسُولِ يَأْكُلُ ٱلطَّعَامَ وَيَمْشِى فِى ٱلْأَسْوَاقِ ۙ لَوْلَآ أُنزِلَ إِلَيْهِ مَلَكٌۭ فَيَكُونَ مَعَهُۥ نَذِيرًا',25,7,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ يُلْقَىٰٓ إِلَيْهِ كَنزٌ أَوْ تَكُونُ لَهُۥ جَنَّةٌۭ يَأْكُلُ مِنْهَا ۚ وَقَالَ ٱلظَّٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلًۭا مَّسْحُورًا',25,8,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱنظُرْ كَيْفَ ضَرَبُوا۟ لَكَ ٱلْأَمْثَٰلَ فَضَلُّوا۟ فَلَا يَسْتَطِيعُونَ سَبِيلًۭا',25,9,360,310,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَبَارَكَ ٱلَّذِىٓ إِن شَآءَ جَعَلَ لَكَ خَيْرًۭا مِّن ذَٰلِكَ جَنَّٰتٍۢ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ وَيَجْعَل لَّكَ قُصُورًۢا',25,10,360,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ كَذَّبُوا۟ بِٱلسَّاعَةِ ۖ وَأَعْتَدْنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِيرًا',25,11,360,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذَا رَأَتْهُم مِّن مَّكَانٍۭ بَعِيدٍۢ سَمِعُوا۟ لَهَا تَغَيُّظًۭا وَزَفِيرًۭا',25,12,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَآ أُلْقُوا۟ مِنْهَا مَكَانًۭا ضَيِّقًۭا مُّقَرَّنِينَ دَعَوْا۟ هُنَالِكَ ثُبُورًۭا',25,13,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّا تَدْعُوا۟ ٱلْيَوْمَ ثُبُورًۭا وَٰحِدًۭا وَٱدْعُوا۟ ثُبُورًۭا كَثِيرًۭا',25,14,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ أَذَٰلِكَ خَيْرٌ أَمْ جَنَّةُ ٱلْخُلْدِ ٱلَّتِى وُعِدَ ٱلْمُتَّقُونَ ۚ كَانَتْ لَهُمْ جَزَآءًۭ وَمَصِيرًۭا',25,15,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّهُمْ فِيهَا مَا يَشَآءُونَ خَٰلِدِينَ ۚ كَانَ عَلَىٰ رَبِّكَ وَعْدًۭا مَّسْـُٔولًۭا',25,16,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِن دُونِ ٱللَّهِ فَيَقُولُ ءَأَنتُمْ أَضْلَلْتُمْ عِبَادِى هَٰٓؤُلَآءِ أَمْ هُمْ ضَلُّوا۟ ٱلسَّبِيلَ',25,17,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ سُبْحَٰنَكَ مَا كَانَ يَنۢبَغِى لَنَآ أَن نَّتَّخِذَ مِن دُونِكَ مِنْ أَوْلِيَآءَ وَلَٰكِن مَّتَّعْتَهُمْ وَءَابَآءَهُمْ حَتَّىٰ نَسُوا۟ ٱلذِّكْرَ وَكَانُوا۟ قَوْمًۢا بُورًۭا',25,18,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَدْ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسْتَطِيعُونَ صَرْفًۭا وَلَا نَصْرًۭا ۚ وَمَن يَظْلِم مِّنكُمْ نُذِقْهُ عَذَابًۭا كَبِيرًۭا',25,19,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَا قَبْلَكَ مِنَ ٱلْمُرْسَلِينَ إِلَّآ إِنَّهُمْ لَيَأْكُلُونَ ٱلطَّعَامَ وَيَمْشُونَ فِى ٱلْأَسْوَاقِ ۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍۢ فِتْنَةً أَتَصْبِرُونَ ۗ وَكَانَ رَبُّكَ بَصِيرًۭا',25,20,361,311,144,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَقَالَ ٱلَّذِينَ لَا يَرْجُونَ لِقَآءَنَا لَوْلَآ أُنزِلَ عَلَيْنَا ٱلْمَلَٰٓئِكَةُ أَوْ نَرَىٰ رَبَّنَا ۗ لَقَدِ ٱسْتَكْبَرُوا۟ فِىٓ أَنفُسِهِمْ وَعَتَوْ عُتُوًّۭا كَبِيرًۭا',25,21,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ يَرَوْنَ ٱلْمَلَٰٓئِكَةَ لَا بُشْرَىٰ يَوْمَئِذٍۢ لِّلْمُجْرِمِينَ وَيَقُولُونَ حِجْرًۭا مَّحْجُورًۭا',25,22,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَدِمْنَآ إِلَىٰ مَا عَمِلُوا۟ مِنْ عَمَلٍۢ فَجَعَلْنَٰهُ هَبَآءًۭ مَّنثُورًا',25,23,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَصْحَٰبُ ٱلْجَنَّةِ يَوْمَئِذٍ خَيْرٌۭ مُّسْتَقَرًّۭا وَأَحْسَنُ مَقِيلًۭا',25,24,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَوْمَ تَشَقَّقُ ٱلسَّمَآءُ بِٱلْغَمَٰمِ وَنُزِّلَ ٱلْمَلَٰٓئِكَةُ تَنزِيلًا',25,25,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلْمُلْكُ يَوْمَئِذٍ ٱلْحَقُّ لِلرَّحْمَٰنِ ۚ وَكَانَ يَوْمًا عَلَى ٱلْكَٰفِرِينَ عَسِيرًۭا',25,26,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَوْمَ يَعَضُّ ٱلظَّالِمُ عَلَىٰ يَدَيْهِ يَقُولُ يَٰلَيْتَنِى ٱتَّخَذْتُ مَعَ ٱلرَّسُولِ سَبِيلًۭا',25,27,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰوَيْلَتَىٰ لَيْتَنِى لَمْ أَتَّخِذْ فُلَانًا خَلِيلًۭا',25,28,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّقَدْ أَضَلَّنِى عَنِ ٱلذِّكْرِ بَعْدَ إِذْ جَآءَنِى ۗ وَكَانَ ٱلشَّيْطَٰنُ لِلْإِنسَٰنِ خَذُولًۭا',25,29,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلرَّسُولُ يَٰرَبِّ إِنَّ قَوْمِى ٱتَّخَذُوا۟ هَٰذَا ٱلْقُرْءَانَ مَهْجُورًۭا',25,30,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِىٍّ عَدُوًّۭا مِّنَ ٱلْمُجْرِمِينَ ۗ وَكَفَىٰ بِرَبِّكَ هَادِيًۭا وَنَصِيرًۭا',25,31,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَوْلَا نُزِّلَ عَلَيْهِ ٱلْقُرْءَانُ جُمْلَةًۭ وَٰحِدَةًۭ ۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَ ۖ وَرَتَّلْنَٰهُ تَرْتِيلًۭا',25,32,362,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا يَأْتُونَكَ بِمَثَلٍ إِلَّا جِئْنَٰكَ بِٱلْحَقِّ وَأَحْسَنَ تَفْسِيرًا',25,33,363,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يُحْشَرُونَ عَلَىٰ وُجُوهِهِمْ إِلَىٰ جَهَنَّمَ أُو۟لَٰٓئِكَ شَرٌّۭ مَّكَانًۭا وَأَضَلُّ سَبِيلًۭا',25,34,363,312,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ وَجَعَلْنَا مَعَهُۥٓ أَخَاهُ هَٰرُونَ وَزِيرًۭا',25,35,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقُلْنَا ٱذْهَبَآ إِلَى ٱلْقَوْمِ ٱلَّذِينَ كَذَّبُوا۟ بِـَٔايَٰتِنَا فَدَمَّرْنَٰهُمْ تَدْمِيرًۭا',25,36,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَوْمَ نُوحٍۢ لَّمَّا كَذَّبُوا۟ ٱلرُّسُلَ أَغْرَقْنَٰهُمْ وَجَعَلْنَٰهُمْ لِلنَّاسِ ءَايَةًۭ ۖ وَأَعْتَدْنَا لِلظَّٰلِمِينَ عَذَابًا أَلِيمًۭا',25,37,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَعَادًۭا وَثَمُودَا۟ وَأَصْحَٰبَ ٱلرَّسِّ وَقُرُونًۢا بَيْنَ ذَٰلِكَ كَثِيرًۭا',25,38,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكُلًّۭا ضَرَبْنَا لَهُ ٱلْأَمْثَٰلَ ۖ وَكُلًّۭا تَبَّرْنَا تَتْبِيرًۭا',25,39,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَتَوْا۟ عَلَى ٱلْقَرْيَةِ ٱلَّتِىٓ أُمْطِرَتْ مَطَرَ ٱلسَّوْءِ ۚ أَفَلَمْ يَكُونُوا۟ يَرَوْنَهَا ۚ بَلْ كَانُوا۟ لَا يَرْجُونَ نُشُورًۭا',25,40,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا رَأَوْكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِى بَعَثَ ٱللَّهُ رَسُولًا',25,41,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِن كَادَ لَيُضِلُّنَا عَنْ ءَالِهَتِنَا لَوْلَآ أَن صَبَرْنَا عَلَيْهَا ۚ وَسَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ ٱلْعَذَابَ مَنْ أَضَلُّ سَبِيلًا',25,42,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَرَءَيْتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَيْهِ وَكِيلًا',25,43,363,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ ۚ إِنْ هُمْ إِلَّا كَٱلْأَنْعَٰمِ ۖ بَلْ هُمْ أَضَلُّ سَبِيلًا',25,44,364,313,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ إِلَىٰ رَبِّكَ كَيْفَ مَدَّ ٱلظِّلَّ وَلَوْ شَآءَ لَجَعَلَهُۥ سَاكِنًۭا ثُمَّ جَعَلْنَا ٱلشَّمْسَ عَلَيْهِ دَلِيلًۭا',25,45,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ قَبَضْنَٰهُ إِلَيْنَا قَبْضًۭا يَسِيرًۭا',25,46,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى جَعَلَ لَكُمُ ٱلَّيْلَ لِبَاسًۭا وَٱلنَّوْمَ سُبَاتًۭا وَجَعَلَ ٱلنَّهَارَ نُشُورًۭا',25,47,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِىٓ أَرْسَلَ ٱلرِّيَٰحَ بُشْرًۢا بَيْنَ يَدَىْ رَحْمَتِهِۦ ۚ وَأَنزَلْنَا مِنَ ٱلسَّمَآءِ مَآءًۭ طَهُورًۭا',25,48,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِّنُحْۦِىَ بِهِۦ بَلْدَةًۭ مَّيْتًۭا وَنُسْقِيَهُۥ مِمَّا خَلَقْنَآ أَنْعَٰمًۭا وَأَنَاسِىَّ كَثِيرًۭا',25,49,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ صَرَّفْنَٰهُ بَيْنَهُمْ لِيَذَّكَّرُوا۟ فَأَبَىٰٓ أَكْثَرُ ٱلنَّاسِ إِلَّا كُفُورًۭا',25,50,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ شِئْنَا لَبَعَثْنَا فِى كُلِّ قَرْيَةٍۢ نَّذِيرًۭا',25,51,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَا تُطِعِ ٱلْكَٰفِرِينَ وَجَٰهِدْهُم بِهِۦ جِهَادًۭا كَبِيرًۭا',25,52,364,314,145,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَهُوَ ٱلَّذِى مَرَجَ ٱلْبَحْرَيْنِ هَٰذَا عَذْبٌۭ فُرَاتٌۭ وَهَٰذَا مِلْحٌ أُجَاجٌۭ وَجَعَلَ بَيْنَهُمَا بَرْزَخًۭا وَحِجْرًۭا مَّحْجُورًۭا',25,53,364,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى خَلَقَ مِنَ ٱلْمَآءِ بَشَرًۭا فَجَعَلَهُۥ نَسَبًۭا وَصِهْرًۭا ۗ وَكَانَ رَبُّكَ قَدِيرًۭا',25,54,364,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَعْبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُهُمْ وَلَا يَضُرُّهُمْ ۗ وَكَانَ ٱلْكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِيرًۭا',25,55,364,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَرْسَلْنَٰكَ إِلَّا مُبَشِّرًۭا وَنَذِيرًۭا',25,56,365,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلَّا مَن شَآءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِيلًۭا',25,57,365,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتَوَكَّلْ عَلَى ٱلْحَىِّ ٱلَّذِى لَا يَمُوتُ وَسَبِّحْ بِحَمْدِهِۦ ۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِيرًا',25,58,365,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِى خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا فِى سِتَّةِ أَيَّامٍۢ ثُمَّ ٱسْتَوَىٰ عَلَى ٱلْعَرْشِ ۚ ٱلرَّحْمَٰنُ فَسْـَٔلْ بِهِۦ خَبِيرًۭا',25,59,365,314,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا قِيلَ لَهُمُ ٱسْجُدُوا۟ لِلرَّحْمَٰنِ قَالُوا۟ وَمَا ٱلرَّحْمَٰنُ أَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُورًۭا ۩',25,60,365,314,146,true);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَبَارَكَ ٱلَّذِى جَعَلَ فِى ٱلسَّمَآءِ بُرُوجًۭا وَجَعَلَ فِيهَا سِرَٰجًۭا وَقَمَرًۭا مُّنِيرًۭا',25,61,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى جَعَلَ ٱلَّيْلَ وَٱلنَّهَارَ خِلْفَةًۭ لِّمَنْ أَرَادَ أَن يَذَّكَّرَ أَوْ أَرَادَ شُكُورًۭا',25,62,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَعِبَادُ ٱلرَّحْمَٰنِ ٱلَّذِينَ يَمْشُونَ عَلَى ٱلْأَرْضِ هَوْنًۭا وَإِذَا خَاطَبَهُمُ ٱلْجَٰهِلُونَ قَالُوا۟ سَلَٰمًۭا',25,63,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّدًۭا وَقِيَٰمًۭا',25,64,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَقُولُونَ رَبَّنَا ٱصْرِفْ عَنَّا عَذَابَ جَهَنَّمَ ۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا',25,65,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّهَا سَآءَتْ مُسْتَقَرًّۭا وَمُقَامًۭا',25,66,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ إِذَآ أَنفَقُوا۟ لَمْ يُسْرِفُوا۟ وَلَمْ يَقْتُرُوا۟ وَكَانَ بَيْنَ ذَٰلِكَ قَوَامًۭا',25,67,365,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ لَا يَدْعُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ وَلَا يَقْتُلُونَ ٱلنَّفْسَ ٱلَّتِى حَرَّمَ ٱللَّهُ إِلَّا بِٱلْحَقِّ وَلَا يَزْنُونَ ۚ وَمَن يَفْعَلْ ذَٰلِكَ يَلْقَ أَثَامًۭا',25,68,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُضَٰعَفْ لَهُ ٱلْعَذَابُ يَوْمَ ٱلْقِيَٰمَةِ وَيَخْلُدْ فِيهِۦ مُهَانًا',25,69,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلًۭا صَٰلِحًۭا فَأُو۟لَٰٓئِكَ يُبَدِّلُ ٱللَّهُ سَيِّـَٔاتِهِمْ حَسَنَٰتٍۢ ۗ وَكَانَ ٱللَّهُ غَفُورًۭا رَّحِيمًۭا',25,70,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن تَابَ وَعَمِلَ صَٰلِحًۭا فَإِنَّهُۥ يَتُوبُ إِلَى ٱللَّهِ مَتَابًۭا',25,71,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ لَا يَشْهَدُونَ ٱلزُّورَ وَإِذَا مَرُّوا۟ بِٱللَّغْوِ مَرُّوا۟ كِرَامًۭا',25,72,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ إِذَا ذُكِّرُوا۟ بِـَٔايَٰتِ رَبِّهِمْ لَمْ يَخِرُّوا۟ عَلَيْهَا صُمًّۭا وَعُمْيَانًۭا',25,73,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَقُولُونَ رَبَّنَا هَبْ لَنَا مِنْ أَزْوَٰجِنَا وَذُرِّيَّٰتِنَا قُرَّةَ أَعْيُنٍۢ وَٱجْعَلْنَا لِلْمُتَّقِينَ إِمَامًا',25,74,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أُو۟لَٰٓئِكَ يُجْزَوْنَ ٱلْغُرْفَةَ بِمَا صَبَرُوا۟ وَيُلَقَّوْنَ فِيهَا تَحِيَّةًۭ وَسَلَٰمًا',25,75,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('خَٰلِدِينَ فِيهَا ۚ حَسُنَتْ مُسْتَقَرًّۭا وَمُقَامًۭا',25,76,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَا يَعْبَؤُا۟ بِكُمْ رَبِّى لَوْلَا دُعَآؤُكُمْ ۖ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَامًۢا',25,77,366,315,146,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ طسٓمٓ',26,1,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تِلْكَ ءَايَٰتُ ٱلْكِتَٰبِ ٱلْمُبِينِ',26,2,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَعَلَّكَ بَٰخِعٌۭ نَّفْسَكَ أَلَّا يَكُونُوا۟ مُؤْمِنِينَ',26,3,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِن نَّشَأْ نُنَزِّلْ عَلَيْهِم مِّنَ ٱلسَّمَآءِ ءَايَةًۭ فَظَلَّتْ أَعْنَٰقُهُمْ لَهَا خَٰضِعِينَ',26,4,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا يَأْتِيهِم مِّن ذِكْرٍۢ مِّنَ ٱلرَّحْمَٰنِ مُحْدَثٍ إِلَّا كَانُوا۟ عَنْهُ مُعْرِضِينَ',26,5,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَدْ كَذَّبُوا۟ فَسَيَأْتِيهِمْ أَنۢبَٰٓؤُا۟ مَا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ',26,6,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوَلَمْ يَرَوْا۟ إِلَى ٱلْأَرْضِ كَمْ أَنۢبَتْنَا فِيهَا مِن كُلِّ زَوْجٍۢ كَرِيمٍ',26,7,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ فِى ذَٰلِكَ لَءَايَةًۭ ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ',26,8,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ',26,9,367,316,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذْ نَادَىٰ رَبُّكَ مُوسَىٰٓ أَنِ ٱئْتِ ٱلْقَوْمَ ٱلظَّٰلِمِينَ',26,10,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَوْمَ فِرْعَوْنَ ۚ أَلَا يَتَّقُونَ',26,11,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ إِنِّىٓ أَخَافُ أَن يُكَذِّبُونِ',26,12,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَضِيقُ صَدْرِى وَلَا يَنطَلِقُ لِسَانِى فَأَرْسِلْ إِلَىٰ هَٰرُونَ',26,13,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَهُمْ عَلَىَّ ذَنۢبٌۭ فَأَخَافُ أَن يَقْتُلُونِ',26,14,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ كَلَّا ۖ فَٱذْهَبَا بِـَٔايَٰتِنَآ ۖ إِنَّا مَعَكُم مُّسْتَمِعُونَ',26,15,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأْتِيَا فِرْعَوْنَ فَقُولَآ إِنَّا رَسُولُ رَبِّ ٱلْعَٰلَمِينَ',26,16,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَنْ أَرْسِلْ مَعَنَا بَنِىٓ إِسْرَٰٓءِيلَ',26,17,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ أَلَمْ نُرَبِّكَ فِينَا وَلِيدًۭا وَلَبِثْتَ فِينَا مِنْ عُمُرِكَ سِنِينَ',26,18,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَفَعَلْتَ فَعْلَتَكَ ٱلَّتِى فَعَلْتَ وَأَنتَ مِنَ ٱلْكَٰفِرِينَ',26,19,367,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَعَلْتُهَآ إِذًۭا وَأَنَا۠ مِنَ ٱلضَّآلِّينَ',26,20,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَفَرَرْتُ مِنكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِى رَبِّى حُكْمًۭا وَجَعَلَنِى مِنَ ٱلْمُرْسَلِينَ',26,21,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتِلْكَ نِعْمَةٌۭ تَمُنُّهَا عَلَىَّ أَنْ عَبَّدتَّ بَنِىٓ إِسْرَٰٓءِيلَ',26,22,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فِرْعَوْنُ وَمَا رَبُّ ٱلْعَٰلَمِينَ',26,23,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبُّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَآ ۖ إِن كُنتُم مُّوقِنِينَ',26,24,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لِمَنْ حَوْلَهُۥٓ أَلَا تَسْتَمِعُونَ',26,25,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبُّكُمْ وَرَبُّ ءَابَآئِكُمُ ٱلْأَوَّلِينَ',26,26,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ إِنَّ رَسُولَكُمُ ٱلَّذِىٓ أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌۭ',26,27,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ وَمَا بَيْنَهُمَآ ۖ إِن كُنتُمْ تَعْقِلُونَ',26,28,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لَئِنِ ٱتَّخَذْتَ إِلَٰهًا غَيْرِى لَأَجْعَلَنَّكَ مِنَ ٱلْمَسْجُونِينَ',26,29,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ أَوَلَوْ جِئْتُكَ بِشَىْءٍۢ مُّبِينٍۢ',26,30,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَأْتِ بِهِۦٓ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ',26,31,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَلْقَىٰ عَصَاهُ فَإِذَا هِىَ ثُعْبَانٌۭ مُّبِينٌۭ',26,32,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَزَعَ يَدَهُۥ فَإِذَا هِىَ بَيْضَآءُ لِلنَّٰظِرِينَ',26,33,368,317,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لِلْمَلَإِ حَوْلَهُۥٓ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٌۭ',26,34,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُرِيدُ أَن يُخْرِجَكُم مِّنْ أَرْضِكُم بِسِحْرِهِۦ فَمَاذَا تَأْمُرُونَ',26,35,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ أَرْجِهْ وَأَخَاهُ وَٱبْعَثْ فِى ٱلْمَدَآئِنِ حَٰشِرِينَ',26,36,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍۢ',26,37,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَجُمِعَ ٱلسَّحَرَةُ لِمِيقَٰتِ يَوْمٍۢ مَّعْلُومٍۢ',26,38,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقِيلَ لِلنَّاسِ هَلْ أَنتُم مُّجْتَمِعُونَ',26,39,368,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَعَلَّنَا نَتَّبِعُ ٱلسَّحَرَةَ إِن كَانُوا۟ هُمُ ٱلْغَٰلِبِينَ',26,40,369,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا جَآءَ ٱلسَّحَرَةُ قَالُوا۟ لِفِرْعَوْنَ أَئِنَّ لَنَا لَأَجْرًا إِن كُنَّا نَحْنُ ٱلْغَٰلِبِينَ',26,41,369,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ نَعَمْ وَإِنَّكُمْ إِذًۭا لَّمِنَ ٱلْمُقَرَّبِينَ',26,42,369,318,147,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ لَهُم مُّوسَىٰٓ أَلْقُوا۟ مَآ أَنتُم مُّلْقُونَ',26,43,369,318,147,false);
