INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَوَهَبْنَا لِدَاوُۥدَ سُلَيْمَٰنَ ۚ نِعْمَ ٱلْعَبْدُ ۖ إِنَّهُۥٓ أَوَّابٌ',38,30,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ عُرِضَ عَلَيْهِ بِٱلْعَشِىِّ ٱلصَّٰفِنَٰتُ ٱلْجِيَادُ',38,31,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَالَ إِنِّىٓ أَحْبَبْتُ حُبَّ ٱلْخَيْرِ عَن ذِكْرِ رَبِّى حَتَّىٰ تَوَارَتْ بِٱلْحِجَابِ',38,32,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رُدُّوهَا عَلَىَّ ۖ فَطَفِقَ مَسْحًۢا بِٱلسُّوقِ وَٱلْأَعْنَاقِ',38,33,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ فَتَنَّا سُلَيْمَٰنَ وَأَلْقَيْنَا عَلَىٰ كُرْسِيِّهِۦ جَسَدًۭا ثُمَّ أَنَابَ',38,34,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ ٱغْفِرْ لِى وَهَبْ لِى مُلْكًۭا لَّا يَنۢبَغِى لِأَحَدٍۢ مِّنۢ بَعْدِىٓ ۖ إِنَّكَ أَنتَ ٱلْوَهَّابُ',38,35,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَسَخَّرْنَا لَهُ ٱلرِّيحَ تَجْرِى بِأَمْرِهِۦ رُخَآءً حَيْثُ أَصَابَ',38,36,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلشَّيَٰطِينَ كُلَّ بَنَّآءٍۢ وَغَوَّاصٍۢ',38,37,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَءَاخَرِينَ مُقَرَّنِينَ فِى ٱلْأَصْفَادِ',38,38,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا عَطَآؤُنَا فَٱمْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسَابٍۢ',38,39,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ لَهُۥ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَـَٔابٍۢ',38,40,455,393,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱذْكُرْ عَبْدَنَآ أَيُّوبَ إِذْ نَادَىٰ رَبَّهُۥٓ أَنِّى مَسَّنِىَ ٱلشَّيْطَٰنُ بِنُصْبٍۢ وَعَذَابٍ',38,41,455,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱرْكُضْ بِرِجْلِكَ ۖ هَٰذَا مُغْتَسَلٌۢ بَارِدٌۭ وَشَرَابٌۭ',38,42,455,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَوَهَبْنَا لَهُۥٓ أَهْلَهُۥ وَمِثْلَهُم مَّعَهُمْ رَحْمَةًۭ مِّنَّا وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ',38,43,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَخُذْ بِيَدِكَ ضِغْثًۭا فَٱضْرِب بِّهِۦ وَلَا تَحْنَثْ ۗ إِنَّا وَجَدْنَٰهُ صَابِرًۭا ۚ نِّعْمَ ٱلْعَبْدُ ۖ إِنَّهُۥٓ أَوَّابٌۭ',38,44,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱذْكُرْ عِبَٰدَنَآ إِبْرَٰهِيمَ وَإِسْحَٰقَ وَيَعْقُوبَ أُو۟لِى ٱلْأَيْدِى وَٱلْأَبْصَٰرِ',38,45,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّآ أَخْلَصْنَٰهُم بِخَالِصَةٍۢ ذِكْرَى ٱلدَّارِ',38,46,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّهُمْ عِندَنَا لَمِنَ ٱلْمُصْطَفَيْنَ ٱلْأَخْيَارِ',38,47,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱذْكُرْ إِسْمَٰعِيلَ وَٱلْيَسَعَ وَذَا ٱلْكِفْلِ ۖ وَكُلٌّۭ مِّنَ ٱلْأَخْيَارِ',38,48,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا ذِكْرٌۭ ۚ وَإِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَـَٔابٍۢ',38,49,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('جَنَّٰتِ عَدْنٍۢ مُّفَتَّحَةًۭ لَّهُمُ ٱلْأَبْوَٰبُ',38,50,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مُتَّكِـِٔينَ فِيهَا يَدْعُونَ فِيهَا بِفَٰكِهَةٍۢ كَثِيرَةٍۢ وَشَرَابٍۢ',38,51,456,394,182,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَعِندَهُمْ قَٰصِرَٰتُ ٱلطَّرْفِ أَتْرَابٌ',38,52,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا مَا تُوعَدُونَ لِيَوْمِ ٱلْحِسَابِ',38,53,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ هَٰذَا لَرِزْقُنَا مَا لَهُۥ مِن نَّفَادٍ',38,54,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا ۚ وَإِنَّ لِلطَّٰغِينَ لَشَرَّ مَـَٔابٍۢ',38,55,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ ٱلْمِهَادُ',38,56,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا فَلْيَذُوقُوهُ حَمِيمٌۭ وَغَسَّاقٌۭ',38,57,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَءَاخَرُ مِن شَكْلِهِۦٓ أَزْوَٰجٌ',38,58,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَٰذَا فَوْجٌۭ مُّقْتَحِمٌۭ مَّعَكُمْ ۖ لَا مَرْحَبًۢا بِهِمْ ۚ إِنَّهُمْ صَالُوا۟ ٱلنَّارِ',38,59,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ بَلْ أَنتُمْ لَا مَرْحَبًۢا بِكُمْ ۖ أَنتُمْ قَدَّمْتُمُوهُ لَنَا ۖ فَبِئْسَ ٱلْقَرَارُ',38,60,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدْهُ عَذَابًۭا ضِعْفًۭا فِى ٱلنَّارِ',38,61,456,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ مَا لَنَا لَا نَرَىٰ رِجَالًۭا كُنَّا نَعُدُّهُم مِّنَ ٱلْأَشْرَارِ',38,62,457,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَتَّخَذْنَٰهُمْ سِخْرِيًّا أَمْ زَاغَتْ عَنْهُمُ ٱلْأَبْصَٰرُ',38,63,457,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ذَٰلِكَ لَحَقٌّۭ تَخَاصُمُ أَهْلِ ٱلنَّارِ',38,64,457,394,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنَّمَآ أَنَا۠ مُنذِرٌۭ ۖ وَمَا مِنْ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ',38,65,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رَبُّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا ٱلْعَزِيزُ ٱلْغَفَّٰرُ',38,66,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ هُوَ نَبَؤٌا۟ عَظِيمٌ',38,67,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَنتُمْ عَنْهُ مُعْرِضُونَ',38,68,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا كَانَ لِىَ مِنْ عِلْمٍۭ بِٱلْمَلَإِ ٱلْأَعْلَىٰٓ إِذْ يَخْتَصِمُونَ',38,69,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِن يُوحَىٰٓ إِلَىَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٌۭ مُّبِينٌ',38,70,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ قَالَ رَبُّكَ لِلْمَلَٰٓئِكَةِ إِنِّى خَٰلِقٌۢ بَشَرًۭا مِّن طِينٍۢ',38,71,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِذَا سَوَّيْتُهُۥ وَنَفَخْتُ فِيهِ مِن رُّوحِى فَقَعُوا۟ لَهُۥ سَٰجِدِينَ',38,72,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَسَجَدَ ٱلْمَلَٰٓئِكَةُ كُلُّهُمْ أَجْمَعُونَ',38,73,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّآ إِبْلِيسَ ٱسْتَكْبَرَ وَكَانَ مِنَ ٱلْكَٰفِرِينَ',38,74,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ يَٰٓإِبْلِيسُ مَا مَنَعَكَ أَن تَسْجُدَ لِمَا خَلَقْتُ بِيَدَىَّ ۖ أَسْتَكْبَرْتَ أَمْ كُنتَ مِنَ ٱلْعَالِينَ',38,75,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ أَنَا۠ خَيْرٌۭ مِّنْهُ ۖ خَلَقْتَنِى مِن نَّارٍۢ وَخَلَقْتَهُۥ مِن طِينٍۢ',38,76,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَٱخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌۭ',38,77,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّ عَلَيْكَ لَعْنَتِىٓ إِلَىٰ يَوْمِ ٱلدِّينِ',38,78,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ رَبِّ فَأَنظِرْنِىٓ إِلَىٰ يَوْمِ يُبْعَثُونَ',38,79,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَإِنَّكَ مِنَ ٱلْمُنظَرِينَ',38,80,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَىٰ يَوْمِ ٱلْوَقْتِ ٱلْمَعْلُومِ',38,81,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ',38,82,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّا عِبَادَكَ مِنْهُمُ ٱلْمُخْلَصِينَ',38,83,457,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ فَٱلْحَقُّ وَٱلْحَقَّ أَقُولُ',38,84,458,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَأَمْلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنْهُمْ أَجْمَعِينَ',38,85,458,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ مَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍۢ وَمَآ أَنَا۠ مِنَ ٱلْمُتَكَلِّفِينَ',38,86,458,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنْ هُوَ إِلَّا ذِكْرٌۭ لِّلْعَٰلَمِينَ',38,87,458,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَتَعْلَمُنَّ نَبَأَهُۥ بَعْدَ حِينٍۭ',38,88,458,395,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ',39,1,458,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّآ أَنزَلْنَآ إِلَيْكَ ٱلْكِتَٰبَ بِٱلْحَقِّ فَٱعْبُدِ ٱللَّهَ مُخْلِصًۭا لَّهُ ٱلدِّينَ',39,2,458,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَا لِلَّهِ ٱلدِّينُ ٱلْخَالِصُ ۚ وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلْفَىٰٓ إِنَّ ٱللَّهَ يَحْكُمُ بَيْنَهُمْ فِى مَا هُمْ فِيهِ يَخْتَلِفُونَ ۗ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ كَٰذِبٌۭ كَفَّارٌۭ',39,3,458,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّوْ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدًۭا لَّٱصْطَفَىٰ مِمَّا يَخْلُقُ مَا يَشَآءُ ۚ سُبْحَٰنَهُۥ ۖ هُوَ ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ',39,4,458,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ ۖ يُكَوِّرُ ٱلَّيْلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيْلِ ۖ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ ۖ كُلٌّۭ يَجْرِى لِأَجَلٍۢ مُّسَمًّى ۗ أَلَا هُوَ ٱلْعَزِيزُ ٱلْغَفَّٰرُ',39,5,458,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('خَلَقَكُم مِّن نَّفْسٍۢ وَٰحِدَةٍۢ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلْأَنْعَٰمِ ثَمَٰنِيَةَ أَزْوَٰجٍۢ ۚ يَخْلُقُكُمْ فِى بُطُونِ أُمَّهَٰتِكُمْ خَلْقًۭا مِّنۢ بَعْدِ خَلْقٍۢ فِى ظُلُمَٰتٍۢ ثَلَٰثٍۢ ۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ لَهُ ٱلْمُلْكُ ۖ لَآ إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُصْرَفُونَ',39,6,459,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِن تَكْفُرُوا۟ فَإِنَّ ٱللَّهَ غَنِىٌّ عَنكُمْ ۖ وَلَا يَرْضَىٰ لِعِبَادِهِ ٱلْكُفْرَ ۖ وَإِن تَشْكُرُوا۟ يَرْضَهُ لَكُمْ ۗ وَلَا تَزِرُ وَازِرَةٌۭ وِزْرَ أُخْرَىٰ ۗ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ ۚ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ',39,7,459,396,183,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَإِذَا مَسَّ ٱلْإِنسَٰنَ ضُرٌّۭ دَعَا رَبَّهُۥ مُنِيبًا إِلَيْهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعْمَةًۭ مِّنْهُ نَسِىَ مَا كَانَ يَدْعُوٓا۟ إِلَيْهِ مِن قَبْلُ وَجَعَلَ لِلَّهِ أَندَادًۭا لِّيُضِلَّ عَن سَبِيلِهِۦ ۚ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلًا ۖ إِنَّكَ مِنْ أَصْحَٰبِ ٱلنَّارِ',39,8,459,396,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمَّنْ هُوَ قَٰنِتٌ ءَانَآءَ ٱلَّيْلِ سَاجِدًۭا وَقَآئِمًۭا يَحْذَرُ ٱلْءَاخِرَةَ وَيَرْجُوا۟ رَحْمَةَ رَبِّهِۦ ۗ قُلْ هَلْ يَسْتَوِى ٱلَّذِينَ يَعْلَمُونَ وَٱلَّذِينَ لَا يَعْلَمُونَ ۗ إِنَّمَا يَتَذَكَّرُ أُو۟لُوا۟ ٱلْأَلْبَٰبِ',39,9,459,396,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ يَٰعِبَادِ ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ رَبَّكُمْ ۚ لِلَّذِينَ أَحْسَنُوا۟ فِى هَٰذِهِ ٱلدُّنْيَا حَسَنَةٌۭ ۗ وَأَرْضُ ٱللَّهِ وَٰسِعَةٌ ۗ إِنَّمَا يُوَفَّى ٱلصَّٰبِرُونَ أَجْرَهُم بِغَيْرِ حِسَابٍۢ',39,10,459,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنِّىٓ أُمِرْتُ أَنْ أَعْبُدَ ٱللَّهَ مُخْلِصًۭا لَّهُ ٱلدِّينَ',39,11,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ ٱلْمُسْلِمِينَ',39,12,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنِّىٓ أَخَافُ إِنْ عَصَيْتُ رَبِّى عَذَابَ يَوْمٍ عَظِيمٍۢ',39,13,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلِ ٱللَّهَ أَعْبُدُ مُخْلِصًۭا لَّهُۥ دِينِى',39,14,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱعْبُدُوا۟ مَا شِئْتُم مِّن دُونِهِۦ ۗ قُلْ إِنَّ ٱلْخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَٰمَةِ ۗ أَلَا ذَٰلِكَ هُوَ ٱلْخُسْرَانُ ٱلْمُبِينُ',39,15,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَهُم مِّن فَوْقِهِمْ ظُلَلٌۭ مِّنَ ٱلنَّارِ وَمِن تَحْتِهِمْ ظُلَلٌۭ ۚ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥ ۚ يَٰعِبَادِ فَٱتَّقُونِ',39,16,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ ٱجْتَنَبُوا۟ ٱلطَّٰغُوتَ أَن يَعْبُدُوهَا وَأَنَابُوٓا۟ إِلَى ٱللَّهِ لَهُمُ ٱلْبُشْرَىٰ ۚ فَبَشِّرْ عِبَادِ',39,17,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يَسْتَمِعُونَ ٱلْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُۥٓ ۚ أُو۟لَٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُ ۖ وَأُو۟لَٰٓئِكَ هُمْ أُو۟لُوا۟ ٱلْأَلْبَٰبِ',39,18,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ ٱلْعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِى ٱلنَّارِ',39,19,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَٰكِنِ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ لَهُمْ غُرَفٌۭ مِّن فَوْقِهَا غُرَفٌۭ مَّبْنِيَّةٌۭ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ ۖ وَعْدَ ٱللَّهِ ۖ لَا يُخْلِفُ ٱللَّهُ ٱلْمِيعَادَ',39,20,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءًۭ فَسَلَكَهُۥ يَنَٰبِيعَ فِى ٱلْأَرْضِ ثُمَّ يُخْرِجُ بِهِۦ زَرْعًۭا مُّخْتَلِفًا أَلْوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصْفَرًّۭا ثُمَّ يَجْعَلُهُۥ حُطَٰمًا ۚ إِنَّ فِى ذَٰلِكَ لَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ',39,21,460,397,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَمَن شَرَحَ ٱللَّهُ صَدْرَهُۥ لِلْإِسْلَٰمِ فَهُوَ عَلَىٰ نُورٍۢ مِّن رَّبِّهِۦ ۚ فَوَيْلٌۭ لِّلْقَٰسِيَةِ قُلُوبُهُم مِّن ذِكْرِ ٱللَّهِ ۚ أُو۟لَٰٓئِكَ فِى ضَلَٰلٍۢ مُّبِينٍ',39,22,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ نَزَّلَ أَحْسَنَ ٱلْحَدِيثِ كِتَٰبًۭا مُّتَشَٰبِهًۭا مَّثَانِىَ تَقْشَعِرُّ مِنْهُ جُلُودُ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَىٰ ذِكْرِ ٱللَّهِ ۚ ذَٰلِكَ هُدَى ٱللَّهِ يَهْدِى بِهِۦ مَن يَشَآءُ ۚ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍ',39,23,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَمَن يَتَّقِى بِوَجْهِهِۦ سُوٓءَ ٱلْعَذَابِ يَوْمَ ٱلْقِيَٰمَةِ ۚ وَقِيلَ لِلظَّٰلِمِينَ ذُوقُوا۟ مَا كُنتُمْ تَكْسِبُونَ',39,24,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ فَأَتَىٰهُمُ ٱلْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ',39,25,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَذَاقَهُمُ ٱللَّهُ ٱلْخِزْىَ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَلَعَذَابُ ٱلْءَاخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا۟ يَعْلَمُونَ',39,26,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِى هَٰذَا ٱلْقُرْءَانِ مِن كُلِّ مَثَلٍۢ لَّعَلَّهُمْ يَتَذَكَّرُونَ',39,27,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُرْءَانًا عَرَبِيًّا غَيْرَ ذِى عِوَجٍۢ لَّعَلَّهُمْ يَتَّقُونَ',39,28,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ضَرَبَ ٱللَّهُ مَثَلًۭا رَّجُلًۭا فِيهِ شُرَكَآءُ مُتَشَٰكِسُونَ وَرَجُلًۭا سَلَمًۭا لِّرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ ٱلْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ',39,29,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّكَ مَيِّتٌۭ وَإِنَّهُم مَّيِّتُونَ',39,30,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ إِنَّكُمْ يَوْمَ ٱلْقِيَٰمَةِ عِندَ رَبِّكُمْ تَخْتَصِمُونَ',39,31,461,398,184,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ فَمَنْ أَظْلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدْقِ إِذْ جَآءَهُۥٓ ۚ أَلَيْسَ فِى جَهَنَّمَ مَثْوًۭى لِّلْكَٰفِرِينَ',39,32,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِى جَآءَ بِٱلصِّدْقِ وَصَدَّقَ بِهِۦٓ ۙ أُو۟لَٰٓئِكَ هُمُ ٱلْمُتَّقُونَ',39,33,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ جَزَآءُ ٱلْمُحْسِنِينَ',39,34,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِيُكَفِّرَ ٱللَّهُ عَنْهُمْ أَسْوَأَ ٱلَّذِى عَمِلُوا۟ وَيَجْزِيَهُمْ أَجْرَهُم بِأَحْسَنِ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ',39,35,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَيْسَ ٱللَّهُ بِكَافٍ عَبْدَهُۥ ۖ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦ ۚ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍۢ',39,36,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يَهْدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّ ۗ أَلَيْسَ ٱللَّهُ بِعَزِيزٍۢ ذِى ٱنتِقَامٍۢ',39,37,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ ٱللَّهُ ۚ قُلْ أَفَرَءَيْتُم مَّا تَدْعُونَ مِن دُونِ ٱللَّهِ إِنْ أَرَادَنِىَ ٱللَّهُ بِضُرٍّ هَلْ هُنَّ كَٰشِفَٰتُ ضُرِّهِۦٓ أَوْ أَرَادَنِى بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَٰتُ رَحْمَتِهِۦ ۚ قُلْ حَسْبِىَ ٱللَّهُ ۖ عَلَيْهِ يَتَوَكَّلُ ٱلْمُتَوَكِّلُونَ',39,38,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ يَٰقَوْمِ ٱعْمَلُوا۟ عَلَىٰ مَكَانَتِكُمْ إِنِّى عَٰمِلٌۭ ۖ فَسَوْفَ تَعْلَمُونَ',39,39,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَن يَأْتِيهِ عَذَابٌۭ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌۭ مُّقِيمٌ',39,40,462,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّآ أَنزَلْنَا عَلَيْكَ ٱلْكِتَٰبَ لِلنَّاسِ بِٱلْحَقِّ ۖ فَمَنِ ٱهْتَدَىٰ فَلِنَفْسِهِۦ ۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍ',39,41,463,399,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ يَتَوَفَّى ٱلْأَنفُسَ حِينَ مَوْتِهَا وَٱلَّتِى لَمْ تَمُتْ فِى مَنَامِهَا ۖ فَيُمْسِكُ ٱلَّتِى قَضَىٰ عَلَيْهَا ٱلْمَوْتَ وَيُرْسِلُ ٱلْأُخْرَىٰٓ إِلَىٰٓ أَجَلٍۢ مُّسَمًّى ۚ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ لِّقَوْمٍۢ يَتَفَكَّرُونَ',39,42,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمِ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ شُفَعَآءَ ۚ قُلْ أَوَلَوْ كَانُوا۟ لَا يَمْلِكُونَ شَيْـًۭٔا وَلَا يَعْقِلُونَ',39,43,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُل لِّلَّهِ ٱلشَّفَٰعَةُ جَمِيعًۭا ۖ لَّهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۖ ثُمَّ إِلَيْهِ تُرْجَعُونَ',39,44,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا ذُكِرَ ٱللَّهُ وَحْدَهُ ٱشْمَأَزَّتْ قُلُوبُ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ ۖ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمْ يَسْتَبْشِرُونَ',39,45,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ عَٰلِمَ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ أَنتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِى مَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ',39,46,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ أَنَّ لِلَّذِينَ ظَلَمُوا۟ مَا فِى ٱلْأَرْضِ جَمِيعًۭا وَمِثْلَهُۥ مَعَهُۥ لَٱفْتَدَوْا۟ بِهِۦ مِن سُوٓءِ ٱلْعَذَابِ يَوْمَ ٱلْقِيَٰمَةِ ۚ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمْ يَكُونُوا۟ يَحْتَسِبُونَ',39,47,463,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ',39,48,464,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِذَا مَسَّ ٱلْإِنسَٰنَ ضُرٌّۭ دَعَانَا ثُمَّ إِذَا خَوَّلْنَٰهُ نِعْمَةًۭ مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلْمٍۭ ۚ بَلْ هِىَ فِتْنَةٌۭ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ',39,49,464,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَدْ قَالَهَا ٱلَّذِينَ مِن قَبْلِهِمْ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ',39,50,464,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَصَابَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ ۚ وَٱلَّذِينَ ظَلَمُوا۟ مِنْ هَٰٓؤُلَآءِ سَيُصِيبُهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَمَا هُم بِمُعْجِزِينَ',39,51,464,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوَلَمْ يَعْلَمُوٓا۟ أَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ ۚ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ لِّقَوْمٍۢ يُؤْمِنُونَ',39,52,464,400,185,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ قُلْ يَٰعِبَادِىَ ٱلَّذِينَ أَسْرَفُوا۟ عَلَىٰٓ أَنفُسِهِمْ لَا تَقْنَطُوا۟ مِن رَّحْمَةِ ٱللَّهِ ۚ إِنَّ ٱللَّهَ يَغْفِرُ ٱلذُّنُوبَ جَمِيعًا ۚ إِنَّهُۥ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ',39,53,464,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنِيبُوٓا۟ إِلَىٰ رَبِّكُمْ وَأَسْلِمُوا۟ لَهُۥ مِن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ ثُمَّ لَا تُنصَرُونَ',39,54,464,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱتَّبِعُوٓا۟ أَحْسَنَ مَآ أُنزِلَ إِلَيْكُم مِّن رَّبِّكُم مِّن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ بَغْتَةًۭ وَأَنتُمْ لَا تَشْعُرُونَ',39,55,464,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَن تَقُولَ نَفْسٌۭ يَٰحَسْرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِى جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّٰخِرِينَ',39,56,464,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ تَقُولَ لَوْ أَنَّ ٱللَّهَ هَدَىٰنِى لَكُنتُ مِنَ ٱلْمُتَّقِينَ',39,57,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ تَقُولَ حِينَ تَرَى ٱلْعَذَابَ لَوْ أَنَّ لِى كَرَّةًۭ فَأَكُونَ مِنَ ٱلْمُحْسِنِينَ',39,58,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلَىٰ قَدْ جَآءَتْكَ ءَايَٰتِى فَكَذَّبْتَ بِهَا وَٱسْتَكْبَرْتَ وَكُنتَ مِنَ ٱلْكَٰفِرِينَ',39,59,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَوْمَ ٱلْقِيَٰمَةِ تَرَى ٱلَّذِينَ كَذَبُوا۟ عَلَى ٱللَّهِ وُجُوهُهُم مُّسْوَدَّةٌ ۚ أَلَيْسَ فِى جَهَنَّمَ مَثْوًۭى لِّلْمُتَكَبِّرِينَ',39,60,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيُنَجِّى ٱللَّهُ ٱلَّذِينَ ٱتَّقَوْا۟ بِمَفَازَتِهِمْ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمْ يَحْزَنُونَ',39,61,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ خَٰلِقُ كُلِّ شَىْءٍۢ ۖ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ وَكِيلٌۭ',39,62,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۗ وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَٰتِ ٱللَّهِ أُو۟لَٰٓئِكَ هُمُ ٱلْخَٰسِرُونَ',39,63,465,401,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ أَفَغَيْرَ ٱللَّهِ تَأْمُرُوٓنِّىٓ أَعْبُدُ أَيُّهَا ٱلْجَٰهِلُونَ',39,64,465,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أُوحِىَ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلْخَٰسِرِينَ',39,65,465,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلِ ٱللَّهَ فَٱعْبُدْ وَكُن مِّنَ ٱلشَّٰكِرِينَ',39,66,465,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدْرِهِۦ وَٱلْأَرْضُ جَمِيعًۭا قَبْضَتُهُۥ يَوْمَ ٱلْقِيَٰمَةِ وَٱلسَّمَٰوَٰتُ مَطْوِيَّٰتٌۢ بِيَمِينِهِۦ ۚ سُبْحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشْرِكُونَ',39,67,465,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنُفِخَ فِى ٱلصُّورِ فَصَعِقَ مَن فِى ٱلسَّمَٰوَٰتِ وَمَن فِى ٱلْأَرْضِ إِلَّا مَن شَآءَ ٱللَّهُ ۖ ثُمَّ نُفِخَ فِيهِ أُخْرَىٰ فَإِذَا هُمْ قِيَامٌۭ يَنظُرُونَ',39,68,466,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَشْرَقَتِ ٱلْأَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلْكِتَٰبُ وَجِا۟ىٓءَ بِٱلنَّبِيِّۦنَ وَٱلشُّهَدَآءِ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَهُمْ لَا يُظْلَمُونَ',39,69,466,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَوُفِّيَتْ كُلُّ نَفْسٍۢ مَّا عَمِلَتْ وَهُوَ أَعْلَمُ بِمَا يَفْعَلُونَ',39,70,466,402,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَسِيقَ ٱلَّذِينَ كَفَرُوٓا۟ إِلَىٰ جَهَنَّمَ زُمَرًا ۖ حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ أَلَمْ يَأْتِكُمْ رُسُلٌۭ مِّنكُمْ يَتْلُونَ عَلَيْكُمْ ءَايَٰتِ رَبِّكُمْ وَيُنذِرُونَكُمْ لِقَآءَ يَوْمِكُمْ هَٰذَا ۚ قَالُوا۟ بَلَىٰ وَلَٰكِنْ حَقَّتْ كَلِمَةُ ٱلْعَذَابِ عَلَى ٱلْكَٰفِرِينَ',39,71,466,403,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قِيلَ ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ',39,72,466,403,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَسِيقَ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ إِلَى ٱلْجَنَّةِ زُمَرًا ۖ حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَٰمٌ عَلَيْكُمْ طِبْتُمْ فَٱدْخُلُوهَا خَٰلِدِينَ',39,73,466,403,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ ٱلْحَمْدُ لِلَّهِ ٱلَّذِى صَدَقَنَا وَعْدَهُۥ وَأَوْرَثَنَا ٱلْأَرْضَ نَتَبَوَّأُ مِنَ ٱلْجَنَّةِ حَيْثُ نَشَآءُ ۖ فَنِعْمَ أَجْرُ ٱلْعَٰمِلِينَ',39,74,466,403,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتَرَى ٱلْمَلَٰٓئِكَةَ حَآفِّينَ مِنْ حَوْلِ ٱلْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ ۖ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَقِيلَ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ',39,75,467,403,186,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ حمٓ',40,1,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْعَلِيمِ',40,2,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوْبِ شَدِيدِ ٱلْعِقَابِ ذِى ٱلطَّوْلِ ۖ لَآ إِلَٰهَ إِلَّا هُوَ ۖ إِلَيْهِ ٱلْمَصِيرُ',40,3,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَا يُجَٰدِلُ فِىٓ ءَايَٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُوا۟ فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى ٱلْبِلَٰدِ',40,4,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍۢ وَٱلْأَحْزَابُ مِنۢ بَعْدِهِمْ ۖ وَهَمَّتْ كُلُّ أُمَّةٍۭ بِرَسُولِهِمْ لِيَأْخُذُوهُ ۖ وَجَٰدَلُوا۟ بِٱلْبَٰطِلِ لِيُدْحِضُوا۟ بِهِ ٱلْحَقَّ فَأَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ',40,5,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓا۟ أَنَّهُمْ أَصْحَٰبُ ٱلنَّارِ',40,6,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يَحْمِلُونَ ٱلْعَرْشَ وَمَنْ حَوْلَهُۥ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِۦ وَيَسْتَغْفِرُونَ لِلَّذِينَ ءَامَنُوا۟ رَبَّنَا وَسِعْتَ كُلَّ شَىْءٍۢ رَّحْمَةًۭ وَعِلْمًۭا فَٱغْفِرْ لِلَّذِينَ تَابُوا۟ وَٱتَّبَعُوا۟ سَبِيلَكَ وَقِهِمْ عَذَابَ ٱلْجَحِيمِ',40,7,467,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رَبَّنَا وَأَدْخِلْهُمْ جَنَّٰتِ عَدْنٍ ٱلَّتِى وَعَدتَّهُمْ وَمَن صَلَحَ مِنْ ءَابَآئِهِمْ وَأَزْوَٰجِهِمْ وَذُرِّيَّٰتِهِمْ ۚ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْحَكِيمُ',40,8,468,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقِهِمُ ٱلسَّيِّـَٔاتِ ۚ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوْمَئِذٍۢ فَقَدْ رَحِمْتَهُۥ ۚ وَذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ',40,9,468,404,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ كَفَرُوا۟ يُنَادَوْنَ لَمَقْتُ ٱللَّهِ أَكْبَرُ مِن مَّقْتِكُمْ أَنفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى ٱلْإِيمَٰنِ فَتَكْفُرُونَ',40,10,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوا۟ رَبَّنَآ أَمَتَّنَا ٱثْنَتَيْنِ وَأَحْيَيْتَنَا ٱثْنَتَيْنِ فَٱعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ إِلَىٰ خُرُوجٍۢ مِّن سَبِيلٍۢ',40,11,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِىَ ٱللَّهُ وَحْدَهُۥ كَفَرْتُمْ ۖ وَإِن يُشْرَكْ بِهِۦ تُؤْمِنُوا۟ ۚ فَٱلْحُكْمُ لِلَّهِ ٱلْعَلِىِّ ٱلْكَبِيرِ',40,12,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هُوَ ٱلَّذِى يُرِيكُمْ ءَايَٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزْقًۭا ۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ',40,13,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱدْعُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ وَلَوْ كَرِهَ ٱلْكَٰفِرُونَ',40,14,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('رَفِيعُ ٱلدَّرَجَٰتِ ذُو ٱلْعَرْشِ يُلْقِى ٱلرُّوحَ مِنْ أَمْرِهِۦ عَلَىٰ مَن يَشَآءُ مِنْ عِبَادِهِۦ لِيُنذِرَ يَوْمَ ٱلتَّلَاقِ',40,15,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ هُم بَٰرِزُونَ ۖ لَا يَخْفَىٰ عَلَى ٱللَّهِ مِنْهُمْ شَىْءٌۭ ۚ لِّمَنِ ٱلْمُلْكُ ٱلْيَوْمَ ۖ لِلَّهِ ٱلْوَٰحِدِ ٱلْقَهَّارِ',40,16,468,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلْيَوْمَ تُجْزَىٰ كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ ۚ لَا ظُلْمَ ٱلْيَوْمَ ۚ إِنَّ ٱللَّهَ سَرِيعُ ٱلْحِسَابِ',40,17,469,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَنذِرْهُمْ يَوْمَ ٱلْءَازِفَةِ إِذِ ٱلْقُلُوبُ لَدَى ٱلْحَنَاجِرِ كَٰظِمِينَ ۚ مَا لِلظَّٰلِمِينَ مِنْ حَمِيمٍۢ وَلَا شَفِيعٍۢ يُطَاعُ',40,18,469,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَعْلَمُ خَآئِنَةَ ٱلْأَعْيُنِ وَمَا تُخْفِى ٱلصُّدُورُ',40,19,469,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱللَّهُ يَقْضِى بِٱلْحَقِّ ۖ وَٱلَّذِينَ يَدْعُونَ مِن دُونِهِۦ لَا يَقْضُونَ بِشَىْءٍ ۗ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ',40,20,469,405,187,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ كَانُوا۟ مِن قَبْلِهِمْ ۚ كَانُوا۟ هُمْ أَشَدَّ مِنْهُمْ قُوَّةًۭ وَءَاثَارًۭا فِى ٱلْأَرْضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٍۢ',40,21,469,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ بِأَنَّهُمْ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَكَفَرُوا۟ فَأَخَذَهُمُ ٱللَّهُ ۚ إِنَّهُۥ قَوِىٌّۭ شَدِيدُ ٱلْعِقَابِ',40,22,469,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلْطَٰنٍۢ مُّبِينٍ',40,23,469,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَىٰ فِرْعَوْنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُوا۟ سَٰحِرٌۭ كَذَّابٌۭ',40,24,469,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا جَآءَهُم بِٱلْحَقِّ مِنْ عِندِنَا قَالُوا۟ ٱقْتُلُوٓا۟ أَبْنَآءَ ٱلَّذِينَ ءَامَنُوا۟ مَعَهُۥ وَٱسْتَحْيُوا۟ نِسَآءَهُمْ ۚ وَمَا كَيْدُ ٱلْكَٰفِرِينَ إِلَّا فِى ضَلَٰلٍۢ',40,25,469,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ فِرْعَوْنُ ذَرُونِىٓ أَقْتُلْ مُوسَىٰ وَلْيَدْعُ رَبَّهُۥٓ ۖ إِنِّىٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمْ أَوْ أَن يُظْهِرَ فِى ٱلْأَرْضِ ٱلْفَسَادَ',40,26,470,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ مُوسَىٰٓ إِنِّى عُذْتُ بِرَبِّى وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٍۢ لَّا يُؤْمِنُ بِيَوْمِ ٱلْحِسَابِ',40,27,470,406,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ رَجُلٌۭ مُّؤْمِنٌۭ مِّنْ ءَالِ فِرْعَوْنَ يَكْتُمُ إِيمَٰنَهُۥٓ أَتَقْتُلُونَ رَجُلًا أَن يَقُولَ رَبِّىَ ٱللَّهُ وَقَدْ جَآءَكُم بِٱلْبَيِّنَٰتِ مِن رَّبِّكُمْ ۖ وَإِن يَكُ كَٰذِبًۭا فَعَلَيْهِ كَذِبُهُۥ ۖ وَإِن يَكُ صَادِقًۭا يُصِبْكُم بَعْضُ ٱلَّذِى يَعِدُكُمْ ۖ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ مُسْرِفٌۭ كَذَّابٌۭ',40,28,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰقَوْمِ لَكُمُ ٱلْمُلْكُ ٱلْيَوْمَ ظَٰهِرِينَ فِى ٱلْأَرْضِ فَمَن يَنصُرُنَا مِنۢ بَأْسِ ٱللَّهِ إِن جَآءَنَا ۚ قَالَ فِرْعَوْنُ مَآ أُرِيكُمْ إِلَّا مَآ أَرَىٰ وَمَآ أَهْدِيكُمْ إِلَّا سَبِيلَ ٱلرَّشَادِ',40,29,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِىٓ ءَامَنَ يَٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُم مِّثْلَ يَوْمِ ٱلْأَحْزَابِ',40,30,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مِثْلَ دَأْبِ قَوْمِ نُوحٍۢ وَعَادٍۢ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعْدِهِمْ ۚ وَمَا ٱللَّهُ يُرِيدُ ظُلْمًۭا لِّلْعِبَادِ',40,31,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُمْ يَوْمَ ٱلتَّنَادِ',40,32,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ تُوَلُّونَ مُدْبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنْ عَاصِمٍۢ ۗ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍۢ',40,33,470,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ جَآءَكُمْ يُوسُفُ مِن قَبْلُ بِٱلْبَيِّنَٰتِ فَمَا زِلْتُمْ فِى شَكٍّۢ مِّمَّا جَآءَكُم بِهِۦ ۖ حَتَّىٰٓ إِذَا هَلَكَ قُلْتُمْ لَن يَبْعَثَ ٱللَّهُ مِنۢ بَعْدِهِۦ رَسُولًۭا ۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنْ هُوَ مُسْرِفٌۭ مُّرْتَابٌ',40,34,471,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ بِغَيْرِ سُلْطَٰنٍ أَتَىٰهُمْ ۖ كَبُرَ مَقْتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُوا۟ ۚ كَذَٰلِكَ يَطْبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلْبِ مُتَكَبِّرٍۢ جَبَّارٍۢ',40,35,471,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ فِرْعَوْنُ يَٰهَٰمَٰنُ ٱبْنِ لِى صَرْحًۭا لَّعَلِّىٓ أَبْلُغُ ٱلْأَسْبَٰبَ',40,36,471,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَسْبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّى لَأَظُنُّهُۥ كَٰذِبًۭا ۚ وَكَذَٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِ ۚ وَمَا كَيْدُ فِرْعَوْنَ إِلَّا فِى تَبَابٍۢ',40,37,471,407,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِىٓ ءَامَنَ يَٰقَوْمِ ٱتَّبِعُونِ أَهْدِكُمْ سَبِيلَ ٱلرَّشَادِ',40,38,471,408,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰقَوْمِ إِنَّمَا هَٰذِهِ ٱلْحَيَوٰةُ ٱلدُّنْيَا مَتَٰعٌۭ وَإِنَّ ٱلْءَاخِرَةَ هِىَ دَارُ ٱلْقَرَارِ',40,39,471,408,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَنْ عَمِلَ سَيِّئَةًۭ فَلَا يُجْزَىٰٓ إِلَّا مِثْلَهَا ۖ وَمَنْ عَمِلَ صَٰلِحًۭا مِّن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌۭ فَأُو۟لَٰٓئِكَ يَدْخُلُونَ ٱلْجَنَّةَ يُرْزَقُونَ فِيهَا بِغَيْرِ حِسَابٍۢ',40,40,471,408,188,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَيَٰقَوْمِ مَا لِىٓ أَدْعُوكُمْ إِلَى ٱلنَّجَوٰةِ وَتَدْعُونَنِىٓ إِلَى ٱلنَّارِ',40,41,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَدْعُونَنِى لِأَكْفُرَ بِٱللَّهِ وَأُشْرِكَ بِهِۦ مَا لَيْسَ لِى بِهِۦ عِلْمٌۭ وَأَنَا۠ أَدْعُوكُمْ إِلَى ٱلْعَزِيزِ ٱلْغَفَّٰرِ',40,42,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا جَرَمَ أَنَّمَا تَدْعُونَنِىٓ إِلَيْهِ لَيْسَ لَهُۥ دَعْوَةٌۭ فِى ٱلدُّنْيَا وَلَا فِى ٱلْءَاخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلْمُسْرِفِينَ هُمْ أَصْحَٰبُ ٱلنَّارِ',40,43,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَسَتَذْكُرُونَ مَآ أَقُولُ لَكُمْ ۚ وَأُفَوِّضُ أَمْرِىٓ إِلَى ٱللَّهِ ۚ إِنَّ ٱللَّهَ بَصِيرٌۢ بِٱلْعِبَادِ',40,44,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُوا۟ ۖ وَحَاقَ بِـَٔالِ فِرْعَوْنَ سُوٓءُ ٱلْعَذَابِ',40,45,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلنَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّۭا وَعَشِيًّۭا ۖ وَيَوْمَ تَقُومُ ٱلسَّاعَةُ أَدْخِلُوٓا۟ ءَالَ فِرْعَوْنَ أَشَدَّ ٱلْعَذَابِ',40,46,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذْ يَتَحَآجُّونَ فِى ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَٰٓؤُا۟ لِلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُنَّا لَكُمْ تَبَعًۭا فَهَلْ أَنتُم مُّغْنُونَ عَنَّا نَصِيبًۭا مِّنَ ٱلنَّارِ',40,47,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالَ ٱلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُلٌّۭ فِيهَآ إِنَّ ٱللَّهَ قَدْ حَكَمَ بَيْنَ ٱلْعِبَادِ',40,48,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِينَ فِى ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدْعُوا۟ رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًۭا مِّنَ ٱلْعَذَابِ',40,49,472,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قَالُوٓا۟ أَوَلَمْ تَكُ تَأْتِيكُمْ رُسُلُكُم بِٱلْبَيِّنَٰتِ ۖ قَالُوا۟ بَلَىٰ ۚ قَالُوا۟ فَٱدْعُوا۟ ۗ وَمَا دُعَٰٓؤُا۟ ٱلْكَٰفِرِينَ إِلَّا فِى ضَلَٰلٍ',40,50,473,408,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُوا۟ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَيَوْمَ يَقُومُ ٱلْأَشْهَٰدُ',40,51,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَوْمَ لَا يَنفَعُ ٱلظَّٰلِمِينَ مَعْذِرَتُهُمْ ۖ وَلَهُمُ ٱللَّعْنَةُ وَلَهُمْ سُوٓءُ ٱلدَّارِ',40,52,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْهُدَىٰ وَأَوْرَثْنَا بَنِىٓ إِسْرَٰٓءِيلَ ٱلْكِتَٰبَ',40,53,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هُدًۭى وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ',40,54,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ وَٱسْتَغْفِرْ لِذَنۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِٱلْعَشِىِّ وَٱلْإِبْكَٰرِ',40,55,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ بِغَيْرِ سُلْطَٰنٍ أَتَىٰهُمْ ۙ إِن فِى صُدُورِهِمْ إِلَّا كِبْرٌۭ مَّا هُم بِبَٰلِغِيهِ ۚ فَٱسْتَعِذْ بِٱللَّهِ ۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ',40,56,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَخَلْقُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ أَكْبَرُ مِنْ خَلْقِ ٱلنَّاسِ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ',40,57,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا يَسْتَوِى ٱلْأَعْمَىٰ وَٱلْبَصِيرُ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَلَا ٱلْمُسِىٓءُ ۚ قَلِيلًۭا مَّا تَتَذَكَّرُونَ',40,58,473,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلسَّاعَةَ لَءَاتِيَةٌۭ لَّا رَيْبَ فِيهَا وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يُؤْمِنُونَ',40,59,474,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ رَبُّكُمُ ٱدْعُونِىٓ أَسْتَجِبْ لَكُمْ ۚ إِنَّ ٱلَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِى سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَ',40,60,474,409,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلَّيْلَ لِتَسْكُنُوا۟ فِيهِ وَٱلنَّهَارَ مُبْصِرًا ۚ إِنَّ ٱللَّهَ لَذُو فَضْلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَشْكُرُونَ',40,61,474,410,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ خَٰلِقُ كُلِّ شَىْءٍۢ لَّآ إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ',40,62,474,410,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَذَٰلِكَ يُؤْفَكُ ٱلَّذِينَ كَانُوا۟ بِـَٔايَٰتِ ٱللَّهِ يَجْحَدُونَ',40,63,474,410,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ قَرَارًۭا وَٱلسَّمَآءَ بِنَآءًۭ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِ ۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ ۖ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلْعَٰلَمِينَ',40,64,474,410,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هُوَ ٱلْحَىُّ لَآ إِلَٰهَ إِلَّا هُوَ فَٱدْعُوهُ مُخْلِصِينَ لَهُ ٱلدِّينَ ۗ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ',40,65,474,410,189,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ قُلْ إِنِّى نُهِيتُ أَنْ أَعْبُدَ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِىَ ٱلْبَيِّنَٰتُ مِن رَّبِّى وَأُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ ٱلْعَٰلَمِينَ',40,66,474,410,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هُوَ ٱلَّذِى خَلَقَكُم مِّن تُرَابٍۢ ثُمَّ مِن نُّطْفَةٍۢ ثُمَّ مِنْ عَلَقَةٍۢ ثُمَّ يُخْرِجُكُمْ طِفْلًۭا ثُمَّ لِتَبْلُغُوٓا۟ أَشُدَّكُمْ ثُمَّ لِتَكُونُوا۟ شُيُوخًۭا ۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبْلُ ۖ وَلِتَبْلُغُوٓا۟ أَجَلًۭا مُّسَمًّۭى وَلَعَلَّكُمْ تَعْقِلُونَ',40,67,475,410,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هُوَ ٱلَّذِى يُحْىِۦ وَيُمِيتُ ۖ فَإِذَا قَضَىٰٓ أَمْرًۭا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ',40,68,475,410,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَمْ تَرَ إِلَى ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ أَنَّىٰ يُصْرَفُونَ',40,69,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ كَذَّبُوا۟ بِٱلْكِتَٰبِ وَبِمَآ أَرْسَلْنَا بِهِۦ رُسُلَنَا ۖ فَسَوْفَ يَعْلَمُونَ',40,70,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذِ ٱلْأَغْلَٰلُ فِىٓ أَعْنَٰقِهِمْ وَٱلسَّلَٰسِلُ يُسْحَبُونَ',40,71,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فِى ٱلْحَمِيمِ ثُمَّ فِى ٱلنَّارِ يُسْجَرُونَ',40,72,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ قِيلَ لَهُمْ أَيْنَ مَا كُنتُمْ تُشْرِكُونَ',40,73,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مِن دُونِ ٱللَّهِ ۖ قَالُوا۟ ضَلُّوا۟ عَنَّا بَل لَّمْ نَكُن نَّدْعُوا۟ مِن قَبْلُ شَيْـًۭٔا ۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلْكَٰفِرِينَ',40,74,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكُم بِمَا كُنتُمْ تَفْرَحُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَبِمَا كُنتُمْ تَمْرَحُونَ',40,75,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ',40,76,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّۭ ۚ فَإِمَّا نُرِيَنَّكَ بَعْضَ ٱلَّذِى نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا يُرْجَعُونَ',40,77,475,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَرْسَلْنَا رُسُلًۭا مِّن قَبْلِكَ مِنْهُم مَّن قَصَصْنَا عَلَيْكَ وَمِنْهُم مَّن لَّمْ نَقْصُصْ عَلَيْكَ ۗ وَمَا كَانَ لِرَسُولٍ أَن يَأْتِىَ بِـَٔايَةٍ إِلَّا بِإِذْنِ ٱللَّهِ ۚ فَإِذَا جَآءَ أَمْرُ ٱللَّهِ قُضِىَ بِٱلْحَقِّ وَخَسِرَ هُنَالِكَ ٱلْمُبْطِلُونَ',40,78,476,411,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَنْعَٰمَ لِتَرْكَبُوا۟ مِنْهَا وَمِنْهَا تَأْكُلُونَ',40,79,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَكُمْ فِيهَا مَنَٰفِعُ وَلِتَبْلُغُوا۟ عَلَيْهَا حَاجَةًۭ فِى صُدُورِكُمْ وَعَلَيْهَا وَعَلَى ٱلْفُلْكِ تُحْمَلُونَ',40,80,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيُرِيكُمْ ءَايَٰتِهِۦ فَأَىَّ ءَايَٰتِ ٱللَّهِ تُنكِرُونَ',40,81,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ ۚ كَانُوٓا۟ أَكْثَرَ مِنْهُمْ وَأَشَدَّ قُوَّةًۭ وَءَاثَارًۭا فِى ٱلْأَرْضِ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ',40,82,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا جَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَرِحُوا۟ بِمَا عِندَهُم مِّنَ ٱلْعِلْمِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ',40,83,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا رَأَوْا۟ بَأْسَنَا قَالُوٓا۟ ءَامَنَّا بِٱللَّهِ وَحْدَهُۥ وَكَفَرْنَا بِمَا كُنَّا بِهِۦ مُشْرِكِينَ',40,84,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمْ يَكُ يَنفَعُهُمْ إِيمَٰنُهُمْ لَمَّا رَأَوْا۟ بَأْسَنَا ۖ سُنَّتَ ٱللَّهِ ٱلَّتِى قَدْ خَلَتْ فِى عِبَادِهِۦ ۖ وَخَسِرَ هُنَالِكَ ٱلْكَٰفِرُونَ',40,85,476,412,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ حمٓ',41,1,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَنزِيلٌۭ مِّنَ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ',41,2,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كِتَٰبٌۭ فُصِّلَتْ ءَايَٰتُهُۥ قُرْءَانًا عَرَبِيًّۭا لِّقَوْمٍۢ يَعْلَمُونَ',41,3,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَشِيرًۭا وَنَذِيرًۭا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ',41,4,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ قُلُوبُنَا فِىٓ أَكِنَّةٍۢ مِّمَّا تَدْعُونَآ إِلَيْهِ وَفِىٓ ءَاذَانِنَا وَقْرٌۭ وَمِنۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌۭ فَٱعْمَلْ إِنَّنَا عَٰمِلُونَ',41,5,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِنَّمَآ أَنَا۠ بَشَرٌۭ مِّثْلُكُمْ يُوحَىٰٓ إِلَىَّ أَنَّمَآ إِلَٰهُكُمْ إِلَٰهٌۭ وَٰحِدٌۭ فَٱسْتَقِيمُوٓا۟ إِلَيْهِ وَٱسْتَغْفِرُوهُ ۗ وَوَيْلٌۭ لِّلْمُشْرِكِينَ',41,6,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ لَا يُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْءَاخِرَةِ هُمْ كَٰفِرُونَ',41,7,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍۢ',41,8,477,413,190,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِٱلَّذِى خَلَقَ ٱلْأَرْضَ فِى يَوْمَيْنِ وَتَجْعَلُونَ لَهُۥٓ أَندَادًۭا ۚ ذَٰلِكَ رَبُّ ٱلْعَٰلَمِينَ',41,9,477,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلَ فِيهَا رَوَٰسِىَ مِن فَوْقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقْوَٰتَهَا فِىٓ أَرْبَعَةِ أَيَّامٍۢ سَوَآءًۭ لِّلسَّآئِلِينَ',41,10,477,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ثُمَّ ٱسْتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِىَ دُخَانٌۭ فَقَالَ لَهَا وَلِلْأَرْضِ ٱئْتِيَا طَوْعًا أَوْ كَرْهًۭا قَالَتَآ أَتَيْنَا طَآئِعِينَ',41,11,477,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَقَضَىٰهُنَّ سَبْعَ سَمَٰوَاتٍۢ فِى يَوْمَيْنِ وَأَوْحَىٰ فِى كُلِّ سَمَآءٍ أَمْرَهَا ۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَٰبِيحَ وَحِفْظًۭا ۚ ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ',41,12,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِنْ أَعْرَضُوا۟ فَقُلْ أَنذَرْتُكُمْ صَٰعِقَةًۭ مِّثْلَ صَٰعِقَةِ عَادٍۢ وَثَمُودَ',41,13,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِذْ جَآءَتْهُمُ ٱلرُّسُلُ مِنۢ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوٓا۟ إِلَّا ٱللَّهَ ۖ قَالُوا۟ لَوْ شَآءَ رَبُّنَا لَأَنزَلَ مَلَٰٓئِكَةًۭ فَإِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَٰفِرُونَ',41,14,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَمَّا عَادٌۭ فَٱسْتَكْبَرُوا۟ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَقَالُوا۟ مَنْ أَشَدُّ مِنَّا قُوَّةً ۖ أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةًۭ ۖ وَكَانُوا۟ بِـَٔايَٰتِنَا يَجْحَدُونَ',41,15,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَرْسَلْنَا عَلَيْهِمْ رِيحًۭا صَرْصَرًۭا فِىٓ أَيَّامٍۢ نَّحِسَاتٍۢ لِّنُذِيقَهُمْ عَذَابَ ٱلْخِزْىِ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَلَعَذَابُ ٱلْءَاخِرَةِ أَخْزَىٰ ۖ وَهُمْ لَا يُنصَرُونَ',41,16,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَأَمَّا ثَمُودُ فَهَدَيْنَٰهُمْ فَٱسْتَحَبُّوا۟ ٱلْعَمَىٰ عَلَى ٱلْهُدَىٰ فَأَخَذَتْهُمْ صَٰعِقَةُ ٱلْعَذَابِ ٱلْهُونِ بِمَا كَانُوا۟ يَكْسِبُونَ',41,17,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَجَّيْنَا ٱلَّذِينَ ءَامَنُوا۟ وَكَانُوا۟ يَتَّقُونَ',41,18,478,414,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَوْمَ يُحْشَرُ أَعْدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمْ يُوزَعُونَ',41,19,478,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَٰرُهُمْ وَجُلُودُهُم بِمَا كَانُوا۟ يَعْمَلُونَ',41,20,478,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ لِجُلُودِهِمْ لِمَ شَهِدتُّمْ عَلَيْنَا ۖ قَالُوٓا۟ أَنطَقَنَا ٱللَّهُ ٱلَّذِىٓ أَنطَقَ كُلَّ شَىْءٍۢ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍۢ وَإِلَيْهِ تُرْجَعُونَ',41,21,479,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا كُنتُمْ تَسْتَتِرُونَ أَن يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ أَبْصَٰرُكُمْ وَلَا جُلُودُكُمْ وَلَٰكِن ظَنَنتُمْ أَنَّ ٱللَّهَ لَا يَعْلَمُ كَثِيرًۭا مِّمَّا تَعْمَلُونَ',41,22,479,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَذَٰلِكُمْ ظَنُّكُمُ ٱلَّذِى ظَنَنتُم بِرَبِّكُمْ أَرْدَىٰكُمْ فَأَصْبَحْتُم مِّنَ ٱلْخَٰسِرِينَ',41,23,479,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِن يَصْبِرُوا۟ فَٱلنَّارُ مَثْوًۭى لَّهُمْ ۖ وَإِن يَسْتَعْتِبُوا۟ فَمَا هُم مِّنَ ٱلْمُعْتَبِينَ',41,24,479,415,191,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَقَيَّضْنَا لَهُمْ قُرَنَآءَ فَزَيَّنُوا۟ لَهُم مَّا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍۢ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ ۖ إِنَّهُمْ كَانُوا۟ خَٰسِرِينَ',41,25,479,415,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَا تَسْمَعُوا۟ لِهَٰذَا ٱلْقُرْءَانِ وَٱلْغَوْا۟ فِيهِ لَعَلَّكُمْ تَغْلِبُونَ',41,26,479,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُوا۟ عَذَابًۭا شَدِيدًۭا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ',41,27,479,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ جَزَآءُ أَعْدَآءِ ٱللَّهِ ٱلنَّارُ ۖ لَهُمْ فِيهَا دَارُ ٱلْخُلْدِ ۖ جَزَآءًۢ بِمَا كَانُوا۟ بِـَٔايَٰتِنَا يَجْحَدُونَ',41,28,479,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالَ ٱلَّذِينَ كَفَرُوا۟ رَبَّنَآ أَرِنَا ٱلَّذَيْنِ أَضَلَّانَا مِنَ ٱلْجِنِّ وَٱلْإِنسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ ٱلْأَسْفَلِينَ',41,29,479,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَٰمُوا۟ تَتَنَزَّلُ عَلَيْهِمُ ٱلْمَلَٰٓئِكَةُ أَلَّا تَخَافُوا۟ وَلَا تَحْزَنُوا۟ وَأَبْشِرُوا۟ بِٱلْجَنَّةِ ٱلَّتِى كُنتُمْ تُوعَدُونَ',41,30,480,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('نَحْنُ أَوْلِيَآؤُكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَفِى ٱلْءَاخِرَةِ ۖ وَلَكُمْ فِيهَا مَا تَشْتَهِىٓ أَنفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ',41,31,480,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('نُزُلًۭا مِّنْ غَفُورٍۢ رَّحِيمٍۢ',41,32,480,416,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَنْ أَحْسَنُ قَوْلًۭا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحًۭا وَقَالَ إِنَّنِى مِنَ ٱلْمُسْلِمِينَ',41,33,480,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا تَسْتَوِى ٱلْحَسَنَةُ وَلَا ٱلسَّيِّئَةُ ۚ ٱدْفَعْ بِٱلَّتِى هِىَ أَحْسَنُ فَإِذَا ٱلَّذِى بَيْنَكَ وَبَيْنَهُۥ عَدَٰوَةٌۭ كَأَنَّهُۥ وَلِىٌّ حَمِيمٌۭ',41,34,480,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُوا۟ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٍۢ',41,35,480,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيْطَٰنِ نَزْغٌۭ فَٱسْتَعِذْ بِٱللَّهِ ۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ',41,36,480,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنْ ءَايَٰتِهِ ٱلَّيْلُ وَٱلنَّهَارُ وَٱلشَّمْسُ وَٱلْقَمَرُ ۚ لَا تَسْجُدُوا۟ لِلشَّمْسِ وَلَا لِلْقَمَرِ وَٱسْجُدُوا۟ لِلَّهِ ٱلَّذِى خَلَقَهُنَّ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ',41,37,480,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِنِ ٱسْتَكْبَرُوا۟ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيْلِ وَٱلنَّهَارِ وَهُمْ لَا يَسْـَٔمُونَ ۩',41,38,480,417,192,true);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنْ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى ٱلْأَرْضَ خَٰشِعَةًۭ فَإِذَآ أَنزَلْنَا عَلَيْهَا ٱلْمَآءَ ٱهْتَزَّتْ وَرَبَتْ ۚ إِنَّ ٱلَّذِىٓ أَحْيَاهَا لَمُحْىِ ٱلْمَوْتَىٰٓ ۚ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌ',41,39,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ يُلْحِدُونَ فِىٓ ءَايَٰتِنَا لَا يَخْفَوْنَ عَلَيْنَآ ۗ أَفَمَن يُلْقَىٰ فِى ٱلنَّارِ خَيْرٌ أَم مَّن يَأْتِىٓ ءَامِنًۭا يَوْمَ ٱلْقِيَٰمَةِ ۚ ٱعْمَلُوا۟ مَا شِئْتُمْ ۖ إِنَّهُۥ بِمَا تَعْمَلُونَ بَصِيرٌ',41,40,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ كَفَرُوا۟ بِٱلذِّكْرِ لَمَّا جَآءَهُمْ ۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٌۭ',41,41,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّا يَأْتِيهِ ٱلْبَٰطِلُ مِنۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِۦ ۖ تَنزِيلٌۭ مِّنْ حَكِيمٍ حَمِيدٍۢ',41,42,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَّا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِن قَبْلِكَ ۚ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍۢ وَذُو عِقَابٍ أَلِيمٍۢ',41,43,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ جَعَلْنَٰهُ قُرْءَانًا أَعْجَمِيًّۭا لَّقَالُوا۟ لَوْلَا فُصِّلَتْ ءَايَٰتُهُۥٓ ۖ ءَا۬عْجَمِىٌّۭ وَعَرَبِىٌّۭ ۗ قُلْ هُوَ لِلَّذِينَ ءَامَنُوا۟ هُدًۭى وَشِفَآءٌۭ ۖ وَٱلَّذِينَ لَا يُؤْمِنُونَ فِىٓ ءَاذَانِهِمْ وَقْرٌۭ وَهُوَ عَلَيْهِمْ عَمًى ۚ أُو۟لَٰٓئِكَ يُنَادَوْنَ مِن مَّكَانٍۭ بَعِيدٍۢ',41,44,481,417,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ فَٱخْتُلِفَ فِيهِ ۗ وَلَوْلَا كَلِمَةٌۭ سَبَقَتْ مِن رَّبِّكَ لَقُضِىَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِى شَكٍّۢ مِّنْهُ مُرِيبٍۢ',41,45,481,418,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَّنْ عَمِلَ صَٰلِحًۭا فَلِنَفْسِهِۦ ۖ وَمَنْ أَسَآءَ فَعَلَيْهَا ۗ وَمَا رَبُّكَ بِظَلَّٰمٍۢ لِّلْعَبِيدِ',41,46,481,418,192,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ إِلَيْهِ يُرَدُّ عِلْمُ ٱلسَّاعَةِ ۚ وَمَا تَخْرُجُ مِن ثَمَرَٰتٍۢ مِّنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ ۚ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَآءِى قَالُوٓا۟ ءَاذَنَّٰكَ مَا مِنَّا مِن شَهِيدٍۢ',41,47,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَضَلَّ عَنْهُم مَّا كَانُوا۟ يَدْعُونَ مِن قَبْلُ ۖ وَظَنُّوا۟ مَا لَهُم مِّن مَّحِيصٍۢ',41,48,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَّا يَسْـَٔمُ ٱلْإِنسَٰنُ مِن دُعَآءِ ٱلْخَيْرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٌۭ قَنُوطٌۭ',41,49,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَئِنْ أَذَقْنَٰهُ رَحْمَةًۭ مِّنَّا مِنۢ بَعْدِ ضَرَّآءَ مَسَّتْهُ لَيَقُولَنَّ هَٰذَا لِى وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةًۭ وَلَئِن رُّجِعْتُ إِلَىٰ رَبِّىٓ إِنَّ لِى عِندَهُۥ لَلْحُسْنَىٰ ۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُوا۟ بِمَا عَمِلُوا۟ وَلَنُذِيقَنَّهُم مِّنْ عَذَابٍ غَلِيظٍۢ',41,50,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَآ أَنْعَمْنَا عَلَى ٱلْإِنسَٰنِ أَعْرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٍۢ',41,51,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ ثُمَّ كَفَرْتُم بِهِۦ مَنْ أَضَلُّ مِمَّنْ هُوَ فِى شِقَاقٍۭ بَعِيدٍۢ',41,52,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('سَنُرِيهِمْ ءَايَٰتِنَا فِى ٱلْءَافَاقِ وَفِىٓ أَنفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ ٱلْحَقُّ ۗ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَىْءٍۢ شَهِيدٌ',41,53,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَلَآ إِنَّهُمْ فِى مِرْيَةٍۢ مِّن لِّقَآءِ رَبِّهِمْ ۗ أَلَآ إِنَّهُۥ بِكُلِّ شَىْءٍۢ مُّحِيطٌۢ',41,54,482,418,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ حمٓ',42,1,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('عٓسٓقٓ',42,2,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('كَذَٰلِكَ يُوحِىٓ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ',42,3,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۖ وَهُوَ ٱلْعَلِىُّ ٱلْعَظِيمُ',42,4,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرْنَ مِن فَوْقِهِنَّ ۚ وَٱلْمَلَٰٓئِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَن فِى ٱلْأَرْضِ ۗ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ',42,5,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيْهِمْ وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍۢ',42,6,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ قُرْءَانًا عَرَبِيًّۭا لِّتُنذِرَ أُمَّ ٱلْقُرَىٰ وَمَنْ حَوْلَهَا وَتُنذِرَ يَوْمَ ٱلْجَمْعِ لَا رَيْبَ فِيهِ ۚ فَرِيقٌۭ فِى ٱلْجَنَّةِ وَفَرِيقٌۭ فِى ٱلسَّعِيرِ',42,7,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ شَآءَ ٱللَّهُ لَجَعَلَهُمْ أُمَّةًۭ وَٰحِدَةًۭ وَلَٰكِن يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ ۚ وَٱلظَّٰلِمُونَ مَا لَهُم مِّن وَلِىٍّۢ وَلَا نَصِيرٍ',42,8,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمِ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ ۖ فَٱللَّهُ هُوَ ٱلْوَلِىُّ وَهُوَ يُحْىِ ٱلْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَىْءٍۢ قَدِيرٌۭ',42,9,483,419,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا ٱخْتَلَفْتُمْ فِيهِ مِن شَىْءٍۢ فَحُكْمُهُۥٓ إِلَى ٱللَّهِ ۚ ذَٰلِكُمُ ٱللَّهُ رَبِّى عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ',42,10,483,420,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۚ جَعَلَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَٰجًۭا وَمِنَ ٱلْأَنْعَٰمِ أَزْوَٰجًۭا ۖ يَذْرَؤُكُمْ فِيهِ ۚ لَيْسَ كَمِثْلِهِۦ شَىْءٌۭ ۖ وَهُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ',42,11,484,420,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۖ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ ۚ إِنَّهُۥ بِكُلِّ شَىْءٍ عَلِيمٌۭ',42,12,484,420,193,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحًۭا وَٱلَّذِىٓ أَوْحَيْنَآ إِلَيْكَ وَمَا وَصَّيْنَا بِهِۦٓ إِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓ ۖ أَنْ أَقِيمُوا۟ ٱلدِّينَ وَلَا تَتَفَرَّقُوا۟ فِيهِ ۚ كَبُرَ عَلَى ٱلْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ ۚ ٱللَّهُ يَجْتَبِىٓ إِلَيْهِ مَن يَشَآءُ وَيَهْدِىٓ إِلَيْهِ مَن يُنِيبُ',42,13,484,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا تَفَرَّقُوٓا۟ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ ٱلْعِلْمُ بَغْيًۢا بَيْنَهُمْ ۚ وَلَوْلَا كَلِمَةٌۭ سَبَقَتْ مِن رَّبِّكَ إِلَىٰٓ أَجَلٍۢ مُّسَمًّۭى لَّقُضِىَ بَيْنَهُمْ ۚ وَإِنَّ ٱلَّذِينَ أُورِثُوا۟ ٱلْكِتَٰبَ مِنۢ بَعْدِهِمْ لَفِى شَكٍّۢ مِّنْهُ مُرِيبٍۢ',42,14,484,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلِذَٰلِكَ فَٱدْعُ ۖ وَٱسْتَقِمْ كَمَآ أُمِرْتَ ۖ وَلَا تَتَّبِعْ أَهْوَآءَهُمْ ۖ وَقُلْ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَٰبٍۢ ۖ وَأُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ ۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمْ ۖ لَنَآ أَعْمَٰلُنَا وَلَكُمْ أَعْمَٰلُكُمْ ۖ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمُ ۖ ٱللَّهُ يَجْمَعُ بَيْنَنَا ۖ وَإِلَيْهِ ٱلْمَصِيرُ',42,15,484,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يُحَآجُّونَ فِى ٱللَّهِ مِنۢ بَعْدِ مَا ٱسْتُجِيبَ لَهُۥ حُجَّتُهُمْ دَاحِضَةٌ عِندَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌۭ وَلَهُمْ عَذَابٌۭ شَدِيدٌ',42,16,485,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ ٱلَّذِىٓ أَنزَلَ ٱلْكِتَٰبَ بِٱلْحَقِّ وَٱلْمِيزَانَ ۗ وَمَا يُدْرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٌۭ',42,17,485,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَسْتَعْجِلُ بِهَا ٱلَّذِينَ لَا يُؤْمِنُونَ بِهَا ۖ وَٱلَّذِينَ ءَامَنُوا۟ مُشْفِقُونَ مِنْهَا وَيَعْلَمُونَ أَنَّهَا ٱلْحَقُّ ۗ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِى ٱلسَّاعَةِ لَفِى ضَلَٰلٍۭ بَعِيدٍ',42,18,485,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱللَّهُ لَطِيفٌۢ بِعِبَادِهِۦ يَرْزُقُ مَن يَشَآءُ ۖ وَهُوَ ٱلْقَوِىُّ ٱلْعَزِيزُ',42,19,485,420,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('مَن كَانَ يُرِيدُ حَرْثَ ٱلْءَاخِرَةِ نَزِدْ لَهُۥ فِى حَرْثِهِۦ ۖ وَمَن كَانَ يُرِيدُ حَرْثَ ٱلدُّنْيَا نُؤْتِهِۦ مِنْهَا وَمَا لَهُۥ فِى ٱلْءَاخِرَةِ مِن نَّصِيبٍ',42,20,485,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ لَهُمْ شُرَكَٰٓؤُا۟ شَرَعُوا۟ لَهُم مِّنَ ٱلدِّينِ مَا لَمْ يَأْذَنۢ بِهِ ٱللَّهُ ۚ وَلَوْلَا كَلِمَةُ ٱلْفَصْلِ لَقُضِىَ بَيْنَهُمْ ۗ وَإِنَّ ٱلظَّٰلِمِينَ لَهُمْ عَذَابٌ أَلِيمٌۭ',42,21,485,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('تَرَى ٱلظَّٰلِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا۟ وَهُوَ وَاقِعٌۢ بِهِمْ ۗ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فِى رَوْضَاتِ ٱلْجَنَّاتِ ۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ هُوَ ٱلْفَضْلُ ٱلْكَبِيرُ',42,22,485,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ذَٰلِكَ ٱلَّذِى يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ ۗ قُل لَّآ أَسْـَٔلُكُمْ عَلَيْهِ أَجْرًا إِلَّا ٱلْمَوَدَّةَ فِى ٱلْقُرْبَىٰ ۗ وَمَن يَقْتَرِفْ حَسَنَةًۭ نَّزِدْ لَهُۥ فِيهَا حُسْنًا ۚ إِنَّ ٱللَّهَ غَفُورٌۭ شَكُورٌ',42,23,486,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ يَقُولُونَ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًۭا ۖ فَإِن يَشَإِ ٱللَّهُ يَخْتِمْ عَلَىٰ قَلْبِكَ ۗ وَيَمْحُ ٱللَّهُ ٱلْبَٰطِلَ وَيُحِقُّ ٱلْحَقَّ بِكَلِمَٰتِهِۦٓ ۚ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ',42,24,486,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى يَقْبَلُ ٱلتَّوْبَةَ عَنْ عِبَادِهِۦ وَيَعْفُوا۟ عَنِ ٱلسَّيِّـَٔاتِ وَيَعْلَمُ مَا تَفْعَلُونَ',42,25,486,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَسْتَجِيبُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضْلِهِۦ ۚ وَٱلْكَٰفِرُونَ لَهُمْ عَذَابٌۭ شَدِيدٌۭ',42,26,486,421,194,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَلَوْ بَسَطَ ٱللَّهُ ٱلرِّزْقَ لِعِبَادِهِۦ لَبَغَوْا۟ فِى ٱلْأَرْضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٍۢ مَّا يَشَآءُ ۚ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرٌۢ بَصِيرٌۭ',42,27,486,421,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَهُوَ ٱلَّذِى يُنَزِّلُ ٱلْغَيْثَ مِنۢ بَعْدِ مَا قَنَطُوا۟ وَيَنشُرُ رَحْمَتَهُۥ ۚ وَهُوَ ٱلْوَلِىُّ ٱلْحَمِيدُ',42,28,486,421,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنْ ءَايَٰتِهِۦ خَلْقُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٍۢ ۚ وَهُوَ عَلَىٰ جَمْعِهِمْ إِذَا يَشَآءُ قَدِيرٌۭ',42,29,486,421,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٍۢ فَبِمَا كَسَبَتْ أَيْدِيكُمْ وَيَعْفُوا۟ عَن كَثِيرٍۢ',42,30,486,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَآ أَنتُم بِمُعْجِزِينَ فِى ٱلْأَرْضِ ۖ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِىٍّۢ وَلَا نَصِيرٍۢ',42,31,486,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمِنْ ءَايَٰتِهِ ٱلْجَوَارِ فِى ٱلْبَحْرِ كَٱلْأَعْلَٰمِ',42,32,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِن يَشَأْ يُسْكِنِ ٱلرِّيحَ فَيَظْلَلْنَ رَوَاكِدَ عَلَىٰ ظَهْرِهِۦٓ ۚ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍۢ لِّكُلِّ صَبَّارٍۢ شَكُورٍ',42,33,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا۟ وَيَعْفُ عَن كَثِيرٍۢ',42,34,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَيَعْلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٍۢ',42,35,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَمَآ أُوتِيتُم مِّن شَىْءٍۢ فَمَتَٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَمَا عِندَ ٱللَّهِ خَيْرٌۭ وَأَبْقَىٰ لِلَّذِينَ ءَامَنُوا۟ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ',42,36,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ يَجْتَنِبُونَ كَبَٰٓئِرَ ٱلْإِثْمِ وَٱلْفَوَٰحِشَ وَإِذَا مَا غَضِبُوا۟ هُمْ يَغْفِرُونَ',42,37,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ ٱسْتَجَابُوا۟ لِرَبِّهِمْ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَمْرُهُمْ شُورَىٰ بَيْنَهُمْ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ',42,38,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلْبَغْىُ هُمْ يَنتَصِرُونَ',42,39,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَزَٰٓؤُا۟ سَيِّئَةٍۢ سَيِّئَةٌۭ مِّثْلُهَا ۖ فَمَنْ عَفَا وَأَصْلَحَ فَأَجْرُهُۥ عَلَى ٱللَّهِ ۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّٰلِمِينَ',42,40,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَمَنِ ٱنتَصَرَ بَعْدَ ظُلْمِهِۦ فَأُو۟لَٰٓئِكَ مَا عَلَيْهِم مِّن سَبِيلٍ',42,41,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظْلِمُونَ ٱلنَّاسَ وَيَبْغُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ ۚ أُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٌۭ',42,42,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنْ عَزْمِ ٱلْأُمُورِ',42,43,487,422,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِىٍّۢ مِّنۢ بَعْدِهِۦ ۗ وَتَرَى ٱلظَّٰلِمِينَ لَمَّا رَأَوُا۟ ٱلْعَذَابَ يَقُولُونَ هَلْ إِلَىٰ مَرَدٍّۢ مِّن سَبِيلٍۢ',42,44,487,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتَرَىٰهُمْ يُعْرَضُونَ عَلَيْهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرْفٍ خَفِىٍّۢ ۗ وَقَالَ ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ ٱلْخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَٰمَةِ ۗ أَلَآ إِنَّ ٱلظَّٰلِمِينَ فِى عَذَابٍۢ مُّقِيمٍۢ',42,45,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا كَانَ لَهُم مِّنْ أَوْلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِ ۗ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ',42,46,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱسْتَجِيبُوا۟ لِرَبِّكُم مِّن قَبْلِ أَن يَأْتِىَ يَوْمٌۭ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِ ۚ مَا لَكُم مِّن مَّلْجَإٍۢ يَوْمَئِذٍۢ وَمَا لَكُم مِّن نَّكِيرٍۢ',42,47,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِنْ أَعْرَضُوا۟ فَمَآ أَرْسَلْنَٰكَ عَلَيْهِمْ حَفِيظًا ۖ إِنْ عَلَيْكَ إِلَّا ٱلْبَلَٰغُ ۗ وَإِنَّآ إِذَآ أَذَقْنَا ٱلْإِنسَٰنَ مِنَّا رَحْمَةًۭ فَرِحَ بِهَا ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ ٱلْإِنسَٰنَ كَفُورٌۭ',42,48,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِّلَّهِ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ۚ يَخْلُقُ مَا يَشَآءُ ۚ يَهَبُ لِمَن يَشَآءُ إِنَٰثًۭا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ',42,49,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ يُزَوِّجُهُمْ ذُكْرَانًۭا وَإِنَٰثًۭا ۖ وَيَجْعَلُ مَن يَشَآءُ عَقِيمًا ۚ إِنَّهُۥ عَلِيمٌۭ قَدِيرٌۭ',42,50,488,423,195,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحْيًا أَوْ مِن وَرَآئِ حِجَابٍ أَوْ يُرْسِلَ رَسُولًۭا فَيُوحِىَ بِإِذْنِهِۦ مَا يَشَآءُ ۚ إِنَّهُۥ عَلِىٌّ حَكِيمٌۭ',42,51,488,423,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ رُوحًۭا مِّنْ أَمْرِنَا ۚ مَا كُنتَ تَدْرِى مَا ٱلْكِتَٰبُ وَلَا ٱلْإِيمَٰنُ وَلَٰكِن جَعَلْنَٰهُ نُورًۭا نَّهْدِى بِهِۦ مَن نَّشَآءُ مِنْ عِبَادِنَا ۚ وَإِنَّكَ لَتَهْدِىٓ إِلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ',42,52,489,423,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('صِرَٰطِ ٱللَّهِ ٱلَّذِى لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ۗ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلْأُمُورُ',42,53,489,423,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ حمٓ',43,1,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلْكِتَٰبِ ٱلْمُبِينِ',43,2,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّا جَعَلْنَٰهُ قُرْءَٰنًا عَرَبِيًّۭا لَّعَلَّكُمْ تَعْقِلُونَ',43,3,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّهُۥ فِىٓ أُمِّ ٱلْكِتَٰبِ لَدَيْنَا لَعَلِىٌّ حَكِيمٌ',43,4,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَنَضْرِبُ عَنكُمُ ٱلذِّكْرَ صَفْحًا أَن كُنتُمْ قَوْمًۭا مُّسْرِفِينَ',43,5,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَمْ أَرْسَلْنَا مِن نَّبِىٍّۢ فِى ٱلْأَوَّلِينَ',43,6,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا يَأْتِيهِم مِّن نَّبِىٍّ إِلَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ',43,7,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَأَهْلَكْنَآ أَشَدَّ مِنْهُم بَطْشًۭا وَمَضَىٰ مَثَلُ ٱلْأَوَّلِينَ',43,8,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلْعَزِيزُ ٱلْعَلِيمُ',43,9,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ مَهْدًۭا وَجَعَلَ لَكُمْ فِيهَا سُبُلًۭا لَّعَلَّكُمْ تَهْتَدُونَ',43,10,489,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِى نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءًۢ بِقَدَرٍۢ فَأَنشَرْنَا بِهِۦ بَلْدَةًۭ مَّيْتًۭا ۚ كَذَٰلِكَ تُخْرَجُونَ',43,11,490,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَٱلَّذِى خَلَقَ ٱلْأَزْوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلْفُلْكِ وَٱلْأَنْعَٰمِ مَا تَرْكَبُونَ',43,12,490,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لِتَسْتَوُۥا۟ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذْكُرُوا۟ نِعْمَةَ رَبِّكُمْ إِذَا ٱسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا۟ سُبْحَٰنَ ٱلَّذِى سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقْرِنِينَ',43,13,490,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ',43,14,490,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلُوا۟ لَهُۥ مِنْ عِبَادِهِۦ جُزْءًا ۚ إِنَّ ٱلْإِنسَٰنَ لَكَفُورٌۭ مُّبِينٌ',43,15,490,424,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمِ ٱتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍۢ وَأَصْفَىٰكُم بِٱلْبَنِينَ',43,16,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحْمَٰنِ مَثَلًۭا ظَلَّ وَجْهُهُۥ مُسْوَدًّۭا وَهُوَ كَظِيمٌ',43,17,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوَمَن يُنَشَّؤُا۟ فِى ٱلْحِلْيَةِ وَهُوَ فِى ٱلْخِصَامِ غَيْرُ مُبِينٍۢ',43,18,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلُوا۟ ٱلْمَلَٰٓئِكَةَ ٱلَّذِينَ هُمْ عِبَٰدُ ٱلرَّحْمَٰنِ إِنَٰثًا ۚ أَشَهِدُوا۟ خَلْقَهُمْ ۚ سَتُكْتَبُ شَهَٰدَتُهُمْ وَيُسْـَٔلُونَ',43,19,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ لَوْ شَآءَ ٱلرَّحْمَٰنُ مَا عَبَدْنَٰهُم ۗ مَّا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَخْرُصُونَ',43,20,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ ءَاتَيْنَٰهُمْ كِتَٰبًۭا مِّن قَبْلِهِۦ فَهُم بِهِۦ مُسْتَمْسِكُونَ',43,21,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ قَالُوٓا۟ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍۢ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّهْتَدُونَ',43,22,490,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَكَذَٰلِكَ مَآ أَرْسَلْنَا مِن قَبْلِكَ فِى قَرْيَةٍۢ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَآ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍۢ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّقْتَدُونَ',43,23,491,425,196,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ قَٰلَ أَوَلَوْ جِئْتُكُم بِأَهْدَىٰ مِمَّا وَجَدتُّمْ عَلَيْهِ ءَابَآءَكُمْ ۖ قَالُوٓا۟ إِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَٰفِرُونَ',43,24,491,425,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱنتَقَمْنَا مِنْهُمْ ۖ فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ ٱلْمُكَذِّبِينَ',43,25,491,425,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِذْ قَالَ إِبْرَٰهِيمُ لِأَبِيهِ وَقَوْمِهِۦٓ إِنَّنِى بَرَآءٌۭ مِّمَّا تَعْبُدُونَ',43,26,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِلَّا ٱلَّذِى فَطَرَنِى فَإِنَّهُۥ سَيَهْدِينِ',43,27,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَجَعَلَهَا كَلِمَةًۢ بَاقِيَةًۭ فِى عَقِبِهِۦ لَعَلَّهُمْ يَرْجِعُونَ',43,28,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('بَلْ مَتَّعْتُ هَٰٓؤُلَآءِ وَءَابَآءَهُمْ حَتَّىٰ جَآءَهُمُ ٱلْحَقُّ وَرَسُولٌۭ مُّبِينٌۭ',43,29,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَمَّا جَآءَهُمُ ٱلْحَقُّ قَالُوا۟ هَٰذَا سِحْرٌۭ وَإِنَّا بِهِۦ كَٰفِرُونَ',43,30,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ لَوْلَا نُزِّلَ هَٰذَا ٱلْقُرْءَانُ عَلَىٰ رَجُلٍۢ مِّنَ ٱلْقَرْيَتَيْنِ عَظِيمٍ',43,31,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ ۚ نَحْنُ قَسَمْنَا بَيْنَهُم مَّعِيشَتَهُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۚ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍۢ دَرَجَٰتٍۢ لِّيَتَّخِذَ بَعْضُهُم بَعْضًۭا سُخْرِيًّۭا ۗ وَرَحْمَتُ رَبِّكَ خَيْرٌۭ مِّمَّا يَجْمَعُونَ',43,32,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةًۭ وَٰحِدَةًۭ لَّجَعَلْنَا لِمَن يَكْفُرُ بِٱلرَّحْمَٰنِ لِبُيُوتِهِمْ سُقُفًۭا مِّن فِضَّةٍۢ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ',43,33,491,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلِبُيُوتِهِمْ أَبْوَٰبًۭا وَسُرُرًا عَلَيْهَا يَتَّكِـُٔونَ',43,34,492,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَزُخْرُفًۭا ۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا ۚ وَٱلْءَاخِرَةُ عِندَ رَبِّكَ لِلْمُتَّقِينَ',43,35,492,426,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَن يَعْشُ عَن ذِكْرِ ٱلرَّحْمَٰنِ نُقَيِّضْ لَهُۥ شَيْطَٰنًۭا فَهُوَ لَهُۥ قَرِينٌۭ',43,36,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ ٱلسَّبِيلِ وَيَحْسَبُونَ أَنَّهُم مُّهْتَدُونَ',43,37,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَٰلَيْتَ بَيْنِى وَبَيْنَكَ بُعْدَ ٱلْمَشْرِقَيْنِ فَبِئْسَ ٱلْقَرِينُ',43,38,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَن يَنفَعَكُمُ ٱلْيَوْمَ إِذ ظَّلَمْتُمْ أَنَّكُمْ فِى ٱلْعَذَابِ مُشْتَرِكُونَ',43,39,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَفَأَنتَ تُسْمِعُ ٱلصُّمَّ أَوْ تَهْدِى ٱلْعُمْىَ وَمَن كَانَ فِى ضَلَٰلٍۢ مُّبِينٍۢ',43,40,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُم مُّنتَقِمُونَ',43,41,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَوْ نُرِيَنَّكَ ٱلَّذِى وَعَدْنَٰهُمْ فَإِنَّا عَلَيْهِم مُّقْتَدِرُونَ',43,42,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱسْتَمْسِكْ بِٱلَّذِىٓ أُوحِىَ إِلَيْكَ ۖ إِنَّكَ عَلَىٰ صِرَٰطٍۢ مُّسْتَقِيمٍۢ',43,43,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّهُۥ لَذِكْرٌۭ لَّكَ وَلِقَوْمِكَ ۖ وَسَوْفَ تُسْـَٔلُونَ',43,44,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَسْـَٔلْ مَنْ أَرْسَلْنَا مِن قَبْلِكَ مِن رُّسُلِنَآ أَجَعَلْنَا مِن دُونِ ٱلرَّحْمَٰنِ ءَالِهَةًۭ يُعْبَدُونَ',43,45,492,427,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦ فَقَالَ إِنِّى رَسُولُ رَبِّ ٱلْعَٰلَمِينَ',43,46,492,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا جَآءَهُم بِـَٔايَٰتِنَآ إِذَا هُم مِّنْهَا يَضْحَكُونَ',43,47,492,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا نُرِيهِم مِّنْ ءَايَةٍ إِلَّا هِىَ أَكْبَرُ مِنْ أُخْتِهَا ۖ وَأَخَذْنَٰهُم بِٱلْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ',43,48,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوا۟ يَٰٓأَيُّهَ ٱلسَّاحِرُ ٱدْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهْتَدُونَ',43,49,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّا كَشَفْنَا عَنْهُمُ ٱلْعَذَابَ إِذَا هُمْ يَنكُثُونَ',43,50,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَادَىٰ فِرْعَوْنُ فِى قَوْمِهِۦ قَالَ يَٰقَوْمِ أَلَيْسَ لِى مُلْكُ مِصْرَ وَهَٰذِهِ ٱلْأَنْهَٰرُ تَجْرِى مِن تَحْتِىٓ ۖ أَفَلَا تُبْصِرُونَ',43,51,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ أَنَا۠ خَيْرٌۭ مِّنْ هَٰذَا ٱلَّذِى هُوَ مَهِينٌۭ وَلَا يَكَادُ يُبِينُ',43,52,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَوْلَآ أُلْقِىَ عَلَيْهِ أَسْوِرَةٌۭ مِّن ذَهَبٍ أَوْ جَآءَ مَعَهُ ٱلْمَلَٰٓئِكَةُ مُقْتَرِنِينَ',43,53,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱسْتَخَفَّ قَوْمَهُۥ فَأَطَاعُوهُ ۚ إِنَّهُمْ كَانُوا۟ قَوْمًۭا فَٰسِقِينَ',43,54,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَلَمَّآ ءَاسَفُونَا ٱنتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَٰهُمْ أَجْمَعِينَ',43,55,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَجَعَلْنَٰهُمْ سَلَفًۭا وَمَثَلًۭا لِّلْءَاخِرِينَ',43,56,493,428,197,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('۞ وَلَمَّا ضُرِبَ ٱبْنُ مَرْيَمَ مَثَلًا إِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ',43,57,493,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَقَالُوٓا۟ ءَأَٰلِهَتُنَا خَيْرٌ أَمْ هُوَ ۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًۢا ۚ بَلْ هُمْ قَوْمٌ خَصِمُونَ',43,58,493,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنْ هُوَ إِلَّا عَبْدٌ أَنْعَمْنَا عَلَيْهِ وَجَعَلْنَٰهُ مَثَلًۭا لِّبَنِىٓ إِسْرَٰٓءِيلَ',43,59,493,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَوْ نَشَآءُ لَجَعَلْنَا مِنكُم مَّلَٰٓئِكَةًۭ فِى ٱلْأَرْضِ يَخْلُفُونَ',43,60,493,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَإِنَّهُۥ لَعِلْمٌۭ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَٱتَّبِعُونِ ۚ هَٰذَا صِرَٰطٌۭ مُّسْتَقِيمٌۭ',43,61,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَا يَصُدَّنَّكُمُ ٱلشَّيْطَٰنُ ۖ إِنَّهُۥ لَكُمْ عَدُوٌّۭ مُّبِينٌۭ',43,62,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَلَمَّا جَآءَ عِيسَىٰ بِٱلْبَيِّنَٰتِ قَالَ قَدْ جِئْتُكُم بِٱلْحِكْمَةِ وَلِأُبَيِّنَ لَكُم بَعْضَ ٱلَّذِى تَخْتَلِفُونَ فِيهِ ۖ فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ',43,63,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱللَّهَ هُوَ رَبِّى وَرَبُّكُمْ فَٱعْبُدُوهُ ۚ هَٰذَا صِرَٰطٌۭ مُّسْتَقِيمٌۭ',43,64,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('فَٱخْتَلَفَ ٱلْأَحْزَابُ مِنۢ بَيْنِهِمْ ۖ فَوَيْلٌۭ لِّلَّذِينَ ظَلَمُوا۟ مِنْ عَذَابِ يَوْمٍ أَلِيمٍ',43,65,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('هَلْ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأْتِيَهُم بَغْتَةًۭ وَهُمْ لَا يَشْعُرُونَ',43,66,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلْأَخِلَّآءُ يَوْمَئِذٍۭ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلَّا ٱلْمُتَّقِينَ',43,67,494,429,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يَٰعِبَادِ لَا خَوْفٌ عَلَيْكُمُ ٱلْيَوْمَ وَلَآ أَنتُمْ تَحْزَنُونَ',43,68,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱلَّذِينَ ءَامَنُوا۟ بِـَٔايَٰتِنَا وَكَانُوا۟ مُسْلِمِينَ',43,69,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('ٱدْخُلُوا۟ ٱلْجَنَّةَ أَنتُمْ وَأَزْوَٰجُكُمْ تُحْبَرُونَ',43,70,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('يُطَافُ عَلَيْهِم بِصِحَافٍۢ مِّن ذَهَبٍۢ وَأَكْوَابٍۢ ۖ وَفِيهَا مَا تَشْتَهِيهِ ٱلْأَنفُسُ وَتَلَذُّ ٱلْأَعْيُنُ ۖ وَأَنتُمْ فِيهَا خَٰلِدُونَ',43,71,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَتِلْكَ ٱلْجَنَّةُ ٱلَّتِىٓ أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَ',43,72,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَكُمْ فِيهَا فَٰكِهَةٌۭ كَثِيرَةٌۭ مِّنْهَا تَأْكُلُونَ',43,73,494,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلْمُجْرِمِينَ فِى عَذَابِ جَهَنَّمَ خَٰلِدُونَ',43,74,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيهِ مُبْلِسُونَ',43,75,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَمَا ظَلَمْنَٰهُمْ وَلَٰكِن كَانُوا۟ هُمُ ٱلظَّٰلِمِينَ',43,76,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('وَنَادَوْا۟ يَٰمَٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَ ۖ قَالَ إِنَّكُم مَّٰكِثُونَ',43,77,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('لَقَدْ جِئْنَٰكُم بِٱلْحَقِّ وَلَٰكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كَٰرِهُونَ',43,78,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ أَبْرَمُوٓا۟ أَمْرًۭا فَإِنَّا مُبْرِمُونَ',43,79,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمْ يَحْسَبُونَ أَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوَىٰهُم ۚ بَلَىٰ وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ',43,80,495,430,198,false);
INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('قُلْ إِن كَانَ لِلرَّحْمَٰنِ وَلَدٌۭ فَأَنَا۠ أَوَّلُ ٱلْعَٰبِدِينَ',43,81,495,430,198,false);
