INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('إِنَّ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ لَهُم مَّغْفِرَةٌۭ وَأَجْرٌۭ كَبِيرٌۭ',67,12,562,496,225,false);
