INSERT INTO ayah(ayah, surah_id, number, quran_page_id, ruku, hizb_quarter, sajda) VALUES ('أَمَّنْ هَٰذَا ٱلَّذِى هُوَ جُندٌۭ لَّكُمْ يَنصُرُكُم مِّن دُونِ ٱلرَّحْمَٰنِ ۚ إِنِ ٱلْكَٰفِرُونَ إِلَّا فِى غُرُورٍ',67,20,563,497,225,false);
